module com.papel.imdb_clone {
    requires javafx.controls;
    requires javafx.fxml;
    requires javafx.graphics;
    requires javafx.media;
    requires javafx.web;
    requires javafx.base;
    requires org.slf4j;
    requires static org.slf4j.simple;
    requires org.controlsfx.controls;
    requires java.sql;
    requires java.base; // For SecureRandom and other base classes
    requires jbcrypt;

    // Export packages
    exports com.papel.imdb_clone.gui;
    exports com.papel.imdb_clone.model;
    exports com.papel.imdb_clone.data;
    exports com.papel.imdb_clone.enums;
    exports com.papel.imdb_clone.exceptions;
    exports com.papel.imdb_clone.controllers;
    exports com.papel.imdb_clone.service;

    opens com.papel.imdb_clone.gui to javafx.fxml;
    opens com.papel.imdb_clone.controllers to javafx.fxml;
    opens com.papel.imdb_clone.model to javafx.base, javafx.fxml;
    opens com.papel.imdb_clone.service to javafx.base;
}

