package com.papel.imdb_clone.data;

import com.papel.imdb_clone.enums.Genre;
import com.papel.imdb_clone.exceptions.DuplicateContentException;
import com.papel.imdb_clone.exceptions.DuplicateUsernameException;
import com.papel.imdb_clone.exceptions.FileParsingException;
import com.papel.imdb_clone.model.*;

import java.io.BufferedReader;
import java.io.FileReader;
import java.io.IOException;
import java.time.LocalDate;
import java.time.format.DateTimeFormatter;
import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;

public class DataManager {
    private final List<User> users;
    private final List<Actor> actors;
    private final List<Director> directors;
    private final List<Movie> movies;
    private final List<Series> series;

    private int nextUserId = 1;
    private int nextActorId = 1;
    private int nextDirectorId = 1;
    private int nextMovieId = 1;
    private int nextSeriesId = 1;
    private int nextSeasonId = 1;
    private int nextEpisodeId = 1;

    // We don't need separate allMovies and allSeries fields since we already have movies and series lists

    public DataManager() {
        this.users = new ArrayList<>();
        this.actors = new ArrayList<>();

        this.directors = new ArrayList<>();
        this.movies = new ArrayList<>();
        this.series = new ArrayList<>();
    }


    public void loadAllData() {
        loadUsers("users.txt");
        loadActors("actors.txt");
        loadDirectors("directors.txt");
        loadMovies("movies.txt");
        loadSeries("series.txt");
    }

    public void loadUsers(String filename) {
        try (BufferedReader reader = new BufferedReader(new FileReader("src/main/resources/data/" + filename))) {
            String line;
            while ((line = reader.readLine()) != null) {
                String[] parts = line.split(",");
                if (parts.length >= 5) {
                    User user = new User(parts[0], parts[1], parts[2], parts[3].charAt(0), parts[4]);
                    user.setId(nextUserId++);
                    users.add(user);
                }
            }
        } catch (IOException e) {
            throw new FileParsingException("Error loading users: " + e.getMessage());
        }
    }

    public void loadActors(String filename) {
        try (BufferedReader reader = new BufferedReader(new FileReader("src/main/resources/data/" + filename))) {
            String line;
            while ((line = reader.readLine()) != null) {
                String[] parts = line.split(",");
                if (parts.length >= 5) {
                    LocalDate birthDate = LocalDate.parse(parts[2], DateTimeFormatter.ISO_LOCAL_DATE);
                    Actor actor = new Actor(parts[0], parts[1], birthDate, parts[3].charAt(0), parts[4]);
                    actor.setId(nextActorId++);
                    actors.add(actor);
                }
            }
        } catch (IOException e) {
            throw new FileParsingException("Error loading actors: " + e.getMessage());
        }
    }

    public void loadDirectors(String filename) {
        try (BufferedReader reader = new BufferedReader(new FileReader("src/main/resources/data/" + filename))) {
            String line;
            while ((line = reader.readLine()) != null) {
                String[] parts = line.split(",");
                if (parts.length >= 4) {
                    LocalDate birthDate = LocalDate.parse(parts[2], DateTimeFormatter.ISO_LOCAL_DATE);
                    Director director = new Director(parts[0], parts[1], birthDate, parts[3].charAt(0));
                    director.setId(nextDirectorId++);
                    directors.add(director);
                }
            }
        } catch (IOException e) {
            throw new FileParsingException("Error loading directors: " + e.getMessage());
        }
    }

    public void loadMovies(String filename) {
        try (BufferedReader reader = new BufferedReader(new FileReader("src/main/resources/data/" + filename))) {
            String line;
            while ((line = reader.readLine()) != null) {
                String[] parts = line.split(",");
                if (parts.length >= 6) {
                    String title = parts[0];
                    int year = Integer.parseInt(parts[1]);
                    Genre genre = Genre.valueOf(parts[2].toUpperCase().replace(" ", "_"));
                    int duration = Integer.parseInt(parts[3]);
                    Director director = findDirectorByName(parts[4]);
                    double imdbRating = Double.parseDouble(parts[5]);

                    List<Actor> movieActors = new ArrayList<>();
                    if (parts.length > 6) {
                        String[] actorNames = parts[6].split(";");
                        for (String actorName : actorNames) {
                            Actor actor = findActorByName(actorName.trim());
                            if (actor != null) {
                                movieActors.add(actor);
                            }
                        }
                    }

                    // Handle case where director might be null
                    Movie movie;
                    if (director != null) {
                        movie = new Movie(title, year, director, null, duration, movieActors);
                    } else {
                        // Create a movie with genre and director name from the file when director object is not found
                        movie = new Movie(title, year, genre.name(), parts[4], new HashMap<>());
                        movie.setActors(movieActors);
                    }
                    movie.setId(nextMovieId++);
                    movie.setImdbRating(imdbRating);
                    movies.add(movie);
                }
            }
        } catch (IOException e) {
            throw new FileParsingException("Error loading movies: " + e.getMessage());
        }
    }

    public void loadSeries(String filename) {
        try (BufferedReader reader = new BufferedReader(new FileReader("src/main/resources/data/" + filename))) {
            String line;
            while ((line = reader.readLine()) != null) {
                String[] parts = line.split(",");
                if (parts.length >= 2) {
                    String title = parts[0];
                    Genre genre = Genre.valueOf(parts[1].toUpperCase().replace(" ", "_"));

                    Series series = new Series(title);
                    series.setGenre(genre);  // Set the genre from the file
                    series.setId(nextSeriesId++);
                    this.series.add(series);
                }
            }
        } catch (IOException e) {
            throw new FileParsingException("Error loading series: " + e.getMessage());
        }
    }

    public void addUser(User user) {
        if (findUserByUsername(user.getUsername()) != null) {
            throw new DuplicateUsernameException("Username already exists: " + user.getUsername());
        }
        user.setId(nextUserId++);
        users.add(user);
    }

    public void addActor(Actor actor) {
        actor.setId(nextActorId++);
        actors.add(actor);
    }

    public void addDirector(Director director) {
        director.setId(nextDirectorId++);
        directors.add(director);
    }

    public void addMovie(Movie movie) {
        if (findMovieByTitle(movie.getTitle()) != null) {
            throw new DuplicateContentException("Movie already exists: " + movie.getTitle());
        }
        movie.setId(nextMovieId++);
        movies.add(movie);
    }

    public void addSeries(Series series) {
        if (findSeriesByTitle(series.getTitle()) != null) {
            throw new DuplicateContentException("Series already exists: " + series.getTitle());
        }
        series.setId(nextSeriesId++);
        this.series.add(series);
    }

    public User findUserByUsername(String username) {
        return users.stream()
                .filter(user -> user.getUsername().equals(username))
                .findFirst()
                .orElse(null);
    }

    public Actor findActorByName(String name) {
        return actors.stream()
                .filter(actor -> actor.getFullName().equals(name))
                .findFirst()
                .orElse(null);
    }

    public Director findDirectorByName(String name) {
        return directors.stream()
                .filter(director -> director.getFullName().equals(name))
                .findFirst()
                .orElse(null);
    }

    public Movie findMovieByTitle(String title) {
        return movies.stream()
                .filter(movie -> movie.getTitle().equals(title))
                .findFirst()
                .orElse(null);
    }

    public Series findSeriesByTitle(String title) {
        return series.stream()
                .filter(series -> series.getTitle().equals(title))
                .findFirst()
                .orElse(null);
    }

    public List<Content> searchContent(SearchCriteria criteria) {
        List<Content> result = new ArrayList<>();

        // Search movies
        result.addAll(movies.stream()
                .filter(movie -> matchesCriteria(movie, criteria))
                .toList());

        // Search series
        result.addAll(series.stream()
                .filter(series -> matchesCriteria(series, criteria))
                .toList());

        return result;
    }

    private boolean matchesCriteria(Content content, SearchCriteria criteria) {
        if (criteria.getTitle() != null && !content.getTitle().toLowerCase().contains(criteria.getTitle().toLowerCase())) {
            return false;
        }

        if (criteria.getMinUserRating() > 0 && content.calculateAverageUserRating() < criteria.getMinUserRating()) {
            return false;
        }

        if (content instanceof Movie movie) {

            if (criteria.getMinImdbRating() > 0 && movie.getImdbRating() < criteria.getMinImdbRating()) {
                return false;
            }

            if (criteria.getActorName() != null) {
                boolean hasActor = movie.getActors().stream()
                        .anyMatch(actor -> actor.getFullName().toLowerCase().contains(criteria.getActorName().toLowerCase()));
                if (!hasActor) return false;
            }

            if (criteria.getDirectorName() != null) {
                return movie.getDirector() != null &&
                        movie.getDirector().getFullName().toLowerCase().contains(criteria.getDirectorName().toLowerCase());
            }
        }

        return true;
    }

    // Getters
    public List<User> getUsers() {
        return new ArrayList<>(users);
    }

    public List<Actor> getActors() {
        return new ArrayList<>(actors);
    }

    public List<Director> getDirectors() {
        return new ArrayList<>(directors);
    }

    public List<Movie> getMovies() {
        return new ArrayList<>(movies);
    }

    public List<Series> getSeries() {
        return new ArrayList<>(series);
    }

    public int getNextSeasonId() {
        return nextSeasonId;
    }

    public void setNextSeasonId(int nextSeasonId) {
        this.nextSeasonId = nextSeasonId;
    }

    public int getNextEpisodeId() {
        return nextEpisodeId;
    }

    public void setNextEpisodeId(int nextEpisodeId) {
        this.nextEpisodeId = nextEpisodeId;
    }

    public List<Movie> searchMovies(SearchCriteria criteria) {
        return movies.stream()
                .filter(movie -> matchesCriteria(movie, criteria))
                .toList();
    }

    public List<Movie> getAllMovies() {
        return new ArrayList<>(movies);
    }

    public void setAllMovies(List<Movie> newMovies) {
        if (newMovies != null) {
            this.movies.clear();
            this.movies.addAll(newMovies);
        }
    }

    public void rateMovie(Movie selected, double rating) {
        if (rating < 0.0 || rating > 10.0) {
            throw new IllegalArgumentException("Rating must be between 0.0 and 10.0");
        }

        // Find the movie in our collection
        Movie movie = findMovieByTitle(selected.getTitle());
        if (movie != null) {
            // For simplicity, we'll use a fixed user ID (1) for now
            // In a real application, this would be the current user's ID
            int userId = 1;
            movie.addUserRating(userId, (int)Math.round(rating));
        }
    }

    public List<Series> getAllSeries() {
        return new ArrayList<>(series);
    }

    public void setAllSeries(List<Series> newSeries) {
        if (newSeries != null) {
            this.series.clear();
            this.series.addAll(newSeries);
        }
    }
}

