+        assertEquals(LocalDate.of(1970, 7, 30), director.getBirthDate());
+        assertEquals('M', director.getGender());
+        assertEquals("Christopher Nolan", director.getFullName());
+        assertTrue(director.getBestWorks().isEmpty());
+    }
+
+    @Test
+    void testAddBestWork() {
+        director.addBestWork("Inception");
+        director.addBestWork("The Dark Knight");
+        
+        assertEquals(2, director.getBestWorks().size());
+        assertTrue(director.getBestWorks().contains("Inception"));
+        assertTrue(director.getBestWorks().contains("The Dark Knight"));
+    }
+
+    @Test
+    void testAddNullBestWork() {
+        director.addBestWork(null);
+        assertTrue(director.getBestWorks().isEmpty());
+    }
+
+    @Test
+    void testAddEmptyBestWork() {
+        director.addBestWork("");
+        director.addBestWork("   ");
+        assertTrue(director.getBestWorks().isEmpty());
+    }
+
+    @Test
+    void testAddBestWorkTrimsWhitespace() {
+        director.addBestWork("  Interstellar  ");
+        assertEquals(1, director.getBestWorks().size());
+        assertEquals("Interstellar", director.getBestWorks().get(0));
+    }
+
+    @Test
+    void testSetId() {
+        director.setId(123);
+        assertEquals(123, director.getId());
+    }
+
+    @Test
+    void testEqualsAndHashCode() {
+        Director director1 = new Director("Christopher", "Nolan", LocalDate.of(1970, 7, 30), 'M');
+        Director director2 = new Director("Christopher", "Nolan", LocalDate.of(1970, 7, 30), 'M');
+        
+        director1.setId(1);
+        director2.setId(1);
+        
+        director1.addBestWork("Inception");
+        director2.addBestWork("Inception");
+        
+        assertEquals(director1, director2);
+        assertEquals(director1.hashCode(), director2.hashCode());
+    }
+
+    @Test
+    void testNotEquals() {
+        Director director1 = new Director("Christopher", "Nolan", LocalDate.of(1970, 7, 30), 'M');
+        Director director2 = new Director("Steven", "Spielberg", LocalDate.of(1946, 12, 18), 'M');
+        
+        assertNotEquals(director1, director2);
+    }
+
+    @Test
+    void testToString() {
+        director.setId(1);
+        director.addBestWork("Inception");
+        
+        String result = director.toString();
+        assertTrue(result.contains("Director{"));
+        assertTrue(result.contains("id=1"));
+        assertTrue(result.contains("firstName='Christopher'"));
+        assertTrue(result.contains("lastName='Nolan'"));
+        assertTrue(result.contains("bestWorks=[Inception]"));
+    }
+
+    @Test
+    void testGetBestWorksReturnsDefensiveCopy() {
+        director.addBestWork("Inception");
+        
+        // Get the list and try to modify it
+        var bestWorks = director.getBestWorks();
+        bestWorks.add("Modified");
+        
+        // Original should be unchanged
+        assertEquals(1, director.getBestWorks().size());
+        assertEquals("Inception", director.getBestWorks().get(0));
+    }
+}

