package com.papel.imdb_clone.data;

import com.papel.imdb_clone.model.*;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.BeforeEach;
import static org.junit.jupiter.api.Assertions.*;

import java.time.LocalDate;
import java.util.*;

class DataManagerTest {
    private DataManager dataManager;
    private Movie testMovie;
    private User testUser;
    private Director testDirector;
    private Actor testActor;

    @BeforeEach
    void setUp() {
        dataManager = new DataManager();
        
        // Create test data
        testDirector = new Director("Christopher", "Nolan", LocalDate.of(1970, 7, 30), 'M');
        testActor = new Actor("Leonardo", "DiCaprio", LocalDate.of(1974, 11, 11), 'M', "White");
        testMovie = new Movie("Inception", 2010, testDirector, "8.8", 148, Arrays.asList(testActor));
        testUser = new User("John", "Doe", "johndoe", 'M', "john.doe@example.com");
    }

    @Test
    void testAddAndGetMovie() {
        dataManager.addMovie(testMovie);
        
        List<Movie> movies = dataManager.getAllMovies();
        assertEquals(1, movies.size());
        assertEquals("Inception", movies.get(0).getTitle());
    }

    @Test
    void testAddAndGetUser() {
        dataManager.addUser(testUser);
        
        List<User> users = dataManager.getAllUsers();
        assertEquals(1, users.size());
        assertEquals("johndoe", users.get(0).getUsername());
    }

    @Test
    void testAddAndGetDirector() {
        dataManager.addDirector(testDirector);
        
        List<Director> directors = dataManager.getAllDirectors();
        assertEquals(1, directors.size());
        assertEquals("Christopher Nolan", directors.get(0).getFullName());
    }

    @Test
    void testAddAndGetActor() {
        dataManager.addActor(testActor);
        
        List<Actor> actors = dataManager.getAllActors();
        assertEquals(1, actors.size());
        assertEquals("Leonardo DiCaprio", actors.get(0).getFullName());
    }

    @Test
    void testSearchMoviesByTitle() {
        dataManager.addMovie(testMovie);
        
        SearchCriteria criteria = new SearchCriteria();
        criteria.setTitle("Inception");
        
        List<Movie> results = dataManager.searchMovies(criteria);
        assertEquals(1, results.size());
        assertEquals("Inception", results.get(0).getTitle());
    }

    @Test
    void testSearchMoviesByDirector() {
        dataManager.addMovie(testMovie);
        
        SearchCriteria criteria = new SearchCriteria();
        criteria.setDirectorName("Nolan");
        
        List<Movie> results = dataManager.searchMovies(criteria);
        assertEquals(1, results.size());
        assertEquals("Inception", results.get(0).getTitle());
    }

    @Test
    void testSearchMoviesWithNoResults() {
        dataManager.addMovie(testMovie);
        
        SearchCriteria criteria = new SearchCriteria();
        criteria.setTitle("NonExistentMovie");
        
        List<Movie> results = dataManager.searchMovies(criteria);
        assertTrue(results.isEmpty());
    }

    @Test
    void testSearchMoviesWithEmptyCriteria() {
        dataManager.addMovie(testMovie);
        
        SearchCriteria criteria = new SearchCriteria();
        
        List<Movie> results = dataManager.searchMovies(criteria);
        assertEquals(1, results.size()); // Should return all movies when no criteria specified
    }

    @Test
    void testGetMovieById() {
        testMovie.setId(1);
        dataManager.addMovie(testMovie);
        
        Movie found = dataManager.getMovieById(1);
        assertNotNull(found);
        assertEquals("Inception", found.getTitle());
    }

    @Test
    void testGetMovieByIdNotFound() {
        Movie found = dataManager.getMovieById(999);
        assertNull(found);
    }

    @Test
    void testGetUserById() {
        testUser.setId(1);
        dataManager.addUser(testUser);
        
        User found = dataManager.getUserById(1);
        assertNotNull(found);
        assertEquals("johndoe", found.getUsername());
    }

    @Test
    void testGetUserByIdNotFound() {
        User found = dataManager.getUserById(999);
        assertNull(found);
    }

    @Test
    void testGetDirectorById() {
        testDirector.setId(1);
        dataManager.addDirector(testDirector);
        
        Director found = dataManager.getDirectorById(1);
        assertNotNull(found);
        assertEquals("Christopher Nolan", found.getFullName());
    }

    @Test
    void testGetDirectorByIdNotFound() {
        Director found = dataManager.getDirectorById(999);
        assertNull(found);
    }

    @Test
    void testGetActorById() {
        testActor.setId(1);
        dataManager.addActor(testActor);
        
        Actor found = dataManager.getActorById(1);
        assertNotNull(found);
        assertEquals("Leonardo DiCaprio", found.getFullName());
    }

