+        
+        // IMDB Rating column
+        TableColumn<Movie, String> imdbColumn = new TableColumn<>("IMDB Rating");
+        imdbColumn.setCellValueFactory(data -> 
+            new SimpleStringProperty(String.format("%.1f", data.getValue().getImdbRating()))
+        );
+        imdbColumn.setPrefWidth(100);
+        
+        // User Rating column
+        TableColumn<Movie, String> userRatingColumn = new TableColumn<>("User Rating");
+        userRatingColumn.setCellValueFactory(data -> 
+            new SimpleStringProperty(
+                data.getValue().getUserRating() != null 
+                    ? String.format("%.1f", data.getValue().getUserRating())
