+        
+        movieTable.getColumns().clear();
+        movieTable.getColumns().addAll(
+            titleColumn, yearColumn, genreColumn, directorColumn, imdbColumn, userRatingColumn
+        );
+        
+        // Enable row selection
+        movieTable.getSelectionModel().setSelectionMode(SelectionMode.SINGLE);
+        
+        // Add double-click handler for movie details
+        movieTable.setRowFactory(tv -> {
+            TableRow<Movie> row = new TableRow<>();
+            row.setOnMouseClicked(event -> {
+                if (event.getClickCount() == 2 && !row.isEmpty()) {
+                    showMovieDetails(row.getItem());
+                }
+            });
+            return row;
+        });
+    }
+    
+    private void setupSeriesTree() {
+        TreeItem<String> root = new TreeItem<>("All Series");
+        root.setExpanded(true);
+        seriesTree.setRoot(root);
+        seriesTree.setShowRoot(false);
+    }
+    
+    private void loadData() {
+        try {
+            dataManager.loadAllData();
+            refreshMovieTable();
+            refreshSeriesTree();
+        } catch (Exception e) {
+            showErrorAlert("Data Loading Error", "Failed to load application data: " + e.getMessage());
+        }
+    }
+    
+    private void refreshMovieTable() {
+        if (movieTable != null) {
+            List<Movie> movies = dataManager.getAllMovies();
+            ObservableList<Movie> items = FXCollections.observableArrayList(movies);
+            movieTable.setItems(items);
+        }
+    }
+    
+    private void refreshSeriesTree() {
+        if (seriesTree != null && seriesTree.getRoot() != null) {
+            TreeItem<String> root = seriesTree.getRoot();
+            root.getChildren().clear();
+            
+            List<Series> allSeries = dataManager.getAllSeries();
+            for (Series series : allSeries) {
+                TreeItem<String> seriesNode = new TreeItem<>(series.getTitle());
+                root.getChildren().add(seriesNode);
+            }
+        }
+    }
+    
+    // FXML Event Handlers
+    @FXML
+    private void showAddMovieDialog() {
+        Dialog<Movie> dialog = createAddMovieDialog();
+        Optional<Movie> result = dialog.showAndWait();
+        
+        result.ifPresent(movie -> {
+            try {
+                dataManager.addMovie(movie);
+                refreshMovieTable();
+                showSuccessAlert("Success", "Movie added successfully!");
+            } catch (Exception e) {
+                showErrorAlert("Error", "Failed to add movie: " + e.getMessage());
+            }
+        });
+    }
+    
+    @FXML
+    private void showRatingDialog() {
+        Movie selectedMovie = movieTable.getSelectionModel().getSelectedItem();
+        if (selectedMovie == null) {
+            showWarningAlert("No Selection", "Please select a movie to rate.");
+            return;
+        }
+        
+        Dialog<Double> dialog = createRatingDialog(selectedMovie);
+        Optional<Double> result = dialog.showAndWait();
+        
+        result.ifPresent(rating -> {
+            try {
+                dataManager.rateMovie(selectedMovie, rating);
+                refreshMovieTable();
+                showSuccessAlert("Success", "Movie rated successfully!");
+            } catch (Exception e) {
+                showErrorAlert("Error", "Failed to rate movie: " + e.getMessage());
+            }
+        });
+    }
+    
+    @FXML
+    private void showAddSeriesDialog() {
+        TextInputDialog dialog = new TextInputDialog();
+        dialog.setTitle("Add Series");
+        dialog.setHeaderText("Add New Series");
+        dialog.setContentText("Enter series title:");
+        
+        Optional<String> result = dialog.showAndWait();
+        result.ifPresent(title -> {
+            if (!title.trim().isEmpty()) {
+                try {
+                    Series newSeries = new Series(title.trim());
+                    dataManager.addSeries(newSeries);
+                    refreshSeriesTree();
+                    showSuccessAlert("Success", "Series added successfully!");
+                } catch (Exception e) {
+                    showErrorAlert("Error", "Failed to add series: " + e.getMessage());
+                }
+            }
+        });
+    }
+    
+    @FXML
+    private void handleSearch() {
+        try {
+            SearchCriteria criteria = buildSearchCriteria();
+            List<Movie> matches = dataManager.searchMovies(criteria);
+            
+            List<String> displayResults = matches.stream()
+                .map(this::formatMovieForDisplay)
+                .collect(Collectors.toList());
+            
+            resultsView.setItems(FXCollections.observableArrayList(displayResults));
+            
+            // Show search results count
+            showInfoAlert("Search Results", 
+                String.format("Found %d movie(s) matching your criteria.", matches.size()));
+                
+        } catch (Exception e) {
+            showErrorAlert("Search Error", "Failed to perform search: " + e.getMessage());
+        }
+    }
+    
+    @FXML
+    private void clearSearch() {
+        titleField.clear();
+        actorField.clear();
+        directorField.clear();
+        minImdbField.clear();
+        minUserField.clear();
+        resultsView.getItems().clear();
+    }
+    
+    @FXML
+    private void showMovieDetails() {
+        Movie selectedMovie = movieTable.getSelectionModel().getSelectedItem();
+        if (selectedMovie != null) {
+            showMovieDetails(selectedMovie);
+        } else {
+            showWarningAlert("No Selection", "Please select a movie to view details.");
+        }
+    }
+    
+    @FXML
+    private void deleteMovie() {
+        Movie selectedMovie = movieTable.getSelectionModel().getSelectedItem();
+        if (selectedMovie == null) {
+            showWarningAlert("No Selection", "Please select a movie to delete.");
+            return;
+        }
+        
+        Alert confirmDialog = new Alert(Alert.AlertType.CONFIRMATION);
+        confirmDialog.setTitle("Confirm Deletion");
+        confirmDialog.setHeaderText("Delete Movie");
+        confirmDialog.setContentText("Are you sure you want to delete \"" + selectedMovie.getTitle() + "\"?");
+        
+        Optional<ButtonType> result = confirmDialog.showAndWait();
+        if (result.isPresent() && result.get() == ButtonType.OK) {
+            try {
+                // Note: You'll need to implement removeMovie in DataManager
+                // dataManager.removeMovie(selectedMovie);
+                refreshMovieTable();
+                showSuccessAlert("Success", "Movie deleted successfully!");
+            } catch (Exception e) {
+                showErrorAlert("Error", "Failed to delete movie: " + e.getMessage());
+            }
+        }
+    }
+    
+    // Helper methods
+    private SearchCriteria buildSearchCriteria() {
+        SearchCriteria criteria = new SearchCriteria();
+        
+        if (!titleField.getText().trim().isEmpty()) {
+            criteria.setTitle(titleField.getText().trim());
+        }
+        if (!actorField.getText().trim().isEmpty()) {
+            criteria.setActorName(actorField.getText().trim());
+        }
+        if (!directorField.getText().trim().isEmpty()) {
+            criteria.setDirectorName(directorField.getText().trim());
+        }
+        
+        try {
+            if (!minImdbField.getText().trim().isEmpty()) {
+                criteria.setMinImdbRating(Double.parseDouble(minImdbField.getText().trim()));
+            }
+            if (!minUserField.getText().trim().isEmpty()) {
+                criteria.setMinUserRating(Double.parseDouble(minUserField.getText().trim()));
+            }
+        } catch (NumberFormatException e) {
+            throw new IllegalArgumentException("Rating fields must contain valid numbers.");
+        }
+        
+        return criteria;
+    }
+    
+    private String formatMovieForDisplay(Movie movie) {
+        return String.format("%s (%d) - %s | IMDB: %.1f | User: %s | Director: %s",
+            movie.getTitle(),
+            movie.getYear().getYear() + 1900,
+            movie.getGenre(),
+            movie.getImdbRating(),
+            movie.getUserRating() != null ? String.format("%.1f", movie.getUserRating()) : "N/A",
+            movie.getDirector() != null ? movie.getDirector() : "Unknown"
+        );
+    }
+    
+    private Dialog<Movie> createAddMovieDialog() {
+        Dialog<Movie> dialog = new Dialog<>();
+        dialog.setTitle("Add New Movie");
+        dialog.setHeaderText("Enter movie details");
+        
+        ButtonType addButtonType = new ButtonType("Add Movie", ButtonData.OK_DONE);
+        dialog.getDialogPane().getButtonTypes().addAll(addButtonType, ButtonType.CANCEL);
+        
+        GridPane grid = new GridPane();
+        grid.setHgap(10);
+        grid.setVgap(10);
+        grid.setPadding(new Insets(20, 150, 10, 10));
+        
+        TextField titleField = new TextField();
+        TextField yearField = new TextField();
+        TextField genreField = new TextField();
+        TextField directorField = new TextField();
+        TextField imdbRatingField = new TextField();
+        TextArea descriptionArea = new TextArea();
+        
+        titleField.setPromptText("Movie title");
+        yearField.setPromptText("Release year (e.g., 2023)");
+        genreField.setPromptText("Genre (e.g., ACTION, DRAMA)");
+        directorField.setPromptText("Director name");
+        imdbRatingField.setPromptText("IMDB rating (0.0 - 10.0)");
+        descriptionArea.setPromptText("Movie description (optional)");
+        descriptionArea.setPrefRowCount(3);
+        
+        grid.add(new Label("Title:"), 0, 0);
+        grid.add(titleField, 1, 0);
+        grid.add(new Label("Year:"), 0, 1);
+        grid.add(yearField, 1, 1);
+        grid.add(new Label("Genre:"), 0, 2);
+        grid.add(genreField, 1, 2);
+        grid.add(new Label("Director:"), 0, 3);
+        grid.add(directorField, 1, 3);
+        grid.add(new Label("IMDB Rating:"), 0, 4);
+        grid.add(imdbRatingField, 1, 4);
+        grid.add(new Label("Description:"), 0, 5);
+        grid.add(descriptionArea, 1, 5);
+        
+        Node addButton = dialog.getDialogPane().lookupButton(addButtonType);
+        addButton.setDisable(true);
+        
+        // Enable/disable add button based on required fields
+        titleField.textProperty().addListener((observable, oldValue, newValue) -> 
+            addButton.setDisable(newValue.trim().isEmpty()));
+        
+        dialog.getDialogPane().setContent(grid);
+        
+        dialog.setResultConverter(dialogButton -> {
+            if (dialogButton == addButtonType) {
+                try {
+                    String title = titleField.getText().trim();
+                    int year = Integer.parseInt(yearField.getText().trim());
+                    String genre = genreField.getText().trim().toUpperCase();
+                    String director = directorField.getText().trim();
+                    
+                    Movie movie = new Movie(title, year, genre, director, Map.of());
+                    
+                    // Set IMDB rating if provided
+                    if (!imdbRatingField.getText().trim().isEmpty()) {
+                        double rating = Double.parseDouble(imdbRatingField.getText().trim());
+                        movie.setImdbRating(rating);
+                    }
+                    
+                    // Set description if provided
+                    if (!descriptionArea.getText().trim().isEmpty()) {
+                        movie.setDescription(descriptionArea.getText().trim());
+                    }
+                    
+                    return movie;
+                } catch (NumberFormatException e) {
+                    showErrorAlert("Invalid Input", "Please enter valid numbers for year and rating.");
+                    return null;
+                } catch (Exception e) {
+                    showErrorAlert("Error", "Failed to create movie: " + e.getMessage());
+                    return null;
+                }
+            }
+            return null;
+        });
+        
+        return dialog;
+    }
+    
+    private Dialog<Double> createRatingDialog(Movie movie) {
+        Dialog<Double> dialog = new Dialog<>();
+        dialog.setTitle("Rate Movie");
+        dialog.setHeaderText("Rate: " + movie.getTitle());
+        
+        ButtonType rateButtonType = new ButtonType("Rate", ButtonData.OK_DONE);
+        dialog.getDialogPane().getButtonTypes().addAll(rateButtonType, ButtonType.CANCEL);
+        
+        VBox content = new VBox(10);
+        content.setPadding(new Insets(20));
+        
+        Label instructionLabel = new Label("Enter your rating (0.0 - 10.0):");
+        Slider ratingSlider = new Slider(0, 10, 5);
+        ratingSlider.setShowTickLabels(true);
+        ratingSlider.setShowTickMarks(true);
+        ratingSlider.setMajorTickUnit(1);
+        ratingSlider.setBlockIncrement(0.1);
+        
+        Label valueLabel = new Label("5.0");
+        ratingSlider.valueProperty().addListener((obs, oldVal, newVal) -> 
+            valueLabel.setText(String.format("%.1f", newVal.doubleValue())));
+        
+        content.getChildren().addAll(instructionLabel, ratingSlider, valueLabel);
+        dialog.getDialogPane().setContent(content);
+        
+        dialog.setResultConverter(dialogButton -> {
+            if (dialogButton == rateButtonType) {
+                return ratingSlider.getValue();
+            }
+            return null;
+        });
+        
+        return dialog;
+    }
+    
+    private void showMovieDetails(Movie movie) {
+        Dialog<Void> dialog = new Dialog<>();
+        dialog.setTitle("Movie Details");
+        dialog.setHeaderText(movie.getTitle());
+        
+        dialog.getDialogPane().getButtonTypes().add(ButtonType.CLOSE);
+        
+        VBox content = new VBox(10);
+        content.setPadding(new Insets(20));
+        content.setMaxWidth(400);
+        
+        content.getChildren().addAll(
+            new Label("Title: " + movie.getTitle()),
+            new Label("Year: " + (movie.getYear().getYear() + 1900)),
+            new Label("Genre: " + movie.getGenre()),
+            new Label("Director: " + (movie.getDirector() != null ? movie.getDirector() : "Unknown")),
+            new Label("IMDB Rating: " + String.format("%.1f", movie.getImdbRating())),
+            new Label("User Rating: " + (movie.getUserRating() != null ? String.format("%.1f", movie.getUserRating()) : "Not rated")),
+            new Label("Duration: " + (movie.getDuration() > 0 ? movie.getDuration() + " minutes" : "Unknown"))
+        );
+        
+        if (movie.getDescription() != null && !movie.getDescription().trim().isEmpty()) {
+            Label descLabel = new Label("Description:");
+            descLabel.setStyle("-fx-font-weight: bold;");
+            TextArea descArea = new TextArea(movie.getDescription());
+            descArea.setEditable(false);
+            descArea.setWrapText(true);
+            descArea.setPrefRowCount(4);
+            content.getChildren().addAll(descLabel, descArea);
+        }
+        
+        dialog.getDialogPane().setContent(content);
+        dialog.showAndWait();
+    }
+    
+    // Alert helper methods
+    private void showSuccessAlert(String title, String message) {
+        showAlert(Alert.AlertType.INFORMATION, title, message);
+    }
+    
+    private void showErrorAlert(String title, String message) {
+        showAlert(Alert.AlertType.ERROR, title, message);
+    }
+    
+    private void showWarningAlert(String title, String message) {
+        showAlert(Alert.AlertType.WARNING, title, message);
+    }
+    
+    private void showInfoAlert(String title, String message) {
+        showAlert(Alert.AlertType.INFORMATION, title, message);
+    }
+    
+    private void showAlert(Alert.AlertType type, String title, String message) {
+        Alert alert = new Alert(type);
+        alert.setTitle(title);
+        alert.setHeaderText(null);
+        alert.setContentText(message);
+        
+        if (primaryStage != null) {
+            alert.initOwner(primaryStage);
+        }
+        
+        alert.showAndWait();
+    }
+    
+    // Getters and setters
+    public void setPrimaryStage(Stage primaryStage) {
+        this.primaryStage = primaryStage;
+    }
+    
+    public DataManager getDataManager() {
+        return dataManager;
+    }
+}

