+package com.papel.imdb_clone.gui;
+
+import java.util.regex.Pattern;
+
+/**
+ * Utility class for data validation
+ */
+public class ValidationUtils {
+    
+    private static final Pattern EMAIL_PATTERN = Pattern.compile(
+        "^[a-zA-Z0-9_+&*-]+(?:\\.[a-zA-Z0-9_+&*-]+)*@(?:[a-zA-Z0-9-]+\\.)+[a-zA-Z]{2,7}$"
+    );
+    
+    private static final Pattern YEAR_PATTERN = Pattern.compile("^(19|20)\\d{2}$");
+    
+    /**
+     * Validates if a string is not null and not empty after trimming
+     */
+    public static boolean isNotEmpty(String value) {
+        return value != null && !value.trim().isEmpty();
+    }
+    
+    /**
+     * Validates if a string represents a valid year (1900-2099)
+     */
+    public static boolean isValidYear(String year) {
+        if (!isNotEmpty(year)) return false;
+        return YEAR_PATTERN.matcher(year.trim()).matches();
+    }
+    
+    /**
+     * Validates if a string represents a valid rating (0.0-10.0)
+     */
+    public static boolean isValidRating(String rating) {
+        if (!isNotEmpty(rating)) return true; // Empty is valid (optional)
+        
+        try {
+            double value = Double.parseDouble(rating.trim());
+            return value >= 0.0 && value <= 10.0;
+        } catch (NumberFormatException e) {
+            return false;
+        }
+    }
+    
+    /**
+     * Validates if a string represents a valid duration in minutes
+     */
+    public static boolean isValidDuration(String duration) {
+        if (!isNotEmpty(duration)) return true; // Empty is valid (optional)
+        
+        try {
+            int value = Integer.parseInt(duration.trim());
+            return value > 0 && value <= 1000; // Reasonable range for movie duration
+        } catch (NumberFormatException e) {
+            return false;
+        }
+    }
+    
+    /**
+     * Validates if a string is a valid email address
+     */
+    public static boolean isValidEmail(String email) {
+        if (!isNotEmpty(email)) return false;
+        return EMAIL_PATTERN.matcher(email.trim()).matches();
+    }
+    
+    /**
+     * Validates if a string represents a valid genre
+     */
+    public static boolean isValidGenre(String genre) {
+        if (!isNotEmpty(genre)) return false;
+        
+        // Check if it's a valid enum value
+        try {
+            com.papel.imdb_clone.enums.Genre.valueOf(genre.trim().toUpperCase().replace(" ", "_"));
+            return true;
+        } catch (IllegalArgumentException e) {
+            return false;
+        }
+    }
+    
+    /**
+     * Validates movie title length
+     */
+    public static boolean isValidTitle(String title) {
+        return isNotEmpty(title) && title.trim().length() >= 1 && title.trim().length() <= 200;
+    }
+    
+    /**
+     * Validates person name (director, actor)
+     */
+    public static boolean isValidPersonName(String name) {
+        if (!isNotEmpty(name)) return true; // Optional field
+        return name.trim().length() >= 2 && name.trim().length() <= 100;
+    }
+    
+    /**
+     * Sanitizes input by trimming and removing extra whitespace
+     */
+    public static String sanitizeInput(String input) {
+        if (input == null) return "";
+        return input.trim().replaceAll("\\s+", " ");
+    }
+    
+    /**
+     * Validates and parses a year string
+     */
+    public static int parseYear(String yearStr) throws IllegalArgumentException {
+        if (!isValidYear(yearStr)) {
+            throw new IllegalArgumentException("Invalid year format. Please enter a year between 1900 and 2099.");
+        }
+        return Integer.parseInt(yearStr.trim());
+    }
+    
+    /**
+     * Validates and parses a rating string
+     */
+    public static double parseRating(String ratingStr) throws IllegalArgumentException {
+        if (!isValidRating(ratingStr)) {
+            throw new IllegalArgumentException("Invalid rating. Please enter a value between 0.0 and 10.0.");
+        }
+        return Double.parseDouble(ratingStr.trim());
+    }
+    
+    /**
+     * Validates and parses a duration string
+     */
+    public static int parseDuration(String durationStr) throws IllegalArgumentException {
+        if (!isValidDuration(durationStr)) {
+            throw new IllegalArgumentException("Invalid duration. Please enter a positive number of minutes.");
+        }
+        return Integer.parseInt(durationStr.trim());
+    }
+    
+    /**
+     * Gets validation error message for a field
+     */
+    public static String getValidationError(String fieldName, String value, String fieldType) {
+        switch (fieldType.toLowerCase()) {
+            case "title":
+                if (!isValidTitle(value)) {
+                    return fieldName + " must be between 1 and 200 characters.";
+                }
+                break;
+            case "year":
+                if (!isValidYear(value)) {
+                    return fieldName + " must be a valid year (1900-2099).";
+                }
+                break;
+            case "rating":
+                if (!isValidRating(value)) {
+                    return fieldName + " must be a number between 0.0 and 10.0.";
+                }
+                break;
+            case "duration":
+                if (!isValidDuration(value)) {
+                    return fieldName + " must be a positive number of minutes.";
+                }
+                break;
+            case "genre":
+                if (!isValidGenre(value)) {
+                    return fieldName + " must be a valid genre.";
+                }
+                break;
+            case "person":
+                if (!isValidPersonName(value)) {
+                    return fieldName + " must be between 2 and 100 characters.";
+                }
+                break;
+            case "email":
+                if (!isValidEmail(value)) {
+                    return fieldName + " must be a valid email address.";
+                }
+                break;
+        }
+        return null; // No error
+    }
