+package com.papel.imdb_clone.gui;
+
+import javafx.application.Application;
+import javafx.fxml.FXMLLoader;
+import javafx.scene.Parent;
+import javafx.scene.Scene;
+import javafx.stage.Stage;
+
+import java.io.IOException;
+
+/**
+ * Simple IMDB Clone Application without CSS dependencies for IntelliJ compatibility
+ */
+public class SimpleMovieApp extends Application {
+    
+    private static final String FXML_FILE = "/fxml/main-improved.fxml";
+    private static final String APP_TITLE = "IMDB Clone - Movie & Series Manager";
+    private static final double MIN_WIDTH = 1000.0;
+    private static final double MIN_HEIGHT = 700.0;
+    
+    @Override
+    public void start(Stage primaryStage) {
+        try {
+            // Load FXML
+            FXMLLoader loader = new FXMLLoader(getClass().getResource(FXML_FILE));
+            Parent root = loader.load();
+            
+            // Get controller and set primary stage
+            MainController controller = loader.getController();
+            controller.setPrimaryStage(primaryStage);
+            
+            // Create scene without CSS
+            Scene scene = new Scene(root, MIN_WIDTH, MIN_HEIGHT);
+            
+            // Configure stage
+            primaryStage.setTitle(APP_TITLE);
+            primaryStage.setScene(scene);
+            primaryStage.setMinWidth(MIN_WIDTH);
+            primaryStage.setMinHeight(MIN_HEIGHT);
+            
+            // Center on screen
+            primaryStage.centerOnScreen();
+            
+            // Show the application
+            primaryStage.show();
+            
+        } catch (IOException e) {
+            showErrorAndExit("Failed to load application", e);
+        } catch (Exception e) {
+            showErrorAndExit("Unexpected error occurred", e);
+        }
+    }
+    
+    private void showErrorAndExit(String message, Exception e) {
+        System.err.println(message + ": " + e.getMessage());
+        e.printStackTrace();
+        
+        // Try to show a JavaFX alert if possible
+        try {
+            javafx.scene.control.Alert alert = new javafx.scene.control.Alert(
+                javafx.scene.control.Alert.AlertType.ERROR
+            );
+            alert.setTitle("Application Error");
+            alert.setHeaderText(message);
+            alert.setContentText(e.getMessage());
+            alert.showAndWait();
+        } catch (Exception alertException) {
+            System.err.println("Could not show error dialog: " + alertException.getMessage());
+        }
+        
+        System.exit(1);
+    }
+    
+    public static void main(String[] args) {
+        launch(args);
+    }
+}

