import com.papel.imdb_clone.model.Movie;
import javafx.application.Platform;
import javafx.fxml.FXML;
import javafx.geometry.Insets;
import javafx.scene.Node;
import javafx.scene.control.*;
import javafx.scene.control.cell.PropertyValueFactory;
import javafx.scene.layout.GridPane;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.sql.Date;
import java.util.Optional;

/**
 * Controller for managing movie-related UI components and logic.
 */
public class MovieController extends BaseController {
    private static final Logger logger = LoggerFactory.getLogger(MovieController.class);

    @FXML
    private TableView<Movie> movieTable;
    @FXML
    private TableColumn<Movie, String> titleColumn;
    @FXML
    private TableColumn<Movie, Integer> yearColumn;
    @FXML
    private TableColumn<Movie, String> genreColumn;
    @FXML
    private TableColumn<Movie, Double> ratingColumn;

    @Override
    protected void initializeController() {
        setupMovieTable();
    }

    private void setupMovieTable() {
        try {
            // Configure table columns
            titleColumn.setCellValueFactory(new PropertyValueFactory<>("title"));
            yearColumn.setCellValueFactory(cellData -> {
                Date date = cellData.getValue().getReleaseDate();
                return new javafx.beans.property.SimpleIntegerProperty(date.getYear() + 1900).asObject();
            });
            genreColumn.setCellValueFactory(cellData ->
                    new javafx.beans.property.SimpleStringProperty(cellData.getValue().getGenre().toString())
            );
            ratingColumn.setCellValueFactory(new PropertyValueFactory<>("imdbRating"));

            logger.debug("Movie table columns configured");
        } catch (Exception e) {
            logger.error("Error setting up movie table", e);
            throw new RuntimeException("Failed to initialize movie table", e);
        }
    }

    /**
     * Refresh the movie table with the latest data.
     */
    public void refreshMovieTable() {
        try {
            // TODO: Implement data refresh logic
            logger.debug("Refreshing movie table");
        } catch (Exception e) {
            logger.error("Error refreshing movie table", e);
            showError("Refresh Error", "Failed to refresh movie data");
        }
    }

    /**
     * Handles the Add Movie button click event.
     * Shows a dialog to add a new movie.
     */
    @FXML
    private void showAddMovieDialog() {
        try {
            // Create a dialog
            Dialog<Movie> dialog = new Dialog<>();
            dialog.setTitle("Add New Movie");
            dialog.setHeaderText("Enter movie details");

            // Set the button types
            ButtonType addButton = new ButtonType("Add", ButtonBar.ButtonData.OK_DONE);
            dialog.getDialogPane().getButtonTypes().addAll(addButton, ButtonType.CANCEL);

            // Create the form layout
            GridPane grid = new GridPane();
            grid.setHgap(10);
            grid.setVgap(10);
            grid.setPadding(new Insets(20, 150, 10, 10));

            // Create form fields
            TextField titleField = new TextField();
            titleField.setPromptText("Title");

            TextField yearField = new TextField();
            yearField.setPromptText("Year (e.g., 2023)");

            ComboBox<Genre> genreComboBox = new ComboBox<>();
            genreComboBox.getItems().setAll(Genre.values());
            genreComboBox.setPromptText("Select Genre");

            TextField ratingField = new TextField();
            ratingField.setPromptText("IMDb Rating (0.0-10.0)");

            // Add fields to grid
            grid.add(new Label("Title:"), 0, 0);
            grid.add(titleField, 1, 0);
            grid.add(new Label("Release Year:"), 0, 1);
            grid.add(yearField, 1, 1);
            grid.add(new Label("Genre:"), 0, 2);
            grid.add(genreComboBox, 1, 2);
            grid.add(new Label("IMDb Rating:"), 0, 3);
            grid.add(ratingField, 1, 3);

            // Enable/Disable add button depending on whether a title was entered
            Node addButtonNode = dialog.getDialogPane().lookupButton(addButton);
            addButtonNode.setDisable(true);

            // Do some validation
            // Enable/disable add button based on form validity
            titleField.textProperty().addListener((observable, oldValue, newValue) -> {
                validateForm(titleField, yearField, genreComboBox, ratingField, addButtonNode);
            });

            yearField.textProperty().addListener((observable, oldValue, newValue) -> {
                validateForm(titleField, yearField, genreComboBox, ratingField, addButtonNode);
            });

            genreComboBox.valueProperty().addListener((observable, oldValue, newValue) -> {
                validateForm(titleField, yearField, genreComboBox, ratingField, addButtonNode);
            });

            ratingField.textProperty().addListener((observable, oldValue, newValue) -> {
                validateForm(titleField, yearField, genreComboBox, ratingField, addButtonNode);
            });

            dialog.getDialogPane().setContent(grid);

            // Request focus on the title field by default
            Platform.runLater(titleField::requestFocus);

            // Convert the result to a Movie when the add button is clicked
            dialog.setResultConverter(dialogButton -> {
                if (dialogButton == addButton) {
                    try {
                        String title = titleField.getText().trim();
                        int year = Integer.parseInt(yearField.getText().trim());
                        Genre genre = genreComboBox.getValue();
                        double rating = Double.parseDouble(ratingField.getText().trim());

                        // Create a default director (can be updated later)
                        Director director = new Director("Default", "Director");

                        // Create and return a new Movie object
                        Movie movie = new Movie(
                                title,
                                year,
                                director,
                                String.valueOf(rating),
                                120, // Default duration in minutes
                                new ArrayList<>() // Empty actors list
                        );

                        // Set additional properties
                        movie.setImdbRating(rating);
                        movie.getGenres().clear(); // Remove default genre
                        movie.getGenres().add(genre); // Add selected genre

                        return movie;
                    } catch (NumberFormatException e) {
                        showError("Invalid Input", "Please enter valid values.\n" +
                                "- Year must be a number (e.g., 2023)\n" +
                                "- Rating must be a number between 0.0 and 10.0");
                        return null;
                    }
                }
                return null;
            });

            // Show the dialog and handle the result
            Optional<Movie> result = dialog.showAndWait();

            result.ifPresent(movie -> {
                // Add the new movie to your data model
                // dataManager.addMovie(movie);

                // Refresh the table to show the new movie
                refreshMovieTable();

                // Show success message
                showSuccess("Success", "Movie added successfully!");
            });

        } catch (Exception e) {
            logger.error("Error showing add movie dialog", e);
            showError("Error", "Failed to show add movie dialog: " + e.getMessage());
        }
    }
}

