package com.papel.imdb_clone.service;

import com.papel.imdb_clone.data.DataManager;
import com.papel.imdb_clone.model.User;
import com.papel.imdb_clone.exceptions.DuplicateUsernameException;
import com.papel.imdb_clone.exceptions.InvalidEmailException;

import java.security.MessageDigest;
import java.security.NoSuchAlgorithmException;
import java.security.SecureRandom;
import java.util.Base64;
import java.util.List;
import java.util.stream.Collectors;

/**
 * Service class for user management including authentication and user operations.
 */
public class UserService {
    private final DataManager dataManager;
    private User currentUser;

    public UserService(DataManager dataManager) {
        this.dataManager = dataManager;
    }

    /**
     * Registers a new user with the given details.
     * 
     * @param firstName User's first name
     * @param lastName User's last name
     * @param username Unique username
     * @param email Valid email address
     * @param password Plain text password (will be hashed)
     * @param gender User's gender
     * @return The created User object
     * @throws DuplicateUsernameException if username already exists
     * @throws InvalidEmailException if email format is invalid
     */
    public User registerUser(String firstName, String lastName, String username, 
                           String email, String password, char gender) 
            throws DuplicateUsernameException, InvalidEmailException {
        
        // Check if username already exists
        if (dataManager.findUserByUsername(username) != null) {
            throw new DuplicateUsernameException("Username '" + username + "' already exists");
        }

        // Create new user
        User user = new User(firstName, lastName, username, gender, email);
        
        // Hash and set password
        String hashedPassword = hashPassword(password);
        user.setPassword(hashedPassword);
        
        // Add to data manager
        dataManager.addUser(user);
        
        return user;
    }

    /**
     * Authenticates a user with username and password.
     * 
     * @param username The username
     * @param password The plain text password
     * @return true if authentication successful, false otherwise
     */
    public boolean login(String username, String password) {
        User user = dataManager.findUserByUsername(username);
        if (user == null) {
            return false;
        }

        String hashedPassword = hashPassword(password);
        if (user.getPassword() != null && user.getPassword().equals(hashedPassword)) {
            this.currentUser = user;
            return true;
        }
        
        return false;
    }

    /**
     * Logs out the current user.
     */
    public void logout() {
        this.currentUser = null;
    }

    /**
     * Gets the currently logged-in user.
     * 
     * @return Current user or null if not logged in
     */
    public User getCurrentUser() {
        return currentUser;
    }

    /**
     * Checks if a user is currently logged in.
     * 
     * @return true if user is logged in, false otherwise
     */
    public boolean isLoggedIn() {
        return currentUser != null;
    }

    /**
     * Updates user profile information.
     * 
     * @param user The user to update
     * @param firstName New first name
     * @param lastName New last name
     * @param email New email address
     * @throws InvalidEmailException if email format is invalid
     */
    public void updateUserProfile(User user, String firstName, String lastName, String email) 
            throws InvalidEmailException {
        user.setFirstName(firstName);
        user.setLastName(lastName);
        user.setEmail(email);
    }

    /**
     * Changes user password.
     * 
     * @param user The user
     * @param oldPassword Current password
     * @param newPassword New password
     * @return true if password changed successfully, false if old password is incorrect
     */
    public boolean changePassword(User user, String oldPassword, String newPassword) {
        String hashedOldPassword = hashPassword(oldPassword);
        if (!user.getPassword().equals(hashedOldPassword)) {
            return false;
        }
        
        String hashedNewPassword = hashPassword(newPassword);
        user.setPassword(hashedNewPassword);
        return true;
    }

    /**
     * Gets all users (admin function).
     * 
     * @return List of all users
     */
    public List<User> getAllUsers() {
        return dataManager.getUsers();
    }

    /**
     * Searches users by name or username.
     * 
     * @param query Search query
     * @return List of matching users
     */
    public List<User> searchUsers(String query) {
        if (query == null || query.trim().isEmpty()) {
            return getAllUsers();
        }
        
        String lowerQuery = query.toLowerCase();
        return dataManager.getUsers().stream()
                .filter(user -> 
                    user.getFirstName().toLowerCase().contains(lowerQuery) ||
                    user.getLastName().toLowerCase().contains(lowerQuery) ||
                    user.getUsername().toLowerCase().contains(lowerQuery))
                .collect(Collectors.toList());
    }

    /**
     * Hashes a password using SHA-256.
     * In a real application, you should use BCrypt or similar.
     * 
     * @param password Plain text password
     * @return Hashed password
     */
    private String hashPassword(String password) {
        try {
            MessageDigest md = MessageDigest.getInstance("SHA-256");
            byte[] hashedBytes = md.digest(password.getBytes());
            return Base64.getEncoder().encodeToString(hashedBytes);
        } catch (NoSuchAlgorithmException e) {
            throw new RuntimeException("SHA-256 algorithm not available", e);
        }
    }

    /**
     * Generates a secure random salt (for future BCrypt implementation).
     * 
     * @return Random salt string
     */
    private String generateSalt() {
        SecureRandom random = new SecureRandom();
        byte[] salt = new byte[16];
        random.nextBytes(salt);
        return Base64.getEncoder().encodeToString(salt);
    }

    /**
     * Validates password strength.
     * 
     * @param password Password to validate
     * @return true if password meets requirements
     */
    public boolean isValidPassword(String password) {
        if (password == null || password.length() < 6) {
            return false;
        }
        
        // Check for at least one digit, one letter
        boolean hasDigit = password.chars().anyMatch(Character::isDigit);
        boolean hasLetter = password.chars().anyMatch(Character::isLetter);
        
        return hasDigit && hasLetter;
    }

    /**
     * Gets password strength description.
     * 
     * @param password Password to check
     * @return Strength description
     */
    public String getPasswordStrength(String password) {
        if (password == null || password.length() < 6) {
            return "Too short (minimum 6 characters)";
        }
        
        int score = 0;
        if (password.length() >= 8) score++;
        if (password.chars().anyMatch(Character::isDigit)) score++;
        if (password.chars().anyMatch(Character::isLowerCase)) score++;
        if (password.chars().anyMatch(Character::isUpperCase)) score++;
        if (password.chars().anyMatch(ch -> "!@#$%^&*()_+-=[]{}|;:,.<>?".indexOf(ch) >= 0)) score++;
        
        return switch (score) {
            case 0, 1 -> "Very Weak";
            case 2 -> "Weak";
            case 3 -> "Fair";
            case 4 -> "Good";
            case 5 -> "Strong";
            default -> "Unknown";
        };
    }
}

