package com.papel.imdb_clone.controllers;

import com.papel.imdb_clone.data.DataManager;
import com.papel.imdb_clone.gui.DialogUtils;
import com.papel.imdb_clone.gui.UiAnimations;
import com.papel.imdb_clone.model.ForumComment;
import com.papel.imdb_clone.model.ForumPost;
import com.papel.imdb_clone.model.User;
import javafx.application.Platform;
import javafx.beans.property.SimpleObjectProperty;
import javafx.beans.property.SimpleStringProperty;
import javafx.collections.FXCollections;
import javafx.collections.ObservableList;
import javafx.collections.transformation.FilteredList;
