public class ForumController extends BaseController {
    private static final Logger logger = LoggerFactory.getLogger(ForumController.class);

    private DataManager dataManager;
    private Stage primaryStage;
    private User currentUser;
    private String sessionToken;

    @FXML
    private TableView<ForumPost> forumTable;
    @FXML
    private TableColumn<ForumPost, String> titleColumn;
    @FXML
    private TableColumn<ForumPost, String> authorColumn;
    @FXML
    private TableColumn<ForumPost, Integer> votesColumn;
    @FXML
    private TableColumn<ForumPost, String> dateColumn;
    @FXML
    private TableColumn<ForumPost, ForumPost> actionsColumn;
    @FXML
    private TextField searchField;
    @FXML
    private ProgressIndicator loadingIndicator;
    @FXML
    private StackPane forumRootStack;
    @FXML
    private ChoiceBox<String> sortChoice;
    @FXML
    private CheckBox myPostsOnly;

    private final ObservableList<ForumPost> postList = FXCollections.observableArrayList();

    public void setDataManager(DataManager dataManager) {
        this.dataManager = dataManager;
        try {
            loadForumPosts();
        } catch (Exception e) {
            logger.error("Forum DataManager setup error", e);
            DialogUtils.showError("Forum Setup Error", "Failed to set up forum data: " + e.getMessage());
        }
    }

    public void setPrimaryStage(Stage primaryStage) {
        this.primaryStage = primaryStage;
    }

    public void setCurrentUser(User user) {
        this.currentUser = user;
        updateLoginUI();
    }

    public void setSessionToken(String sessionToken) {
        this.sessionToken = sessionToken;
    }

    private void updateLoginUI() {
        if (forumTable != null) {
            forumTable.refresh();
        }
    }

    @Override
    protected void initializeController() throws Exception {
        setupTableColumns();
        setupEventHandlers();
        setupSearchFunctionality();
        // Load persisted forum preferences
        SettingsManager sm = SettingsManager.getInstance();
        if (sortChoice != null) {
            String prefSort = sm.getForumSort();
            if (prefSort != null) {
                sortChoice.getSelectionModel().select(prefSort);
            }
            sortChoice.getSelectionModel().selectedItemProperty().addListener((o, ov, nv) -> {
                if (nv != null) {
                    sm.setForumSort(nv);
                    sm.save();
                }
            });
        }
        if (myPostsOnly != null) {
            myPostsOnly.setSelected(sm.isForumMyPostsOnly());
            myPostsOnly.selectedProperty().addListener((o, ov, nv) -> {
                sm.setForumMyPostsOnly(Boolean.TRUE.equals(nv));
                sm.save();
            });
        }
        logger.debug("ForumController initialized");
    }

    private void setupTableColumns() {
        titleColumn.setCellValueFactory(cellData -> new SimpleStringProperty(cellData.getValue().getTitle()));
        authorColumn.setCellValueFactory(cellData -> {
            User author = dataManager != null ? dataManager.findUserById(cellData.getValue().getUserId()) : null;
            return new SimpleStringProperty(author != null ? author.getUsername() : "Unknown");
        });
        // Render author with avatar icon
        authorColumn.setCellFactory(col -> new TableCell<>() {
            private final HBox box = new HBox(8);
            private final Label name = new Label();

            {
                box.setAlignment(Pos.CENTER_LEFT);
                name.getStyleClass().add("author-name");
            }

            @Override
            protected void updateItem(String username, boolean empty) {
                super.updateItem(username, empty);
                if (empty) {
                    setGraphic(null);
                } else {
                    ForumPost post = getTableView().getItems().get(getIndex());
                    User author = dataManager != null ? dataManager.findUserById(post.getUserId()) : null;
                    Node avatar = createAvatarNode(author, 24);
                    name.setText(username != null ? username : "Unknown");
                    box.getChildren().setAll(avatar, name);
                    setGraphic(box);
                }
            }
        });
        votesColumn.setCellValueFactory(cellData -> new SimpleObjectProperty<>(cellData.getValue().getUpvotes() - cellData.getValue().getDownvotes()));
        // Relative time display for createdAt
        dateColumn.setCellValueFactory(cellData -> new SimpleStringProperty(
                com.papel.imdb_clone.util.TimeUtils.toRelative(cellData.getValue().getCreatedAt())
        ));

        actionsColumn.setCellFactory(createActionsCellFactory());
    }

    private void setupEventHandlers() {
        forumTable.setOnKeyPressed(event -> {
            if (event.getCode() == KeyCode.ENTER) {
                ForumPost selectedPost = forumTable.getSelectionModel().getSelectedItem();
                if (selectedPost != null) {
                    showPostDetails(selectedPost);
                }
            }
        });
    }

    private void setupSearchFunctionality() {
        FilteredList<ForumPost> filteredPosts = new FilteredList<>(postList, p -> true);
        if (searchField != null) {
            searchField.textProperty().addListener((observable, oldValue, newValue) ->
                    filteredPosts.setPredicate(post -> {
                        if (newValue == null || newValue.isEmpty()) {
                            return myPostsOnly == null || !myPostsOnly.isSelected() || (currentUser != null && post.getUserId() == currentUser.getId());
                        }
                        String lowerCaseFilter = newValue.toLowerCase();
                        boolean matches = post.getTitle().toLowerCase().contains(lowerCaseFilter) ||
                                post.getContent().toLowerCase().contains(lowerCaseFilter);
                        if (myPostsOnly != null && myPostsOnly.isSelected()) {
                            return matches && currentUser != null && post.getUserId() == currentUser.getId();
                        }
                        return matches;
                    }));
        }
        if (myPostsOnly != null) {
            myPostsOnly.selectedProperty().addListener((obs, ov, nv) -> {
                String q = searchField != null ? searchField.getText() : null;
                // Trigger predicate recompute
                filteredPosts.setPredicate(post -> {
                    boolean base = (q == null || q.isEmpty()) || post.getTitle().toLowerCase().contains(q.toLowerCase()) || post.getContent().toLowerCase().contains(q.toLowerCase());
                    if (nv) {
                        return base && currentUser != null && post.getUserId() == currentUser.getId();
                    }
                    return base;
                });
            });
        }

        SortedList<ForumPost> sortedPosts = new SortedList<>(filteredPosts);
        if (sortChoice != null) {
            sortChoice.getSelectionModel().selectedItemProperty().addListener((obs, ov, sel) -> {
                Comparator<ForumPost> comp;
                if ("Top".equalsIgnoreCase(sel)) {
                    comp = Comparator.comparingInt((ForumPost p) -> p.getUpvotes() - p.getDownvotes()).reversed()
                            .thenComparing(ForumPost::getCreatedAt).reversed();
                } else if ("Oldest".equalsIgnoreCase(sel)) {
                    comp = Comparator.comparing(ForumPost::getCreatedAt);
                } else { // Newest default
                    comp = Comparator.comparing(ForumPost::getCreatedAt).reversed();
                }
                sortedPosts.setComparator(comp);
            });
            // default selection
            if (sortChoice.getSelectionModel().getSelectedItem() == null) {
                sortChoice.getSelectionModel().select("Newest");
            }
        }
        forumTable.setItems(sortedPosts);
    }

    private Callback<TableColumn<ForumPost, ForumPost>, TableCell<ForumPost, ForumPost>> createActionsCellFactory() {
        logger.debug("Creating actions cell factory");
        return param -> new TableCell<>() {
            private final HBox container = new HBox(3);
            private final Button upvoteButton = new Button("ðŸ‘");
            private final Button downvoteButton = new Button("ðŸ‘Ž");
            private final Button viewButton = new Button("ðŸ‘ï¸");
            private final Button replyButton = new Button("ðŸ’¬");
            private final Button boostButton = new Button("ðŸš€");
            private final Button editButton = new Button("âœï¸");
            private final Button deleteButton = new Button("ðŸ—‘ï¸");

            {
                logger.debug("Initializing cell factory components");
                container.setAlignment(Pos.CENTER);
                container.setSpacing(3);

                upvoteButton.getStyleClass().add("forum-upvote-button");
                downvoteButton.getStyleClass().add("forum-downvote-button");
                viewButton.getStyleClass().add("view-button");
                replyButton.getStyleClass().add("secondary-button");
                boostButton.getStyleClass().add("primary-button");
                editButton.getStyleClass().add("secondary-button");
                deleteButton.getStyleClass().add("danger-button");

                upvoteButton.setMinWidth(30);
                downvoteButton.setMinWidth(30);
                viewButton.setMinWidth(30);
                replyButton.setMinWidth(30);
                boostButton.setMinWidth(30);
                editButton.setMinWidth(30);
                deleteButton.setMinWidth(30);

                upvoteButton.setOnAction(e -> {
                    ForumPost post = getTableView().getItems().get(getIndex());
                    UiAnimations.playVoteClick(upvoteButton, true);
                    handleUpvote(post);
                });

                downvoteButton.setOnAction(e -> {
                    ForumPost post = getTableView().getItems().get(getIndex());
                    UiAnimations.playVoteClick(downvoteButton, false);
                    handleDownvote(post);
                });

                viewButton.setOnAction(e -> {
                    ForumPost post = getTableView().getItems().get(getIndex());
                    showPostDetails(post);
                });

                replyButton.setOnAction(e -> {
                    ForumPost post = getTableView().getItems().get(getIndex());
                    handleReplyToPost(post);
                });

                boostButton.setTooltip(new Tooltip("Boost Post (Pro/Ultra)"));
                boostButton.setOnAction(e -> {
                    ForumPost post = getTableView().getItems().get(getIndex());
                    String plan = com.papel.imdb_clone.util.SettingsManager.getInstance().getPlan();
                    if (plan == null) plan = "free";
                    boolean allowed = "pro".equalsIgnoreCase(plan) || "ultra".equalsIgnoreCase(plan);
                    if (!allowed) {
                        com.papel.imdb_clone.gui.DialogUtils.showInfo("Upgrade required", "Boosting posts requires Pro or Ultra. See Pricing to upgrade.");
                        UiAnimations.playVoteClick(boostButton, true);
                        return;
                    }
                    // Visual teaser: briefly highlight the row and show confirmation.
                    UiAnimations.playVoteClick(boostButton, true);
                    com.papel.imdb_clone.gui.DialogUtils.showInfo("Boosted!", "Your post will be highlighted and shown to more users.");
                });

                editButton.setOnAction(e -> {
                    ForumPost post = getTableView().getItems().get(getIndex());
                    handleEditPost(post);
                });

                deleteButton.setOnAction(e -> {
                    ForumPost post = getTableView().getItems().get(getIndex());
                    handleDeletePost(post);
                });

                container.getChildren().addAll(upvoteButton, downvoteButton, viewButton, replyButton, boostButton, editButton, deleteButton);
                logger.debug("Cell factory components initialized");
            }

            @Override
            protected void updateItem(ForumPost post, boolean empty) {
                logger.debug("Updating cell item - empty: {}, post: {}", empty, post != null ? post.getIdSanitized() : "null");
                super.updateItem(post, empty);
                if (empty || post == null) {
                    setGraphic(null);
                } else {
                    setGraphic(container);
                    updateVoteButtons(post);
                }
            }

            private void updateVoteButtons(ForumPost post) {
                logger.debug("Updating vote buttons for post {}", post.getIdSanitized());
                if (currentUser == null) {
                    upvoteButton.setDisable(true);
                    downvoteButton.setDisable(true);
                    replyButton.setDisable(true);
                    editButton.setDisable(true);
                    deleteButton.setDisable(true);
                    return;
                }

                boolean hasUpvoted = post.getUpvotedBy().contains(currentUser.getId());
                boolean hasDownvoted = post.getDownvotedBy().contains(currentUser.getId());
                boolean isAuthor = post.getUserId() == currentUser.getId();

                // Keep vote buttons enabled for toggle-like UX; just reflect state via style
                upvoteButton.setDisable(false);
                downvoteButton.setDisable(false);

                // Update visual state classes
                upvoteButton.getStyleClass().removeAll("voted-up");
                downvoteButton.getStyleClass().removeAll("voted-down");
                if (hasUpvoted) {
                    if (!upvoteButton.getStyleClass().contains("voted-up")) {
                        upvoteButton.getStyleClass().add("voted-up");
                    }
                }
                if (hasDownvoted) {
                    if (!downvoteButton.getStyleClass().contains("voted-down")) {
                        downvoteButton.getStyleClass().add("voted-down");
                    }
                }

                replyButton.setDisable(false);
                editButton.setDisable(!isAuthor);
                deleteButton.setDisable(!isAuthor);
            }
        };
    }

    public void loadForumPosts() {
        if (dataManager == null) {
            logger.warn("DataManager is not initialized");
            return;
        }

        Task<List<ForumPost>> loadPostsTask = new Task<List<ForumPost>>() {
            @Override
            protected List<ForumPost> call() throws Exception {
                return dataManager.getAllForumPosts();
            }

            @Override
            protected void succeeded() {
                List<ForumPost> posts = getValue();
                Platform.runLater(() -> {
                    postList.setAll(posts);
                    logger.info("Loaded {} forum posts.", posts.size());
                    if (loadingIndicator != null) {
                        loadingIndicator.setVisible(false);
                    }
                });
            }

            @Override
            protected void failed() {
                logger.error("Failed to load forum posts", getException());
                Platform.runLater(() -> {
                    DialogUtils.showError("Load Error", "Could not reload forum posts: " + getException().getMessage());
                    if (loadingIndicator != null) {
                        loadingIndicator.setVisible(false);
                    }
                });
            }
        };

        if (loadingIndicator != null) {
            loadingIndicator.setVisible(true);
        }
        new Thread(loadPostsTask).start();
    }

    @FXML
    private void showCreatePostDialog() {
        if (currentUser == null || sessionToken == null) {
            DialogUtils.showError("Not Logged In", "You must be logged in to create a post.");
            return;
        }
        try {
            TextInputDialog titleDialog = DialogUtils.createTextInputDialog(
                    "Create New Post",
                    null,
                    "Enter post title:",
                    ""
            );

            Node okButton = titleDialog.getDialogPane().lookupButton(ButtonType.OK);
            okButton.setDisable(true);
            titleDialog.getEditor().textProperty().addListener((obs, oldVal, newVal) -> {
                okButton.setDisable(newVal.trim().isEmpty() || newVal.trim().length() > 200);
            });

            titleDialog.showAndWait().ifPresent(title -> {
                if (!title.trim().isEmpty() && title.trim().length() <= 200) {
                    TextInputDialog contentDialog = DialogUtils.createTextInputDialog(
                            "Create New Post",
                            null,
                            "Enter post content:",
                            ""
                    );
                    Node okContentButton = contentDialog.getDialogPane().lookupButton(ButtonType.OK);
                    okContentButton.setDisable(true);
                    contentDialog.getEditor().textProperty().addListener((obs, oldVal, newVal) -> {
                        okContentButton.setDisable(newVal.trim().isEmpty() || newVal.trim().length() > 1000);
                    });
                    contentDialog.showAndWait().ifPresent(content -> {
                        if (!content.trim().isEmpty() && content.trim().length() <= 1000) {
                            try {
                                ForumPost newPost = new ForumPost(currentUser.getId(), title, content);
                                dataManager.addForumPost(newPost);
                                loadForumPosts();
                                logger.info("New forum post created: " + title);
                            } catch (Exception e) {
                                logger.error("Forum post creation error", e);
                                DialogUtils.showError("Create Post Error", "Failed to create post: " + e.getMessage());
                            }
                        } else {
                            DialogUtils.showError("Validation Error", "Post content is required and must be under 1000 characters.");
                        }
                    });
                } else {
                    DialogUtils.showError("Validation Error", "Title is required and must be under 200 characters.");
                }
            });
        } catch (Exception e) {
            logger.error("Forum post dialog error", e);
            DialogUtils.showError("Dialog Error", "Failed to open post creation dialog: " + e.getMessage());
        }
    }

    public void handleUpvote(ForumPost post) {
        if (currentUser == null || sessionToken == null) {
            DialogUtils.showError("Not Logged In", "You must be logged in to upvote a post.");
            return;
        }
        try {
            dataManager.upvoteForumPost(post.getIdSanitized(), currentUser.getId());
            loadForumPosts();
            com.papel.imdb_clone.gui.Toast.show(forumRootStack, "Upvoted", com.papel.imdb_clone.gui.Toast.Type.SUCCESS);
        } catch (Exception e) {
            logger.error("Forum upvote error", e);
            DialogUtils.showError("Upvote Error", "Failed to upvote post: " + e.getMessage());
        }
    }

    public void handleDownvote(ForumPost post) {
        if (currentUser != null) {
            try {
                dataManager.downvoteForumPost(post.getIdSanitized(), currentUser.getId());
                loadForumPosts();
                com.papel.imdb_clone.gui.Toast.show(forumRootStack, "Downvoted", com.papel.imdb_clone.gui.Toast.Type.INFO);
            } catch (Exception e) {
                logger.error("Forum downvote error", e);
                DialogUtils.showError("Downvote Error", "Failed to downvote post: " + e.getMessage());
            }
        }
    }

    private void showPostDetails(ForumPost post) {
        Dialog<ButtonType> dialog = new Dialog<>();
        dialog.setTitle(post.getTitle());

        User author = dataManager != null ? dataManager.findUserById(post.getUserId()) : null;
        HBox header = new HBox(10);
        header.setAlignment(Pos.CENTER_LEFT);
        Node avatar = createAvatarNode(author, 28);
        Label headerLabel = new Label("Posted by: " + (author != null ? author.getUsername() : "Unknown"));
        header.getChildren().addAll(avatar, headerLabel);
        dialog.setHeaderText(null);
        dialog.getDialogPane().setGraphic(header);

        VBox content = new VBox(12);
        content.setPadding(new Insets(10));

        TextArea postContent = new TextArea(post.getContent());
        postContent.setEditable(false);
        postContent.setWrapText(true);
        postContent.setPrefHeight(180);

        Label commentsTitle = new Label("Comments");
        commentsTitle.getStyleClass().add("section-title");

        ListView<ForumComment> commentsList = new ListView<>();
        commentsList.setPrefHeight(220);
        commentsList.setCellFactory(list -> new ListCell<>() {
            private final HBox row = new HBox(10);
            private final VBox textBox = new VBox(3);
            private final Label meta = new Label();
            private final Label body = new Label();

            {
                row.setAlignment(Pos.TOP_LEFT);
                body.setWrapText(true);
                meta.getStyleClass().add("comment-meta");
                body.getStyleClass().add("comment-body");
                textBox.getChildren().addAll(meta, body);
            }

            @Override
            protected void updateItem(ForumComment c, boolean empty) {
                super.updateItem(c, empty);
                if (empty || c == null) {
                    setGraphic(null);
                } else {
                    User u = dataManager != null ? dataManager.findUserById(c.getUserId()) : null;
                    Node av = createAvatarNode(u, 22);
                    String uname = u != null ? u.getUsername() : "Unknown";
                    meta.setText(uname + " â€¢ " + c.getCreatedAt().format(DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm")));
                    body.setText(c.getContent());
                    row.getChildren().setAll(av, textBox);
                    setGraphic(row);
                }
            }
        });

        Runnable reloadComments = () -> {
            List<ForumComment> list = dataManager != null ? dataManager.getCommentsForPost(post.getIdSanitized()) : List.of();
            commentsList.getItems().setAll(list);
        };
        reloadComments.run();

        TextArea addCommentArea = new TextArea();
        addCommentArea.setPromptText("Write a comment...");
        addCommentArea.setPrefRowCount(3);
        addCommentArea.setWrapText(true);

        Button addCommentBtn = new Button("Post Comment");
        addCommentBtn.getStyleClass().add("primary-button");
        addCommentBtn.setOnAction(e -> {
            if (currentUser == null || sessionToken == null) {
                DialogUtils.showWarning("Not Logged In", "Please log in to comment.");
                return;
            }
            String txt = addCommentArea.getText();
            if (txt == null || txt.trim().isEmpty()) {
                DialogUtils.showWarning("Empty Comment", "Please enter something to post.");
                return;
            }
            try {
                dataManager.addForumComment(post.getIdSanitized(), currentUser.getId(), txt.trim());
                addCommentArea.clear();
                reloadComments.run();
            } catch (Exception ex) {
                logger.error("Add comment error", ex);
                DialogUtils.showError("Comment Error", "Failed to add comment: " + ex.getMessage());
            }
        });

        HBox addBox = new HBox(10, addCommentArea, addCommentBtn);
        addBox.setAlignment(Pos.CENTER_RIGHT);
        HBox.setHgrow(addCommentArea, javafx.scene.layout.Priority.ALWAYS);

        content.getChildren().addAll(postContent, commentsTitle, commentsList, addBox);
        dialog.getDialogPane().setContent(content);
        dialog.getDialogPane().getButtonTypes().add(ButtonType.CLOSE);
        dialog.showAndWait();
    }

    private Node createAvatarNode(User user, int size) {
        HBox wrapper = new HBox();
        wrapper.setAlignment(Pos.CENTER);
        wrapper.getStyleClass().add("avatar");
        if (user != null && user.getAvatarUrl() != null && !user.getAvatarUrl().isBlank()) {
            try {
                Image img = new Image(user.getAvatarUrl(), size, size, true, true);
                ImageView iv = new ImageView(img);
                iv.setFitWidth(size);
                iv.setFitHeight(size);
                Circle clip = new Circle(size / 2.0, size / 2.0, size / 2.0);
                iv.setClip(clip);
                wrapper.getChildren().add(iv);
                return wrapper;
            } catch (Exception ignored) {
            }
        }
        // Fallback initials avatar
        Circle circle = new Circle(size / 2.0);
        circle.setFill(Color.web("#2a2f3a"));
        circle.setStroke(Color.web("#00e5ff"));
        circle.setStrokeWidth(1.2);
        Label initials = new Label(user != null ? user.getInitials() : "?");
        initials.getStyleClass().add("avatar-initials");
        StackPane sp = new StackPane(circle, initials);
        sp.setPrefSize(size, size);
        sp.setMaxSize(size, size);
        wrapper.getChildren().add(sp);
        return wrapper;
    }

    /**
     * Handles replying to a forum post.
     */
    private void handleReplyToPost(ForumPost post) {
        if (currentUser == null) {
            DialogUtils.showWarning("Not Logged In", "Please log in to reply to posts.");
            return;
        }

        try {
            TextInputDialog dialog = new TextInputDialog();
            dialog.setTitle("Reply to Post");
            dialog.setHeaderText("Replying to: " + post.getTitle());
            dialog.setContentText("Your reply:");

            // Make the input area larger for replies
            dialog.getDialogPane().setPrefWidth(500);
            TextArea textArea = new TextArea();
            textArea.setWrapText(true);
            textArea.setPrefRowCount(5);
            textArea.setPromptText("Enter your reply here...");
            dialog.getDialogPane().setExpandableContent(textArea);
            dialog.getDialogPane().setExpanded(true);

            Optional<String> result = dialog.showAndWait();
            result.ifPresent(replyContent -> {
                if (!replyContent.trim().isEmpty()) {
                    try {
                        // Create a new post as a reply (in a real system, this would be a threaded reply)
                        String replyTitle = "Re: " + post.getTitle();
                        ForumPost replyPost = new ForumPost(currentUser.getId(), replyTitle, replyContent);
                        dataManager.addForumPost(replyPost);
                        loadForumPosts();
                        DialogUtils.showSuccess("Success", "Reply posted successfully!");
                        com.papel.imdb_clone.gui.Toast.show(forumRootStack, "Reply posted", com.papel.imdb_clone.gui.Toast.Type.SUCCESS);
                        logger.info("Reply created for post: {}", post.getTitle());
                    } catch (Exception e) {
                        logger.error("Reply creation error", e);
                        DialogUtils.showError("Reply Error", "Failed to post reply: " + e.getMessage());
                    }
                } else {
                    DialogUtils.showWarning("Empty Reply", "Reply content cannot be empty.");
                }
            });
        } catch (Exception e) {
            logger.error("Reply dialog error", e);
            DialogUtils.showError("Dialog Error", "Failed to open reply dialog: " + e.getMessage());
        }
    }

    /**
     * Handles editing a forum post.
     */
    private void handleEditPost(ForumPost post) {
        if (currentUser == null || post.getUserId() != currentUser.getId()) {
            DialogUtils.showWarning("Access Denied", "You can only edit your own posts.");
            return;
        }

        try {
            Dialog<ButtonType> dialog = new Dialog<>();
            dialog.setTitle("Edit Post");
            dialog.setHeaderText("Edit your post");

            GridPane grid = new GridPane();
            grid.setHgap(10);
            grid.setVgap(10);
            grid.setPadding(new Insets(20, 150, 10, 10));

            TextField titleField = new TextField(post.getTitle());
