package com.papel.imdb_clone.service;

import com.papel.imdb_clone.data.RefactoredDataManager;
import com.papel.imdb_clone.model.*;

import java.util.ArrayList;
import java.util.List;
import java.util.stream.Collectors;

/**
 * Service class for managing user watchlists and favorites.
 */
public class WatchlistService {
    private final RefactoredDataManager dataManager;
    private final List<Watchlist> watchlists;
    private final List<UserRating> userRatings;
    private int nextWatchlistId = 1;
    private int nextRatingId = 1;

    public WatchlistService(RefactoredDataManager dataManager) {
        this.dataManager = dataManager;
        this.watchlists = new ArrayList<>();
        this.userRatings = new ArrayList<>();
    }

    // Watchlist Management
    public Watchlist createWatchlist(int userId, String name) {
        return createWatchlist(userId, name, null);
    }

    public Watchlist createWatchlist(int userId, String name, String description) {
        Watchlist watchlist = new Watchlist(userId, name, description);
        watchlist.setId(nextWatchlistId++);
        watchlists.add(watchlist);
        return watchlist;
    }

    public List<Watchlist> getUserWatchlists(int userId) {
        return watchlists.stream()
                .filter(w -> w.getUserId() == userId)
                .collect(Collectors.toList());
    }

    public List<Watchlist> getPublicWatchlists() {
        return watchlists.stream()
                .filter(Watchlist::isPublic)
                .collect(Collectors.toList());
    }

    public Watchlist getWatchlistById(int watchlistId) {
        return watchlists.stream()
                .filter(w -> w.getId() == watchlistId)
                .findFirst()
                .orElse(null);
    }

    public boolean deleteWatchlist(int watchlistId, int userId) {
        Watchlist watchlist = getWatchlistById(watchlistId);
        if (watchlist != null && watchlist.getUserId() == userId) {
            return watchlists.remove(watchlist);
        }
        return false;
    }

    public void addToWatchlist(int watchlistId, int contentId) {
        Watchlist watchlist = getWatchlistById(watchlistId);
        if (watchlist != null) {
            watchlist.addContent(contentId);
        }
    }

    public void removeFromWatchlist(int watchlistId, int contentId) {
        Watchlist watchlist = getWatchlistById(watchlistId);
        if (watchlist != null) {
            watchlist.removeContent(contentId);
        }
    }

    public List<Content> getWatchlistContent(int watchlistId) {
        Watchlist watchlist = getWatchlistById(watchlistId);
        if (watchlist == null) {
            return new ArrayList<>();
        }

        List<Content> content = new ArrayList<>();
        for (int contentId : watchlist.getContentIds()) {
            // Try to find movie first
            Movie movie = dataManager.getAllMovies().stream()
                    .filter(m -> m.getId() == contentId)
                    .findFirst()
                    .orElse(null);
            if (movie != null) {
                content.add(movie);
                continue;
            }

            // Try to find series
            Series series = dataManager.getAllSeries().stream()
                    .filter(s -> s.getId() == contentId)
                    .findFirst()
                    .orElse(null);
            if (series != null) {
                content.add(series);
            }
        }
        return content;
    }

    // Favorites (special watchlist)
    public Watchlist getFavoritesWatchlist(int userId) {
        return watchlists.stream()
                .filter(w -> w.getUserId() == userId && "Favorites".equals(w.getName()))
                .findFirst()
                .orElseGet(() -> createWatchlist(userId, "Favorites", "My favorite movies and series"));
    }

    public void addToFavorites(int userId, int contentId) {
        Watchlist favorites = getFavoritesWatchlist(userId);
        favorites.addContent(contentId);
    }

    public void removeFromFavorites(int userId, int contentId) {
        Watchlist favorites = getFavoritesWatchlist(userId);
        favorites.removeContent(contentId);
    }

    public boolean isFavorite(int userId, int contentId) {
        Watchlist favorites = getFavoritesWatchlist(userId);
        return favorites.containsContent(contentId);
    }

    // User Ratings Management
    public UserRating addRating(int userId, int contentId, double rating) {
        return addRating(userId, contentId, rating, null, null);
    }

    public UserRating addRating(int userId, int contentId, double rating, String review) {
        return addRating(userId, contentId, rating, null, review);
    }

    public UserRating addRating(int userId, int contentId, double rating, String title, String review) {
        // Remove existing rating if any
        removeRating(userId, contentId);

        UserRating userRating = new UserRating(userId, contentId, rating, title, review);
        userRating.setId(nextRatingId++);
        userRatings.add(userRating);
        return userRating;
    }

    public boolean removeRating(int userId, int contentId) {
        return userRatings.removeIf(r -> r.getUserId() == userId && r.getContentId() == contentId);
    }

    public UserRating getUserRating(int userId, int contentId) {
        return userRatings.stream()
                .filter(r -> r.getUserId() == userId && r.getContentId() == contentId)
                .findFirst()
                .orElse(null);
    }

    public List<UserRating> getContentRatings(int contentId) {
        return userRatings.stream()
                .filter(r -> r.getContentId() == contentId)
                .collect(Collectors.toList());
    }

    public List<UserRating> getUserRatings(int userId) {
        return userRatings.stream()
                .filter(r -> r.getUserId() == userId)
                .collect(Collectors.toList());
    }

    public double getAverageRating(int contentId) {
        List<UserRating> ratings = getContentRatings(contentId);
        if (ratings.isEmpty()) {
            return 0.0;
        }
        return ratings.stream()
                .mapToDouble(UserRating::getRating)
                .average()
                .orElse(0.0);
    }

    public int getRatingCount(int contentId) {
        return getContentRatings(contentId).size();
    }

    // Search and Discovery
    public List<Watchlist> searchWatchlists(String query) {
        if (query == null || query.trim().isEmpty()) {
            return getPublicWatchlists();
        }

        String lowerQuery = query.toLowerCase();
        return watchlists.stream()
                .filter(w -> w.isPublic() && 
                    (w.getName().toLowerCase().contains(lowerQuery) ||
                     (w.getDescription() != null && w.getDescription().toLowerCase().contains(lowerQuery))))
                .collect(Collectors.toList());
    }

    public List<UserRating> getTopRatedContent(int limit) {
        return userRatings.stream()
                .collect(Collectors.groupingBy(UserRating::getContentId))
                .entrySet().stream()
                .map(entry -> {
                    List<UserRating> ratings = entry.getValue();
                    double avgRating = ratings.stream()
                            .mapToDouble(UserRating::getRating)
                            .average()
                            .orElse(0.0);
                    UserRating representative = ratings.get(0);
                    representative.setRating(avgRating);
                    return representative;
                })
                .sorted((a, b) -> Double.compare(b.getRating(), a.getRating()))
                .limit(limit)
                .collect(Collectors.toList());
    }

    public List<UserRating> getRecentReviews(int limit) {
        return userRatings.stream()
                .filter(UserRating::hasReview)
                .sorted((a, b) -> b.getCreatedAt().compareTo(a.getCreatedAt()))
                .limit(limit)
                .collect(Collectors.toList());
    }

    // Statistics
    public int getTotalWatchlists() {
        return watchlists.size();
    }

    public int getTotalRatings() {
        return userRatings.size();
    }

    public int getUserWatchlistCount(int userId) {
        return (int) watchlists.stream()
                .filter(w -> w.getUserId() == userId)
                .count();
    }

    public int getUserRatingCount(int userId) {
        return (int) userRatings.stream()
                .filter(r -> r.getUserId() == userId)
                .count();
    }
}

