package com.papel.imdb_clone.util;

import javafx.scene.Scene;

/**
 * Central manager for applying base and theme styles to the application's Scene.
 * Reads preferences from SettingsManager and applies stylesheets accordingly.
 */
public class ThemeManager {
    private static final ThemeManager INSTANCE = new ThemeManager();

    private static final String BASE_CSS = "/css/modern-styles.css"; // single global stylesheet

    private ThemeManager() { }

    public static ThemeManager getInstance() {
        return INSTANCE;
    }

    public void applyTheme(Scene scene) {
        if (scene == null) return;
        // Remove previous theme sheets
        scene.getStylesheets().removeIf(s -> s.contains("/css/theme-") || s.contains("/css/modern-styles.css") || s.contains("/css/improved-styles.css"));

        // Apply base stylesheet
        try {
            scene.getStylesheets().add(getClass().getResource(BASE_CSS).toExternalForm());
        } catch (Exception ignored) { }

        // Font size
        if (scene.getRoot() != null) {
            SettingsManager settings = SettingsManager.getInstance();
            int fontSize = settings.getFontSize();
            scene.getRoot().setStyle("-fx-font-size: " + fontSize + "px;");
        }

        // Publish theme-changed event for interested parties
        AppEventBus.getInstance().publish("THEME_CHANGED", "single-css");
    }
}

