package com.papel.imdb_clone.data;

import com.papel.imdb_clone.enums.Genre;
import com.papel.imdb_clone.exceptions.DuplicateContentException;
import com.papel.imdb_clone.exceptions.DuplicateUsernameException;
import com.papel.imdb_clone.exceptions.FileParsingException;
import com.papel.imdb_clone.model.*;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.io.BufferedReader;
import java.io.FileReader;
import java.io.IOException;
import java.time.LocalDate;
import java.time.LocalDateTime;
import java.time.format.DateTimeFormatter;
import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Objects;
import java.util.concurrent.CopyOnWriteArrayList;
import java.util.concurrent.atomic.AtomicInteger;
import java.util.concurrent.locks.ReentrantReadWriteLock;
import java.util.stream.Collectors;

public class DataManager {
    private final List<User> users;
    private final List<Actor> actors;
    private final List<Director> directors;
    private final List<Movie> movies;
    private final List<Series> series;
    private final List<ForumPost> forumPosts;

    // Atomic counters for thread-safe ID generation
    private final AtomicInteger nextUserId = new AtomicInteger(1);
    private final AtomicInteger nextActorId = new AtomicInteger(1);
    private final AtomicInteger nextDirectorId = new AtomicInteger(1);
    private final AtomicInteger nextMovieId = new AtomicInteger(1);
    private final AtomicInteger nextSeriesId = new AtomicInteger(1);
    private final AtomicInteger nextSeasonId = new AtomicInteger(1);
    private final AtomicInteger nextEpisodeId = new AtomicInteger(1);
    private final AtomicInteger nextForumPostId = new AtomicInteger(1);
    // Locks for thread-safe operations
    private final ReentrantReadWriteLock forumPostLock = new ReentrantReadWriteLock();
    private final ReentrantReadWriteLock movieLock = new ReentrantReadWriteLock();
    private final ReentrantReadWriteLock seriesLock = new ReentrantReadWriteLock();
    private final ReentrantReadWriteLock userLock = new ReentrantReadWriteLock();
    private final ReentrantReadWriteLock actorLock = new ReentrantReadWriteLock();
    private final ReentrantReadWriteLock directorLock = new ReentrantReadWriteLock();

    private static final Logger logger = LoggerFactory.getLogger(DataManager.class.getName());

    // We don't need separate allMovies and allSeries fields since we already have movies and series lists

    /**
     * Initializes a new instance of the DataManager class.
     * This constructor sets up empty lists for users, actors, directors, movies, series, and forum posts.
     */
    public DataManager() {
        // Using CopyOnWriteArrayList for thread-safe iteration
        this.users = new CopyOnWriteArrayList<>();
        this.actors = new CopyOnWriteArrayList<>();
        this.directors = new CopyOnWriteArrayList<>();
        this.movies = new CopyOnWriteArrayList<>();
        this.series = new CopyOnWriteArrayList<>();
        this.forumPosts = new CopyOnWriteArrayList<>();
    }


    public void loadAllData() {
        loadUsers("users.txt");
        loadActors("actors.txt");
        loadDirectors("directors.txt");
        loadMovies("movies.txt");
        loadSeries("series.txt");
        loadAwardsAndBoxOffice("awards_boxoffice.txt");
        loadForumPosts("forum_posts.txt");
    }

    public void loadUsers(String filename) {
        try (BufferedReader reader = new BufferedReader(new FileReader("src/main/resources/data/" + filename))) {
            String line;
            while ((line = reader.readLine()) != null) {
                String[] parts = parseCSVLine(line);
                if (parts.length >= 6) {

                    // Format: firstName,middleName/nickname,lastName,gender,nationality,username
                    String firstName = parts[0];
                    String middleName = parts[1].replaceAll("\"", ""); // Remove quotes
                    String lastName = parts[2];
                    char gender = parts[3].charAt(0);
                    String nationality = parts[4];
                    String username = parts[5];

                    // Combine names appropriately
                    String fullFirstName = middleName.isEmpty() ? firstName : firstName + " " + middleName;
                    User user = new User(fullFirstName, lastName, username, gender, nationality);
                    user.setId(nextUserId.getAndIncrement());
                    users.add(user);
                } else if (parts.length >= 5) {
                    // Standard format: firstName,lastName,username,gender,nationality
                    User user = new User(parts[0], parts[1], parts[2], parts[3].charAt(0), parts[4]);
                    user.setId(nextUserId.getAndIncrement());
                    users.add(user);
                }
            }
        } catch (IOException e) {
            throw new FileParsingException("Error loading users: " + e.getMessage());
        }
    }

    public void loadActors(String filename) {
        try (BufferedReader reader = new BufferedReader(new FileReader("src/main/resources/data/" + filename))) {
            String line;
            while ((line = reader.readLine()) != null) {
                String[] parts = parseCSVLine(line);
                if (parts.length >= 6) {
                    // Handle the case where middle name/nickname is quoted
                    // Format: firstName,middleName/nickname,lastName,birthDate,gender,nationality
                    String firstName = parts[0];
                    String middleName = parts[1].replaceAll("\"", ""); // Remove quotes
                    String lastName = parts[2];
                    LocalDate birthDate = LocalDate.parse(parts[3], DateTimeFormatter.ISO_LOCAL_DATE);
                    char gender = parts[4].charAt(0);
                    String nationality = parts[5];

                    // Combine names appropriately
                    String fullFirstName = middleName.isEmpty() ? firstName : firstName + " " + middleName;
                    Actor actor = new Actor(fullFirstName, lastName, birthDate, gender, nationality);
                    actor.setId(nextActorId.getAndIncrement());
                    actors.add(actor);
                } else if (parts.length >= 5) {
                    // Standard format: firstName,lastName,birthDate,gender,nationality
                    LocalDate birthDate = LocalDate.parse(parts[2], DateTimeFormatter.ISO_LOCAL_DATE);
                    Actor actor = new Actor(parts[0], parts[1], birthDate, parts[3].charAt(0), parts[4]);
                    actor.setId(nextActorId.getAndIncrement());
                    actors.add(actor);
                }
            }
        } catch (IOException e) {
            throw new FileParsingException("Error loading actors: " + e.getMessage());
        }
    }

    public void loadDirectors(String filename) {
        try (BufferedReader reader = new BufferedReader(new FileReader("src/main/resources/data/" + filename))) {
            String line;
            while ((line = reader.readLine()) != null) {
                String[] parts = parseCSVLine(line);
                if (parts.length >= 5) {
                    // Handle the case where middle name/nickname is quoted
                    // Format: firstName,middleName/nickname,lastName,birthDate,gender
                    String firstName = parts[0];
                    String middleName = parts[1].replaceAll("\"", ""); // Remove quotes
                    String lastName = parts[2];
                    LocalDate birthDate = LocalDate.parse(parts[3], DateTimeFormatter.ISO_LOCAL_DATE);
                    char gender = parts[4].charAt(0);

                    // Combine names appropriately
                    String fullFirstName = middleName.isEmpty() ? firstName : firstName + " " + middleName;
                    Director director = new Director(fullFirstName, lastName, birthDate, gender);
                    director.setId(nextDirectorId.getAndIncrement());
                    directors.add(director);
                } else if (parts.length >= 4) {
                    // Standard format: firstName,lastName,birthDate,gender
                    LocalDate birthDate = LocalDate.parse(parts[2], DateTimeFormatter.ISO_LOCAL_DATE);
                    Director director = new Director(parts[0], parts[1], birthDate, parts[3].charAt(0));
                    director.setId(nextDirectorId.getAndIncrement());
                    directors.add(director);
                }
            }
        } catch (IOException e) {
            throw new FileParsingException("Error loading directors: " + e.getMessage());
        }
    }

    public void loadMovies(String filename) {
        try (BufferedReader reader = new BufferedReader(new FileReader("src/main/resources/data/" + filename))) {
            String line;
            while ((line = reader.readLine()) != null) {
                String[] parts = parseCSVLine(line);
                if (parts.length >= 6) {
                    String title = parts[0];
                    int year = Integer.parseInt(parts[1]);
                    Genre genre = Genre.valueOf(parts[2].toUpperCase().replace(" ", "_"));
                    int duration = Integer.parseInt(parts[3]);
                    Director director = findDirectorByName(parts[4]);
                    double imdbRating = Double.parseDouble(parts[5]);

                    List<Actor> movieActors = new ArrayList<>();
                    if (parts.length > 6) {
                        String[] actorNames = parts[6].split(";");
                        for (String actorName : actorNames) {
                            Actor actor = findActorByName(actorName.trim());
                            if (actor != null) {
                                movieActors.add(actor);
                            }
                        }
                    }

                    // Handle case where director might be null
                    Movie movie;
                    if (director != null) {
                        movie = new Movie(title, year, director, null, duration, movieActors, imdbRating);
                    } else {
                        // Create a movie with genre and director name from the file when director object is not found
                        movie = new Movie(title, year, genre.name(), parts[4], new HashMap<>(), imdbRating);
                        movie.setActors(movieActors);
                    }
                    movie.setId(nextMovieId.getAndIncrement());
                    movies.add(movie);
                }
            }
        } catch (IOException e) {
            throw new FileParsingException("Error loading movies: " + e.getMessage());
        }
    }

    public void loadSeries(String filename) {
        try (BufferedReader reader = new BufferedReader(new FileReader("src/main/resources/data/" + filename))) {
            String line;
            while ((line = reader.readLine()) != null) {
                String[] parts = parseCSVLine(line);
                if (parts.length >= 2) {
                    String title = parts[0];
                    // Split the genre string on pipe and take the first part
                    String genreStr = parts[1].split("\\|")[0].trim();
                    Genre genre = Genre.valueOf(genreStr.toUpperCase().replace(" ", "_"));

                    Series series = new Series(title);
                    series.setGenre(genre);  // Set the genre from the file
                    series.setId(nextSeriesId.getAndIncrement());
                    this.series.add(series);
                }
            }
        } catch (IOException e) {
            throw new FileParsingException("Error loading series: " + e.getMessage());
        }
    }

    public void loadAwardsAndBoxOffice(String filename) {
        try (BufferedReader reader = new BufferedReader(new FileReader("src/main/resources/data/" + filename))) {
            String line;
            while ((line = reader.readLine()) != null) {
                // Skip comments and empty lines
                if (line.trim().isEmpty() || line.startsWith("#")) {
                    continue;
                }

                String[] parts = line.split("\\|");
                if (parts.length >= 4) {
                    String type = parts[0].trim();
                    String title = parts[1].trim();
                    String boxOffice = parts[2].trim();
                    String awardsStr = parts[3].trim();

                    // Parse awards
                    List<String> awards = new ArrayList<>();
                    if (!awardsStr.isEmpty()) {
                        String[] awardArray = awardsStr.split(";");
                        for (String award : awardArray) {
                            awards.add(award.trim());
                        }
                    }

                    // Apply to movies or series
                    if ("MOVIE".equals(type)) {
                        Movie movie = findMovieByTitle(title);
                        if (movie != null) {
                            movie.setBoxOffice(boxOffice);
                            movie.setAwards(awards);
                        }
                    } else if ("SERIES".equals(type)) {
                        Series series = findSeriesByTitle(title);
                        if (series != null) {
                            series.setBoxOffice(boxOffice);
                            series.setAwards(awards);
                        }
                    }
                }
            }
        } catch (IOException e) {
            throw new FileParsingException("Error loading awards and box office data: " + e.getMessage());
        }
    }

    public void loadForumPosts(String filename) {
        try (BufferedReader reader = new BufferedReader(new FileReader("src/main/resources/data/" + filename))) {
            String line;
            while ((line = reader.readLine()) != null) {
                // Skip empty lines
                if (line.trim().isEmpty()) {
                    continue;
                }

                String[] parts = parseCSVLine(line);
                if (parts.length >= 8) {
                    // Format: id,userId,title,content,createdAt,updatedAt,upvotes,downvotes
                    int id = Integer.parseInt(parts[0]);
                    int userId = Integer.parseInt(parts[1]);
                    String title = parts[2];
                    String content = parts[3];
                    LocalDateTime createdAt = LocalDateTime.parse(parts[4]);
                    LocalDateTime updatedAt = LocalDateTime.parse(parts[5]);
                    int upvotes = Integer.parseInt(parts[6]);
                    int downvotes = Integer.parseInt(parts[7]);

                    ForumPost post = new ForumPost(userId, title, content);
                    post.setId(id); // Use the standard ID setter
                    post.setCreatedAt(createdAt);
                    post.setUpdatedAt(updatedAt);
                    post.setUpvotes(upvotes);
                    post.setDownvotes(downvotes);

                    // Update the next ID counter to avoid conflicts
                    // Using getAndUpdate to ensure thread-safe update of the counter
                    nextForumPostId.getAndUpdate(current -> Math.max(current, id + 1));

                    forumPosts.add(post);
                }
            }
        } catch (IOException e) {
            throw new FileParsingException("Error loading forum posts: " + e.getMessage());
        }
    }

    /**
     * Saves all forum posts to a file in a thread-safe manner.
     * @param filename The name of the file to save to (will be saved in src/main/resources/data/)
     * @throws FileParsingException if there's an error writing to the file
     */
    public void saveForumPosts(String filename) {
        forumPostLock.readLock().lock();
        try {
            try (java.io.PrintWriter writer = new java.io.PrintWriter("src/main/resources/data/" + filename)) {
                for (ForumPost post : forumPosts) {
                    writer.println(String.format("%d,%d,\"%s\",\"%s\",%s,%s,%d,%d",
                            post.getId(),
                            post.getUserId(),
                            escapeCsv(post.getTitle()),
                            escapeCsv(post.getContent()),
                            post.getCreatedAt(),
                            post.getUpdatedAt(),
                            post.getUpvotes(),
                            post.getDownvotes()));
                }
                logger.info("Successfully saved {} forum posts to {}", forumPosts.size(), filename);
            } catch (IOException e) {
                logger.error("Error saving forum posts to {}: {}", filename, e.getMessage(), e);
                throw new FileParsingException("Error saving forum posts: " + e.getMessage());
            }
        } finally {
            forumPostLock.readLock().unlock();
        }
    }

    private String escapeCsv(String input) {
        if (input == null) {
            return "";
        }
        return input.replace("\"", "\"\"");
    }

    /**
     * Adds a new user to the system in a thread-safe manner.
     * @param user The user to add
     * @throws DuplicateUsernameException if a user with the same username already exists
     */
    public void addUser(User user) {
        if (user == null) {
            logger.warn("Attempted to add a null user");
            return;
        }

        userLock.writeLock().lock();
        try {
            // Check if the username already exists
            if (users.stream().anyMatch(u -> u.getUsername().equals(user.getUsername()))) {
                throw new DuplicateUsernameException("Username already exists: " + user.getUsername());
            }
            user.setId(nextUserId.getAndIncrement());
            users.add(user);
            logger.info("Added new user: {}", user.getUsername());
        } finally {
            userLock.writeLock().unlock();
        }
    }

    /**
     * Adds a new actor to the system in a thread-safe manner.
     * @param actor The actor to add
     */
    public void addActor(Actor actor) {
        if (actor == null) {
            logger.warn("Attempted to add a null actor");
            return;
        }

        actorLock.writeLock().lock();
        try {
            actor.setId(nextActorId.getAndIncrement());
            actors.add(actor);
            logger.debug("Added new actor: {}", actor.getFullName());
        } finally {
            actorLock.writeLock().unlock();
        }
    }

    /**
     * Adds a new director to the system in a thread-safe manner.
     * @param director The director to add
     */
    public void addDirector(Director director) {
        if (director == null) {
            logger.warn("Attempted to add a null director");
            return;
        }

        directorLock.writeLock().lock();
        try {
            director.setId(nextDirectorId.getAndIncrement());
            directors.add(director);
            logger.debug("Added new director: {}", director.getFullName());
        } finally {
            directorLock.writeLock().unlock();
        }
    }

    /**
     * Adds a new movie to the system in a thread-safe manner.
     * @param movie The movie to add
     * @throws DuplicateContentException if a movie with the same title already exists
     */
    public void addMovie(Movie movie) {
        if (movie == null) {
            logger.warn("Attempted to add a null movie");
            return;
        }

        movieLock.writeLock().lock();
        try {
            if (findMovieByTitle(movie.getTitle()) != null) {
                throw new DuplicateContentException("Movie already exists: " + movie.getTitle());
            }
            movie.setId(nextMovieId.getAndIncrement());
            movies.add(movie);
            logger.info("Added new movie: {}", movie.getTitle());
        } finally {
            movieLock.writeLock().unlock();
        }
    }

    /**
     * Adds a new series to the system in a thread-safe manner.
     * @param series The series to add
     * @throws DuplicateContentException if a series with the same title already exists
     */
    public void addSeries(Series series) {
        if (series == null) {
            logger.warn("Attempted to add a null series");
            return;
        }

        seriesLock.writeLock().lock();
        try {
            if (findSeriesByTitle(series.getTitle()) != null) {
                throw new DuplicateContentException("Series already exists: " + series.getTitle());
            }
            series.setId(nextSeriesId.getAndIncrement());
            this.series.add(series);
            logger.info("Added new series: {}", series.getTitle());
        } finally {
            seriesLock.writeLock().unlock();
        }
    }

    /**
     * Removes a movie from the system in a thread-safe manner.
     * @param movie The movie to remove
     * @throws IllegalArgumentException if the movie is null or not found
     */
    public void removeMovie(Movie movie) {
        if (movie == null) {
            throw new IllegalArgumentException("Movie cannot be null");
        }

        movieLock.writeLock().lock();
        try {
            boolean removed = movies.remove(movie);
            if (!removed) {
                throw new IllegalArgumentException("Movie not found in collection: " + movie.getTitle());
            }
            logger.info("Removed movie: {}", movie.getTitle());
        } finally {
            movieLock.writeLock().unlock();
        }
    }

    /**
     * Removes a series from the system in a thread-safe manner.
     * @param series The series to remove
     * @throws IllegalArgumentException if the series is null or not found
     */
    public void removeSeries(Series series) {
        if (series == null) {
            throw new IllegalArgumentException("Series cannot be null");
        }

        seriesLock.writeLock().lock();
        try {
            boolean removed = this.series.remove(series);
            if (!removed) {
                throw new IllegalArgumentException("Series not found in collection: " + series.getTitle());
            }
            logger.info("Removed series: {}", series.getTitle());
        } finally {
            seriesLock.writeLock().unlock();
        }
    }

    /**
     * Retrieves a user by username in a thread-safe manner.
     * @param username The username to search for
     * @return The User object if found, null otherwise
     */
    public User getUserByUsername(String username) {
        if (username == null || username.trim().isEmpty()) {
            logger.warn("Attempted to search for user with null or empty username");
            return null;
        }

        userLock.readLock().lock();
        try {
            return users.stream()
                    .filter(user -> user.getUsername().equals(username))
                    .findFirst()
                    .orElse(null);
        } finally {
            userLock.readLock().unlock();
        }
    }

    /**
     * Finds an actor by their full name in a thread-safe manner.
     * @param name The full name of the actor to find
     * @return The Actor object if found, null otherwise
     */
    public Actor findActorByName(String name) {
        if (name == null || name.trim().isEmpty()) {
            logger.warn("Attempted to find actor with null or empty name");
            return null;
        }

        actorLock.readLock().lock();
        try {
            return actors.stream()
                    .filter(actor -> actor.getFullName().equals(name))
                    .findFirst()
                    .orElse(null);
        } finally {
            actorLock.readLock().unlock();
        }
    }

    /**
     * Finds a director by their full name in a thread-safe manner.
     * @param name The full name of the director to find
     * @return The Director object if found, null otherwise
     */
    public Director findDirectorByName(String name) {
        if (name == null || name.trim().isEmpty()) {
            logger.warn("Attempted to find director with null or empty name");
            return null;
        }

        directorLock.readLock().lock();
        try {
            return directors.stream()
                    .filter(director -> director.getFullName().equals(name))
                    .findFirst()
                    .orElse(null);
        } finally {
            directorLock.readLock().unlock();
        }
    }

    public Movie findMovieByTitle(String title) {
        movieLock.readLock().lock();
        try {
            return movies.stream()
                    .filter(movie -> movie.getTitle().equals(title))
                    .findFirst()
                    .orElse(null);
        } finally {
            movieLock.readLock().unlock();
        }
    }

    /**
     * Finds a series by its title in a thread-safe manner.
     * @param title The title of the series to find
     * @return The Series object if found, null otherwise
     */
    public Series findSeriesByTitle(String title) {
    seriesLock.readLock().lock();
    try {
        return series.stream()
                .filter(series -> series.getTitle().equals(title))
                .findFirst()
                .orElse(null);
    } finally {
        seriesLock.readLock().unlock();
    }
}

    /**
     * Finds a user by their ID in a thread-safe manner.
     * @param id The ID of the user to find
     * @return The User object if found, null otherwise
     */
    public User findUserById(int id) {
        userLock.readLock().lock();
        try {
            return users.stream()
                    .filter(user -> user.getId() == id)
                    .findFirst()
                    .orElse(null);
        } finally {
            userLock.readLock().unlock();
        }
    }

/**
 * Returns a thread-safe copy of all users.
 * @return A new list containing all users
 */
public List<User> getUsers() {
    userLock.readLock().lock();
    try {
        return new ArrayList<>(users);
    } finally {
        userLock.readLock().unlock();
    }
}

/**
 * Returns a thread-safe copy of all actors.
 * @return A new list containing all actors
