    /**
     * Returns a thread-safe copy of all forum posts.
     * @return A new list containing all forum posts
     */
    public List<ForumPost> getForumPosts() {
        forumPostLock.readLock().lock();
        try {
            return new ArrayList<>(forumPosts);
        } finally {
            forumPostLock.readLock().unlock();
        }
    }

    /**
     * @deprecated Use getForumPosts() instead. This method is kept for backward compatibility.
     * @return A new list containing all forum posts
     */
    @Deprecated
    public List<ForumPost> getAllForumPosts() {
        return getForumPosts();
    }

public int getNextSeasonId() {
    return nextSeasonId.get();
}

public void setNextSeasonId(int nextSeasonId) {
    this.nextSeasonId.set(nextSeasonId);
}

public int getNextEpisodeId() {
    return nextEpisodeId.get();
}

public void setNextEpisodeId(int nextEpisodeId) {
    this.nextEpisodeId.set(nextEpisodeId);
}

public List<Movie> searchMovies(SearchCriteria criteria) {
        List<Movie> result = movies.stream()
                .filter(movie -> matchesCriteria(movie, criteria))
                .toList();

        // Convert to ArrayList since toList() returns an unmodifiable list
        List<Movie> modifiableResult = new ArrayList<>(result);

        // Apply sorting if criteria has sorting parameters
        if (criteria.getSortBy() != null && !criteria.getSortBy().isEmpty()) {
            sortMovies(modifiableResult, criteria);
        }

        return modifiableResult;
    }


    /**
     * Replaces all movies with the provided list in a thread-safe manner.
     * @param newMovies The new list of movies to set
     * @throws IllegalArgumentException if newMovies is null
     */
    public void setAllMovies(List<Movie> newMovies) {
        if (newMovies == null) {
            throw new IllegalArgumentException("Movie list cannot be null");
        }

        movieLock.writeLock().lock();
        try {
            this.movies.clear();
            this.movies.addAll(newMovies);
            logger.info("Replaced all movies. New count: {}", newMovies.size());
        } finally {
            movieLock.writeLock().unlock();
        }
    }

    /**
     * Rates a movie in a thread-safe manner.
     * @param movie The movie to rate
     * @param rating The rating value (must be between 0.0 and 10.0)
     * @throws IllegalArgumentException if movie is null or rating is out of range
     */
    /**
     * Rates a movie in a thread-safe manner.
     * @param movie The movie to rate
     * @param rating The rating value (must be between 0.0 and 10.0)
     * @throws IllegalArgumentException if movie is null or rating is out of range
     */
    public void rateMovie(Movie movie, double rating) {
        if (movie == null) {
            throw new IllegalArgumentException("Movie cannot be null");
        }
        if (rating < 0.0 || rating > 10.0) {
            throw new IllegalArgumentException("Rating must be between 0.0 and 10.0");
        }

        movieLock.writeLock().lock();
        try {
            // Find the movie in our collection
            Movie existingMovie = findMovieByTitle(movie.getTitle());
            if (existingMovie != null) {
                // For simplicity, we'll use a fixed user ID (1) for now
                // In a real application, this would be the current user's ID
                int userId = 1;
                existingMovie.addUserRating(userId, (int) Math.round(rating));
                logger.debug("User {} rated movie {}: {}", userId, existingMovie.getTitle(), rating);
            } else {
                logger.warn("Attempted to rate non-existent movie: {}", movie.getTitle());
            }
        } finally {
            movieLock.writeLock().unlock();
        }
    }


    /**
     * Replaces all series with the provided list in a thread-safe manner.
     * @param newSeries The new list of series to set
     * @throws IllegalArgumentException if newSeries is null
     */
    public void setAllSeries(List<Series> newSeries) {
        if (newSeries == null) {
            throw new IllegalArgumentException("Series list cannot be null");
        }

        seriesLock.writeLock().lock();
        try {
            this.series.clear();
            this.series.addAll(newSeries);
            logger.info("Replaced all series. New count: {}", newSeries.size());
        } finally {
            seriesLock.writeLock().unlock();
        }
    }


    /**
     * Adds a new forum post in a thread-safe manner.
     * @param post The forum post to add
     * @throws IllegalArgumentException if post is null, or if title/content is empty
     */
    public void addForumPost(ForumPost post) {
        if (post == null) {
            throw new IllegalArgumentException("Forum post cannot be null");
        }
        if (post.getTitle() == null || post.getTitle().trim().isEmpty()) {
            throw new IllegalArgumentException("Forum post title cannot be empty");
        }
        if (post.getContent() == null || post.getContent().trim().isEmpty()) {
            throw new IllegalArgumentException("Forum post content cannot be empty");
        }

        forumPostLock.writeLock().lock();
        try {
            // Ensure ID is not already in use
            int newId = nextForumPostId.getAndIncrement();
            while (findForumPostByIdInternal(newId) != null) {
                newId = nextForumPostId.getAndIncrement();
            }
            post.setId(newId);

            // Set timestamps if not already set
            LocalDateTime now = LocalDateTime.now();
            if (post.getCreatedAt() == null) {
                post.setCreatedAt(now);
            }
            if (post.getUpdatedAt() == null) {
                post.setUpdatedAt(post.getCreatedAt());
            }

            forumPosts.add(post);
            logger.debug("Added new forum post with ID: {}", newId);
        } finally {
            forumPostLock.writeLock().unlock();
        }
    }

    /**
     * Downvotes a forum post in a thread-safe manner.
     * @param postId The ID of the forum post to downvote
     * @param userId The ID of the user who is downvoting
     * @throws IllegalArgumentException if postId or userId is invalid
     */
    public void downvoteForumPost(int postId, int userId) {
        if (postId <= 0 || userId <= 0) {
            throw new IllegalArgumentException("Invalid post ID or user ID");
        }

        forumPostLock.writeLock().lock();
        try {
            ForumPost post = findForumPostById(postId);
            if (post != null) {
                post.downvote(userId);
                post.setUpdatedAt(LocalDateTime.now());
                logger.debug("User {} downvoted post {}", userId, postId);
            } else {
                logger.warn("Attempted to downvote non-existent post: {}", postId);
            }
        } finally {
            forumPostLock.writeLock().unlock();
        }
    }

    /**
     * Finds a forum post by its ID (internal method without locking).
     * @param id The ID of the forum post to find
     * @return The ForumPost object if found, null otherwise
     */
    private ForumPost findForumPostByIdInternal(int id) {
        return forumPosts.stream()
                .filter(post -> post.getId() == id)
                .findFirst()
                .orElse(null);
    }

    /**
     * Finds a forum post by its ID in a thread-safe manner.
     * @param id The ID of the forum post to find
     * @return The ForumPost object if found, null otherwise
     */
    public ForumPost findForumPostById(int id) {
        forumPostLock.readLock().lock();
        try {
            return findForumPostByIdInternal(id);
        } finally {
            forumPostLock.readLock().unlock();
        }
    }

    /**
     * Checks if a movie matches the given search criteria.
     * @param movie The movie to check
     * @param criteria The search criteria to match against
     * @return true if the movie matches the criteria, false otherwise
     */
    private boolean matchesCriteria(Movie movie, SearchCriteria criteria) {
        // Title filter
        if (criteria.getTitle() != null && !criteria.getTitle().trim().isEmpty()) {
            if (!movie.getTitle().toLowerCase().contains(criteria.getTitle().toLowerCase())) {
                return false;
            }
        }

        // Actor filter
        if (criteria.getActorName() != null && !criteria.getActorName().trim().isEmpty()) {
            boolean hasActor = movie.getActors().stream()
                    .anyMatch(actor -> actor.getFullName().toLowerCase().contains(criteria.getActorName().toLowerCase()));
            if (!hasActor) {
                return false;
            }
        }

        // Director filter
        if (criteria.getDirectorName() != null && !criteria.getDirectorName().trim().isEmpty()) {
            if (movie.getDirector() == null || 
                !movie.getDirector().toLowerCase().contains(criteria.getDirectorName().toLowerCase())) {
                return false;
            }
        }

        // Year filter
        int movieYear = movie.getYear().getYear() + 1900; // Convert Date to year
        if (criteria.getMinYear() != null && movieYear < criteria.getMinYear()) {
            return false;
        }
        if (criteria.getMaxYear() != null && movieYear > criteria.getMaxYear()) {
            return false;
        }

        // Duration filter
        if (criteria.getMinDuration() != null && movie.getDuration() < criteria.getMinDuration()) {
            return false;
        }
        if (criteria.getMaxDuration() != null && movie.getDuration() > criteria.getMaxDuration()) {
            return false;
        }

        // IMDB rating filter
        if (movie.getImdbRating() < criteria.getMinImdbRating() || 
            movie.getImdbRating() > criteria.getMaxImdbRating()) {
            return false;
        }

        // User rating filter
        double avgUserRating = movie.calculateAverageUserRating();
        if (avgUserRating < criteria.getMinUserRating() || 
            avgUserRating > criteria.getMaxUserRating()) {
            return false;
        }

        // Genre filter
        if (criteria.hasGenreFilter()) {
            if (movie.getGenre() == null || !criteria.getGenres().contains(movie.getGenre())) {
                return false;
            }
        }

        return true;
    }

    /**
     * Sorts a list of movies based on the given criteria.
     * @param movies The list of movies to sort
     * @param criteria The search criteria containing sorting parameters
     */
    private void sortMovies(List<Movie> movies, SearchCriteria criteria) {
        if (criteria.getSortBy() == null || criteria.getSortBy().trim().isEmpty()) {
            return;
        }

        switch (criteria.getSortBy().toLowerCase()) {
            case "title":
                movies.sort((m1, m2) -> criteria.isSortDescending() ? 
                    m2.getTitle().compareToIgnoreCase(m1.getTitle()) : 
                    m1.getTitle().compareToIgnoreCase(m2.getTitle()));
                break;
            case "year":
                movies.sort((m1, m2) -> criteria.isSortDescending() ? 
                    Integer.compare(m2.getYear().getYear() + 1900, m1.getYear().getYear() + 1900) : 
                    Integer.compare(m1.getYear().getYear() + 1900, m2.getYear().getYear() + 1900));
                break;
            case "rating":
                movies.sort((m1, m2) -> criteria.isSortDescending() ? 
                    Double.compare(m2.getImdbRating(), m1.getImdbRating()) : 
                    Double.compare(m1.getImdbRating(), m2.getImdbRating()));
                break;
            case "duration":
                movies.sort((m1, m2) -> criteria.isSortDescending() ? 
                    Integer.compare(m2.getDuration(), m1.getDuration()) : 
                    Integer.compare(m1.getDuration(), m2.getDuration()));
                break;
            default:
                logger.warn("Unknown sort criteria: {}", criteria.getSortBy());
                break;
        }
    }

    /**
     * Parses a CSV line handling quoted fields properly
     *
     * @param line the CSV line to parse
     * @return array of parsed fields
     */
    private String[] parseCSVLine(String line) {
        List<String> fields = new ArrayList<>();
        StringBuilder currentField = new StringBuilder();
        boolean inQuotes = false;

        for (int i = 0; i < line.length(); i++) {
            char c = line.charAt(i);

            if (c == '"') {
                inQuotes = !inQuotes;
                currentField.append(c); // Keep the quotes for now, we'll remove them later
            } else if (c == ',' && !inQuotes) {
                fields.add(currentField.toString().trim());
                currentField = new StringBuilder();
            } else {
                currentField.append(c);
            }
        }

        // Add the last field
        fields.add(currentField.toString().trim());

        return fields.toArray(new String[0]);
    }

