    public void loadMovies(String filename) {
        try (BufferedReader reader = new BufferedReader(new FileReader("src/main/resources/data/" + filename))) {
            String line;
            while ((line = reader.readLine()) != null) {
                String[] parts = parseCSVLine(line);
                if (parts.length >= 6) {
                    String title = parts[0];
                    int year = Integer.parseInt(parts[1]);
                    Genre genre = Genre.valueOf(parts[2].toUpperCase().replace(" ", "_"));
                    int duration = Integer.parseInt(parts[3]);
                    Director director = findDirectorByName(parts[4]);
                    double imdbRating = Double.parseDouble(parts[5]);

                    List<Actor> movieActors = new ArrayList<>();
                    if (parts.length > 6) {
                        String[] actorNames = parts[6].split(";");
                        for (String actorName : actorNames) {
                            Actor actor = findActorByName(actorName.trim());
                            if (actor != null) {
                                movieActors.add(actor);
                            }
                        }
                    }

                    // Handle case where director might be null
                    Movie movie;
                    if (director != null) {
                        movie = new Movie(title, year, director, null, duration, movieActors, imdbRating);
                    } else {
                        // Create a movie with genre and director name from the file when director object is not found
                        movie = new Movie(title, year, genre.name(), parts[4], new HashMap<>(), imdbRating);
                        movie.setActors(movieActors);
                    }
                    movie.setId(nextMovieId.getAndIncrement());
                    movies.add(movie);
                }
            }
        } catch (IOException e) {
            throw new FileParsingException("Error loading movies: " + e.getMessage());
        }
    }

    public void loadSeries(String filename) {
        try (BufferedReader reader = new BufferedReader(new FileReader("src/main/resources/data/" + filename))) {
            String line;
            while ((line = reader.readLine()) != null) {
                String[] parts = parseCSVLine(line);
                if (parts.length >= 2) {
                    String title = parts[0];
                    // Split the genre string on pipe and take the first part
                    String genreStr = parts[1].split("\\|")[0].trim();
                    Genre genre = Genre.valueOf(genreStr.toUpperCase().replace(" ", "_"));

                    Series series = new Series(title);
                    series.setGenre(genre);  // Set the genre from the file
                    series.setId(nextSeriesId.getAndIncrement());
                    this.series.add(series);
                }
            }
        } catch (IOException e) {
            throw new FileParsingException("Error loading series: " + e.getMessage());
        }
    }

    public void loadAwardsAndBoxOffice(String filename) {
        try (BufferedReader reader = new BufferedReader(new FileReader("src/main/resources/data/" + filename))) {
            String line;
            while ((line = reader.readLine()) != null) {
                // Skip comments and empty lines
                if (line.trim().isEmpty() || line.startsWith("#")) {
                    continue;
                }

                String[] parts = line.split("\\|");
                if (parts.length >= 4) {
                    String type = parts[0].trim();
                    String title = parts[1].trim();
                    String boxOffice = parts[2].trim();
                    String awardsStr = parts[3].trim();

                    // Parse awards
                    List<String> awards = new ArrayList<>();
                    if (!awardsStr.isEmpty()) {
                        String[] awardArray = awardsStr.split(";");
                        for (String award : awardArray) {
                            awards.add(award.trim());
                        }
                    }

                    // Apply to movies or series
                    if ("MOVIE".equals(type)) {
                        Movie movie = findMovieByTitle(title);
                        if (movie != null) {
                            movie.setBoxOffice(boxOffice);
                            movie.setAwards(awards);
                        }
                    } else if ("SERIES".equals(type)) {
                        Series series = findSeriesByTitle(title);
                        if (series != null) {
                            series.setBoxOffice(boxOffice);
                            series.setAwards(awards);
                        }
                    }
                }
            }
        } catch (IOException e) {
            throw new FileParsingException("Error loading awards and box office data: " + e.getMessage());
        }
    }

    public void loadForumPosts(String filename) {
        try (BufferedReader reader = new BufferedReader(new FileReader("src/main/resources/data/" + filename))) {
            String line;
            while ((line = reader.readLine()) != null) {
                // Skip empty lines
                if (line.trim().isEmpty()) {
                    continue;
                }

                String[] parts = parseCSVLine(line);
                if (parts.length >= 8) {
                    // Format: id,userId,title,content,createdAt,updatedAt,upvotes,downvotes
                    int id = Integer.parseInt(parts[0]);
                    int userId = Integer.parseInt(parts[1]);
                    String title = parts[2];
                    String content = parts[3];
                    LocalDateTime createdAt = LocalDateTime.parse(parts[4]);
                    LocalDateTime updatedAt = LocalDateTime.parse(parts[5]);
                    int upvotes = Integer.parseInt(parts[6]);
                    int downvotes = Integer.parseInt(parts[7]);

                    ForumPost post = new ForumPost(userId, title, content);
                    post.setId(id); // Use the standard ID setter
                    post.setCreatedAt(createdAt);
                    post.setUpdatedAt(updatedAt);
                    post.setUpvotes(upvotes);
                    post.setDownvotes(downvotes);

                    // Update the next ID counter to avoid conflicts
                    // Using getAndUpdate to ensure thread-safe update of the counter
                    nextForumPostId.getAndUpdate(current -> Math.max(current, id + 1));

                    forumPosts.add(post);
                }
            }
        } catch (IOException e) {
            throw new FileParsingException("Error loading forum posts: " + e.getMessage());
        }
    }

    /**
     * Saves all forum posts to a file in a thread-safe manner.
     * @param filename The name of the file to save to (will be saved in src/main/resources/data/)
     * @throws FileParsingException if there's an error writing to the file
     */
    public void saveForumPosts(String filename) {
        forumPostLock.readLock().lock();
        try {
            try (java.io.PrintWriter writer = new java.io.PrintWriter("src/main/resources/data/" + filename)) {
                for (ForumPost post : forumPosts) {
                    writer.println(String.format("%d,%d,\"%s\",\"%s\",%s,%s,%d,%d",
                            post.getId(),
                            post.getUserId(),
                            escapeCsv(post.getTitle()),
                            escapeCsv(post.getContent()),
                            post.getCreatedAt(),
                            post.getUpdatedAt(),
                            post.getUpvotes(),
                            post.getDownvotes()));
                }
                logger.info("Successfully saved {} forum posts to {}", forumPosts.size(), filename);
            } catch (IOException e) {
                logger.error("Error saving forum posts to {}: {}", filename, e.getMessage(), e);
                throw new FileParsingException("Error saving forum posts: " + e.getMessage());
            }
        } finally {
            forumPostLock.readLock().unlock();
        }
    }

    private String escapeCsv(String input) {
        if (input == null) {
