public List<Movie> searchMovies(SearchCriteria criteria) {
        List<Movie> result = movies.stream()
                .filter(movie -> matchesCriteria(movie, criteria))
                .toList();

        // Convert to ArrayList since toList() returns an unmodifiable list
        List<Movie> modifiableResult = new ArrayList<>(result);

        // Apply sorting if criteria has sorting parameters
        if (criteria.getSortBy() != null && !criteria.getSortBy().isEmpty()) {
            sortMovies(modifiableResult, criteria);
        }

        return modifiableResult;
    }


