    /**
     * Adds a new forum post in a thread-safe manner.
     * @param post The forum post to add
     * @throws IllegalArgumentException if post is null, or if title/content is empty
     */
    public void addForumPost(ForumPost post) {
        if (post == null) {
            throw new IllegalArgumentException("Forum post cannot be null");
        }
        if (post.getTitle() == null || post.getTitle().trim().isEmpty()) {
            throw new IllegalArgumentException("Forum post title cannot be empty");
        }
        if (post.getContent() == null || post.getContent().trim().isEmpty()) {
            throw new IllegalArgumentException("Forum post content cannot be empty");
        }

        forumPostLock.writeLock().lock();
        try {
            // Ensure ID is not already in use
            int newId = nextForumPostId.getAndIncrement();
            while (findForumPostByIdInternal(newId) != null) {
                newId = nextForumPostId.getAndIncrement();
            }
            post.setId(newId);

            // Set timestamps if not already set
            LocalDateTime now = LocalDateTime.now();
            if (post.getCreatedAt() == null) {
                post.setCreatedAt(now);
            }
            if (post.getUpdatedAt() == null) {
                post.setUpdatedAt(post.getCreatedAt());
            }

            forumPosts.add(post);
            logger.debug("Added new forum post with ID: {}", newId);
        } finally {
            forumPostLock.writeLock().unlock();
        }
    }

