    /**
     * Checks if a movie matches the given search criteria.
     * @param movie The movie to check
     * @param criteria The search criteria to match against
     * @return true if the movie matches the criteria, false otherwise
     */
    private boolean matchesCriteria(Movie movie, SearchCriteria criteria) {
        // Title filter
        if (criteria.getTitle() != null && !criteria.getTitle().trim().isEmpty()) {
            if (!movie.getTitle().toLowerCase().contains(criteria.getTitle().toLowerCase())) {
                return false;
            }
        }

        // Actor filter
        if (criteria.getActorName() != null && !criteria.getActorName().trim().isEmpty()) {
            boolean hasActor = movie.getActors().stream()
                    .anyMatch(actor -> actor.getFullName().toLowerCase().contains(criteria.getActorName().toLowerCase()));
            if (!hasActor) {
                return false;
            }
        }

        // Director filter
        if (criteria.getDirectorName() != null && !criteria.getDirectorName().trim().isEmpty()) {
            if (movie.getDirector() == null || 
                !movie.getDirector().toLowerCase().contains(criteria.getDirectorName().toLowerCase())) {
                return false;
            }
        }

        // Year filter
        int movieYear = movie.getYear().getYear() + 1900; // Convert Date to year
        if (criteria.getMinYear() != null && movieYear < criteria.getMinYear()) {
            return false;
        }
        if (criteria.getMaxYear() != null && movieYear > criteria.getMaxYear()) {
            return false;
        }

        // Duration filter
        if (criteria.getMinDuration() != null && movie.getDuration() < criteria.getMinDuration()) {
            return false;
        }
        if (criteria.getMaxDuration() != null && movie.getDuration() > criteria.getMaxDuration()) {
            return false;
        }

        // IMDB rating filter
        if (movie.getImdbRating() < criteria.getMinImdbRating() || 
            movie.getImdbRating() > criteria.getMaxImdbRating()) {
            return false;
        }

        // User rating filter
        double avgUserRating = movie.calculateAverageUserRating();
        if (avgUserRating < criteria.getMinUserRating() || 
            avgUserRating > criteria.getMaxUserRating()) {
            return false;
        }

        // Genre filter
        if (criteria.hasGenreFilter()) {
            if (movie.getGenre() == null || !criteria.getGenres().contains(movie.getGenre())) {
                return false;
            }
        }

        return true;
    }

    /**
     * Sorts a list of movies based on the given criteria.
     * @param movies The list of movies to sort
     * @param criteria The search criteria containing sorting parameters
     */
    private void sortMovies(List<Movie> movies, SearchCriteria criteria) {
        if (criteria.getSortBy() == null || criteria.getSortBy().trim().isEmpty()) {
            return;
        }

        switch (criteria.getSortBy().toLowerCase()) {
            case "title":
                movies.sort((m1, m2) -> criteria.isSortDescending() ? 
                    m2.getTitle().compareToIgnoreCase(m1.getTitle()) : 
                    m1.getTitle().compareToIgnoreCase(m2.getTitle()));
                break;
            case "year":
                movies.sort((m1, m2) -> criteria.isSortDescending() ? 
                    Integer.compare(m2.getYear().getYear() + 1900, m1.getYear().getYear() + 1900) : 
                    Integer.compare(m1.getYear().getYear() + 1900, m2.getYear().getYear() + 1900));
                break;
            case "rating":
                movies.sort((m1, m2) -> criteria.isSortDescending() ? 
                    Double.compare(m2.getImdbRating(), m1.getImdbRating()) : 
                    Double.compare(m1.getImdbRating(), m2.getImdbRating()));
                break;
            case "duration":
                movies.sort((m1, m2) -> criteria.isSortDescending() ? 
                    Integer.compare(m2.getDuration(), m1.getDuration()) : 
                    Integer.compare(m1.getDuration(), m2.getDuration()));
                break;
            default:
                logger.warn("Unknown sort criteria: {}", criteria.getSortBy());
                break;
        }
    }

    /**
     * Parses a CSV line handling quoted fields properly
     *
     * @param line the CSV line to parse
     * @return array of parsed fields
     */
    private String[] parseCSVLine(String line) {
        List<String> fields = new ArrayList<>();
        StringBuilder currentField = new StringBuilder();
        boolean inQuotes = false;

        for (int i = 0; i < line.length(); i++) {
            char c = line.charAt(i);

            if (c == '"') {
                inQuotes = !inQuotes;
                currentField.append(c); // Keep the quotes for now, we'll remove them later
            } else if (c == ',' && !inQuotes) {
                fields.add(currentField.toString().trim());
                currentField = new StringBuilder();
            } else {
                currentField.append(c);
            }
        }

        // Add the last field
        fields.add(currentField.toString().trim());

        return fields.toArray(new String[0]);
    }

