+            if (criteria.getSortBy() != null && !criteria.getSortBy().isEmpty()) {
+                sortSeries(modifiableResult, criteria);
+            }
+
+            return modifiableResult;
+        } finally {
+            seriesLock.readLock().unlock();
+        }
+    }
+
+    /**
+     * Checks if a series matches the given search criteria.
+     * @param series The series to check
+     * @param criteria The search criteria to match against
+     * @return true if the series matches the criteria, false otherwise
+     */
+    private boolean matchesSeriesCriteria(Series series, SearchCriteria criteria) {
+        // Title filter
+        if (criteria.getTitle() != null && !criteria.getTitle().trim().isEmpty()) {
+            if (!series.getTitle().toLowerCase().contains(criteria.getTitle().toLowerCase())) {
+                return false;
+            }
+        }
+
+        // IMDB rating filter
+        if (series.getImdbRating() < criteria.getMinImdbRating() || 
+            series.getImdbRating() > criteria.getMaxImdbRating()) {
+            return false;
+        }
+
+        // User rating filter
+        double avgUserRating = series.calculateAverageUserRating();
+        if (avgUserRating < criteria.getMinUserRating() || 
+            avgUserRating > criteria.getMaxUserRating()) {
+            return false;
+        }
+
+        // Genre filter
+        if (criteria.hasGenreFilter()) {
+            if (series.getGenre() == null || !criteria.getGenres().contains(series.getGenre())) {
+                return false;
+            }
+        }
+
+        return true;
+    }
+
+    /**
+     * Sorts a list of series based on the given criteria.
+     * @param series The list of series to sort
+     * @param criteria The search criteria containing sorting parameters
+     */
+    private void sortSeries(List<Series> series, SearchCriteria criteria) {
+        if (criteria.getSortBy() == null || criteria.getSortBy().trim().isEmpty()) {
+            return;
+        }
+
+        switch (criteria.getSortBy().toLowerCase()) {
+            case "title":
+                series.sort((s1, s2) -> criteria.isSortDescending() ? 
+                    s2.getTitle().compareToIgnoreCase(s1.getTitle()) : 
+                    s1.getTitle().compareToIgnoreCase(s2.getTitle()));
+                break;
+            case "rating":
+                series.sort((s1, s2) -> criteria.isSortDescending() ? 
+                    Double.compare(s2.getImdbRating(), s1.getImdbRating()) : 
+                    Double.compare(s1.getImdbRating(), s2.getImdbRating()));
+                break;
+            default:
+                logger.warn("Unknown sort criteria for series: {}", criteria.getSortBy());
+                break;
+        }
     }

