    private void loadMovies() {
        try {
            // Using search with empty criteria to get all movies
            List<Movie> movies = contentService.search(new SearchCriteria(query))
                    .stream()
                    .filter(Movie.class::isInstance)
                    .map(Movie.class::cast)
                    .collect(Collectors.toList());
            updateMoviesTable(movies);
        } catch (Exception e) {
            logger.error("Error loading movies: {}", e.getMessage(), e);
            UIUtils.showError("Error", "Failed to load movies");
        }
    }

    private void loadSeries() {
        try {
            // Using search with empty criteria to get all series
            List<Series> seriesList = contentService.search(new SearchCriteria(query))
                    .stream()
                    .filter(Series.class::isInstance)
                    .map(Series.class::cast)
                    .collect(Collectors.toList());
            updateSeriesTable(seriesList);
        } catch (Exception e) {
            logger.error("Error loading series: {}", e.getMessage(), e);
            UIUtils.showError("Error", "Failed to load series");
        }
    }

    private void updateMoviesTable(List<Movie> movies) {
        if (movieTableView == null) {
            logger.error("Movie TableView is not initialized");
            return;
        }

        movieTableView.getItems().clear();
        movieTableView.getItems().addAll(movies);

        // Setup columns if not already done
        if (movieTableView.getColumns().isEmpty()) {
            TableColumn<Movie, String> titleCol = new TableColumn<>("Title");
            titleCol.setCellValueFactory(new PropertyValueFactory<>("title"));

            TableColumn<Movie, Integer> yearCol = new TableColumn<>("Year");
            yearCol.setCellValueFactory(new PropertyValueFactory<>("releaseYear"));

            movieTableView.getColumns().addAll(titleCol, yearCol);
        }
    }

    private void updateSeriesTable(List<Series> seriesList) {
        if (seriesTableView == null) {
            logger.error("Series TableView is not initialized");
            return;
        }

        seriesTableView.getItems().clear();
        seriesTableView.getItems().addAll(seriesList);

        // Setup columns if not already done
        if (seriesTableView.getColumns().isEmpty()) {
            TableColumn<Series, String> titleCol = new TableColumn<>("Title");
            titleCol.setCellValueFactory(new PropertyValueFactory<>("title"));

            TableColumn<Series, Integer> startYearCol = new TableColumn<>("Start Year");
            startYearCol.setCellValueFactory(new PropertyValueFactory<>("startYear"));

            seriesTableView.getColumns().addAll(titleCol, startYearCol);
        }
    }

    private void setupEventHandlers() {
        // Set up event handlers for content selection
        if (movieTableView != null) {
            movieTableView.getSelectionModel().selectedItemProperty().addListener((obs, oldSelection, newSelection) -> {
                if (newSelection != null) {
