    @FXML
    private void showAddMovieDialog() {
        try {
            // Create a dialog
            Dialog<Movie> dialog = new Dialog<>();
            dialog.setTitle("Add New Movie");
            dialog.setHeaderText("Enter movie details");

            // Set the button types
            ButtonType addButton = new ButtonType("Add", ButtonBar.ButtonData.OK_DONE);
            dialog.getDialogPane().getButtonTypes().addAll(addButton, ButtonType.CANCEL);

            // Create form fields
            GridPane grid = new GridPane();
            grid.setHgap(10);
            grid.setVgap(10);
            grid.setPadding(new Insets(20, 150, 10, 10));

            TextField titleField = new TextField();
            TextArea descriptionField = new TextArea();
            TextField releaseYearField = new TextField();
            TextField durationField = new TextField();
            TextField directorField = new TextField();
            TextField genresField = new TextField();

            grid.add(new Label("Title:"), 0, 0);
            grid.add(titleField, 1, 0);
            grid.add(new Label("Description:"), 0, 1);
            grid.add(descriptionField, 1, 1);
            grid.add(new Label("Release Year:"), 0, 2);
            grid.add(releaseYearField, 1, 2);
            grid.add(new Label("Duration (minutes):"), 0, 3);
            grid.add(durationField, 1, 3);
            grid.add(new Label("Director:"), 0, 4);
            grid.add(directorField, 1, 4);
            grid.add(new Label("Genres (comma-separated):"), 0, 5);
            grid.add(genresField, 1, 5);

            dialog.getDialogPane().setContent(grid);

            // Request focus on the title field by default
            Platform.runLater(titleField::requestFocus);

            // Convert the result to a Movie object when the add button is clicked
            dialog.setResultConverter(dialogButton -> {
                if (dialogButton == addButton) {
                    try {
                        Movie movie = new Movie();
                        movie.setTitle(titleField.getText().trim());
                        movie.setSummary(descriptionField.getText().trim());
                        movie.setReleaseYear(Integer.parseInt(releaseYearField.getText().trim()));
                        movie.setDuration(Integer.parseInt(durationField.getText().trim()));
                        movie.setDirector(directorField.getText().trim());

                        // Parse genres (comma-separated)
                        String[] genreArray = genresField.getText().split(",");
                        for (String genre : genreArray) {
                            String genreName = genre.trim();
                            if (!genreName.isEmpty()) {
                                movie.addGenre(Genre.valueOf(genreName));
                            }
                        }

                        return movie;
                    } catch (NumberFormatException e) {
                        showError("Invalid Input", "Please enter valid numbers for year and duration.");
                        return null;
                    } catch (Exception e) {
                        showError("Error", "Failed to create movie: " + e.getMessage());
                        return null;
                    }
                }
                return null;
            });

            Optional<Movie> result = dialog.showAndWait();
            result.ifPresent(movie -> {
                addContent(movie);
                showSuccess("Success", "Movie added successfully!");
            });

        } catch (Exception e) {
            logger.error("Error showing add movie dialog: {}", e.getMessage(), e);
            showError("Error", "Failed to show add movie dialog: " + e.getMessage());
        }
    }

    private void showMovieDetails(Movie movie) {
        try {
            Dialog<Void> dialog = new Dialog<>();
            dialog.setTitle("Movie Details");
            dialog.setHeaderText(movie.getTitle());

            // Set the button type
            dialog.getDialogPane().getButtonTypes().add(ButtonType.CLOSE);

            // Create details view
            VBox content = new VBox(10);
            content.setPadding(new Insets(20));

            Label yearLabel = new Label("Year: " + movie.getReleaseYear());
            Label durationLabel = new Label("Duration: " + movie.getDuration() + " minutes");

            // Handle potentially null fields
            String description = movie.getDescription() != null ? movie.getDescription() : "No description available";
            Label descriptionLabel = new Label("Description: " + description);

            descriptionLabel.setWrapText(true);
            descriptionLabel.setMaxWidth(400);

            content.getChildren().addAll(yearLabel, durationLabel, descriptionLabel);

            // Add genres if available
            try {
                if (movie.getGenres() != null && !movie.getGenres().isEmpty()) {
                    Label genresLabel = new Label("Genres: " + String.join(", ", movie.getGenres()));
                    content.getChildren().add(genresLabel);
                }
            } catch (Exception e) {
                logger.warn("Error displaying genres: {}", e.getMessage());
            }

            dialog.getDialogPane().setContent(content);
            dialog.showAndWait();
        } catch (Exception e) {
            logger.error("Error showing movie details: {}", e.getMessage(), e);
            UIUtils.showError("Error", "Failed to show movie details");
        }
    }

    @FXML
    private void showRatingDialog() {
        try {
            // Get the selected movie
            Movie selectedMovie = movieTableView.getSelectionModel().getSelectedItem();
            if (selectedMovie == null) {
                showWarning("No Selection", "Please select a movie to rate.");
                return;
            }

            // Create the rating dialog
            Dialog<Rating> dialog = new Dialog<>();
            dialog.setTitle("Rate Movie");
            dialog.setHeaderText("Rate: " + selectedMovie.getTitle());

            // Set the button types
            ButtonType rateButton = new ButtonType("Rate", ButtonBar.ButtonData.OK_DONE);
            dialog.getDialogPane().getButtonTypes().addAll(rateButton, ButtonType.CANCEL);

            // Create the rating control
            RatingControl ratingControl = new RatingControl();
            ratingControl.setRating(0);
            ratingControl.setMax(10);

            // Add a text area for review (optional)
            TextArea reviewArea = new TextArea();
            reviewArea.setPromptText("Write your review here (optional)");
            reviewArea.setWrapText(true);
            reviewArea.setPrefRowCount(4);

            VBox content = new VBox(10);
            content.setPadding(new Insets(20));
            content.getChildren().addAll(
                    new Label("Your Rating:"),
                    ratingControl,
                    new Label("Your Review (optional):"),
                    reviewArea
            );

            dialog.getDialogPane().setContent(content);

            // Request focus on the rating control by default
            Platform.runLater(ratingControl::requestFocus);

            // Convert the result to a Rating object when the rate button is clicked
            dialog.setResultConverter(dialogButton -> {
                if (dialogButton == rateButton) {
                    try {
                        Rating rating = new Rating();
                        rating.setContentId(selectedMovie.getId());
                        rating.setScore(ratingControl.getRating());
                        rating.setReview(reviewArea.getText().trim());
                        rating.setUserId(UserService.getInstance(dataManager, encryptionService).getCurrentUser().getId());
                        rating.setCreatedAt(java.time.LocalDateTime.now());
                        return rating;
                    } catch (Exception e) {
                        showError("Error", "Failed to create rating: " + e.getMessage());
                        return null;
                    }
                }
                return null;
            });

            Optional<Rating> result = dialog.showAndWait();
            result.ifPresent(rating -> {
                try {
                    // Save the rating
                    contentService.saveRating(rating);
                    showSuccess("Success", "Thank you for your rating!");

                    // Refresh the movie details to show the updated rating
                    if (movieTableView.getSelectionModel().getSelectedItem() != null) {
                        showMovieDetails(movieTableView.getSelectionModel().getSelectedItem());
                    }
                } catch (Exception e) {
                    logger.error("Error saving rating: {}", e.getMessage(), e);
                    showError("Error", "Failed to save rating: " + e.getMessage());
                }
            });

        } catch (Exception e) {
            logger.error("Error showing rating dialog: {}", e.getMessage(), e);
            showError("Error", "Failed to show rating dialog: " + e.getMessage());
        }
    }

    private void showSeriesDetails(Series series) {
        // Show series details including seasons and episodes
        try {
            Dialog<Void> dialog = new Dialog<>();
            dialog.setTitle("Series Details");
            dialog.setHeaderText(series.getTitle());

            // Set the button type
            dialog.getDialogPane().getButtonTypes().add(ButtonType.CLOSE);

            // Create details view
            VBox content = new VBox(10);
            content.setPadding(new Insets(20));

            Label yearLabel = new Label("Year: " + series.getReleaseYear());
            Label seasonsLabel = new Label("Seasons: " + (series.getSeasons() != null ? series.getSeasons().size() : 0));
            String description = series.getDescription() != null ? series.getDescription() : "No description available";
            Label descriptionLabel = new Label("Description: " + description);

            descriptionLabel.setWrapText(true);
            descriptionLabel.setMaxWidth(400);

            // Create a list view for seasons and episodes
            TreeView<String> seasonsTree = new TreeView<>();
            TreeItem<String> rootItem = new TreeItem<>("Seasons");

            if (series.getSeasons() != null) {
                for (Season season : series.getSeasons()) {
                    TreeItem<String> seasonItem = new TreeItem<>("Season " + season.getSeasonNumber());

                    if (season.getEpisodes() != null) {
                        for (Episode episode : season.getEpisodes()) {
                            String episodeTitle = episode.getTitle() != null ? episode.getTitle() : "Untitled";
                            TreeItem<String> episodeItem = new TreeItem<>(
                                    String.format("Episode %d: %s",
                                            episode.getEpisodeNumber(),
                                            episodeTitle)
                            );
                            seasonItem.getChildren().add(episodeItem);
                        }
                    }

                    rootItem.getChildren().add(seasonItem);
                }
            }

            seasonsTree.setRoot(rootItem);
            seasonsTree.setShowRoot(false);

            // Add all components to the content
            content.getChildren().addAll(yearLabel, descriptionLabel, seasonsLabel, new Label("\nEpisodes:"), seasonsTree);

            dialog.getDialogPane().setContent(content);
            dialog.showAndWait();

        } catch (Exception e) {
            logger.error("Error showing series details: {}", e.getMessage(), e);
            showError("Error", "Failed to show series details: " + e.getMessage());
        }
    }

    // Content management methods
    public void addContent(Content content) {
        try {
            if (content == null) {
                logger.warn("Attempted to add null content");
                return;
            }

            contentService.save(content);
            refreshContentList();
            UIUtils.showSuccess("Success", "Content added successfully!");
        } catch (Exception e) {
            logger.error("Error adding content: {}", e.getMessage(), e);
            UIUtils.showError("Error", "Failed to add content: " + e.getMessage());
        }
    }

    public void updateContent(Content content) {
        try {
            if (content == null) {
                logger.warn("Attempted to update null content");
                return;
            }

            contentService.save(content);
            refreshContentList();
            UIUtils.showSuccess("Success", "Content updated successfully!");
        } catch (Exception e) {
            logger.error("Error updating content: {}", e.getMessage(), e);
            UIUtils.showError("Error", "Failed to update content: " + e.getMessage());
        }
    }

    public void deleteContent(Content content) {
        try {
            if (content == null) {
                logger.warn("Attempted to delete null content");
                return;
            }

            // Show confirmation dialog
            Alert alert = new Alert(Alert.AlertType.CONFIRMATION);
            alert.setTitle("Confirm Deletion");
            alert.setHeaderText("Delete " + content.getTitle() + "?");
            alert.setContentText("Are you sure you want to delete this content? This action cannot be undone.");

