                            Season season = new Season();
                            season.setSeasonNumber(i);
                            season.setSeries(series);
                            series.addSeason(season);
                        }

                        return series;
                    } catch (NumberFormatException e) {
                        showError("Invalid Input", "Please enter valid numbers for year and seasons.");
                        return null;
                    } catch (Exception e) {
                        showError("Error", "Failed to create series: " + e.getMessage());
                        return null;
                    }
                }
                return null;
            });

