            result.ifPresent(series -> {
                addContent(series);
                showSuccess("Success", "Series added successfully!");
            });

        } catch (Exception e) {
            logger.error("Error showing add series dialog: {}", e.getMessage(), e);
            showError("Error", "Failed to show add series dialog: " + e.getMessage());
        }
    }

