    // Search functionality
    public void searchContent(String query) {
        if (query == null || query.trim().isEmpty()) {
            // If search is empty, just refresh the current view
            refreshContentList();
            return;
        }

        // Show loading indicator
        showLoadingIndicator(true);

        // Create search criteria
        SearchCriteria criteria = new SearchCriteria(query);

        // Perform search asynchronously
        searchService.searchAsync(criteria)
                .thenAccept(results -> {
                    Platform.runLater(() -> {
                        // Update UI with search results
                        updateSearchResults(results);
                        showLoadingIndicator(false);
                    });
                })
                .exceptionally(e -> {
                    logger.error("Error performing search: {}", e.getMessage(), e);
                    Platform.runLater(() -> {
                        UIUtils.showError("Search Error", "Failed to perform search: " + e.getMessage());
                        showLoadingIndicator(false);
                    });
                    return null;
                });
    }

    /**
     * Updates the UI with search results.
     *
     * @param results The search results to display
     */
    private void updateSearchResults(List<Content> results) {
        if (results == null || results.isEmpty()) {
            // Show no results message
            UIUtils.showInfo("No Results", "No content found matching your search.");
            return;
        }

        // Clear current selection
        movieTableView.getSelectionModel().clearSelection();
        seriesTableView.getSelectionModel().clearSelection();

        // Group results by type
        Map<Class<? extends Content>, List<Content>> resultsByType = results.stream()
                .collect(Collectors.groupingBy(Content::getClass));

        // Update movies table if there are movie results
        if (resultsByType.containsKey(Movie.class)) {
            List<Movie> movies = resultsByType.get(Movie.class).stream()
                    .map(m -> (Movie) m)
                    .collect(Collectors.toList());
            updateMoviesTable(movies);
        }

        // Update series table if there are series results
        if (resultsByType.containsKey(Series.class)) {
            List<Series> seriesList = resultsByType.get(Series.class).stream()
                    .map(s -> (Series) s)
                    .collect(Collectors.toList());
            updateSeriesTable(seriesList);
        }

        // Switch to the appropriate tab if needed
        if (resultsByType.containsKey(Movie.class) && !resultsByType.containsKey(Series.class)) {
            contentTabPane.getSelectionModel().select(moviesTab);
        } else if (resultsByType.containsKey(Series.class) && !resultsByType.containsKey(Movie.class)) {
            contentTabPane.getSelectionModel().select(seriesTab);
        }
    }

