package com.papel.imdb_clone.gui;

import com.papel.imdb_clone.data.DataManager;
import com.papel.imdb_clone.data.SearchCriteria;
import com.papel.imdb_clone.model.Movie;
import com.papel.imdb_clone.model.Series;
import javafx.application.Application;
import javafx.beans.property.SimpleStringProperty;
import javafx.collections.FXCollections;
import javafx.collections.ObservableList;
import javafx.fxml.FXMLLoader;
import javafx.geometry.Insets;
import javafx.scene.Node;
import javafx.scene.Parent;
import javafx.scene.Scene;
import javafx.scene.control.*;
import javafx.scene.control.ButtonBar.ButtonData;
import javafx.scene.layout.GridPane;
import javafx.scene.layout.HBox;
import javafx.scene.layout.VBox;
import javafx.stage.Stage;

import java.io.IOException;
import java.util.List;
import java.util.Map;
import java.util.Objects;
import java.util.Optional;
import java.util.stream.Collectors;

public class MovieManagementGUI extends Application {
    private final DataManager dataManager;
    private Stage primaryStage;
    private Scene mainScene;
    private Tab moviesTab;
    private Tab seriesTab;
    private Tab addContentTab;
    private Tab searchTab;
    private TableView<Movie> movieTable;
    private TreeView<String> seriesTree;

    public MovieManagementGUI() {
        this.dataManager = new DataManager();
    }

    public static void main(String[] args) {
        launch(args);
    }

    @Override
    public void start(Stage primaryStage) {
        try {
            this.primaryStage = primaryStage;
            primaryStage.setTitle("IMDB Clone");

            // Load FXML
            FXMLLoader loader = new FXMLLoader(getClass().getResource("/fxml/main.fxml"));
            loader.setController(this);
            Parent root = loader.load();

            // Apply CSS
            Scene scene = new Scene(root, 800, 600);
            scene.getStylesheets().add(Objects.requireNonNull(getClass().getResource("/css/styles.css")).toExternalForm());

            dataManager.loadAllData();

            // Initialize data in tables/trees
            refreshMovieTable();
            refreshSeriesTree();

            primaryStage.setScene(scene);
            primaryStage.show();

        } catch (IOException e) {
            showAlert("Error", "Failed to load application: " + e.getMessage());
            e.printStackTrace();
        }
    }

    private void initializeComponents() {
        TabPane tabPane = new TabPane();

        setupMoviesTab();
        setupSeriesTab();
        setupAddContentTab();
        setupSearchTab();

        tabPane.getTabs().addAll(moviesTab, seriesTab, addContentTab, searchTab);

        VBox root = new VBox(tabPane);

        mainScene = new Scene(root, 800, 600);
    }

    private void setupMoviesTab() {
        moviesTab = new Tab("Movies");
        moviesTab.setClosable(false);

        movieTable = new TableView<>();

        TableColumn<Movie, String> titleColumn = new TableColumn<>("Title");
        titleColumn.setCellValueFactory(data -> new SimpleStringProperty(data.getValue().getTitle()));
        titleColumn.setPrefWidth(180);

        TableColumn<Movie, String> yearColumn = new TableColumn<>("Year");
        yearColumn.setCellValueFactory(data ->
                new SimpleStringProperty(String.valueOf(data.getValue().getYear().getYear() + 1900))
        );
        yearColumn.setPrefWidth(80);

        TableColumn<Movie, String> genreColumn = new TableColumn<>("Genre");
        genreColumn.setCellValueFactory(data ->
                new SimpleStringProperty(data.getValue().getGenre().toString())
        );
        genreColumn.setPrefWidth(100);

        TableColumn<Movie, String> directorColumn = new TableColumn<>("Director");
        directorColumn.setCellValueFactory(data ->
                new SimpleStringProperty(
                        data.getValue().getDirector() != null
                                ? data.getValue().getDirector()
                                : "Unknown"
                )
        );
        directorColumn.setPrefWidth(150);

        TableColumn<Movie, String> imdbColumn = new TableColumn<>("IMDB Rating");
        imdbColumn.setCellValueFactory(data ->
                new SimpleStringProperty(String.format("%.1f", data.getValue().getImdbRating()))
        );
        imdbColumn.setPrefWidth(100);

        movieTable.getColumns().addAll(
                titleColumn, yearColumn, genreColumn, directorColumn, imdbColumn
        );

        Button addMovieBtn = new Button("Add Movie");
        addMovieBtn.setOnAction(e -> showAddMovieDialog());

        Button rateMovieBtn = new Button("Rate Movie");
        rateMovieBtn.setOnAction(e -> {
            Movie selected = movieTable.getSelectionModel().getSelectedItem();
            if (selected != null) {
                showRatingDialog(selected);
            } else {
                showAlert("No Selection", "Please select a movie to rate.");
            }
        });

        HBox buttonBox = new HBox(10, addMovieBtn, rateMovieBtn);
        buttonBox.setPadding(new Insets(10));

        VBox movieLayout = new VBox(10, movieTable, buttonBox);
        movieLayout.setPadding(new Insets(10));

        moviesTab.setContent(movieLayout);
        refreshMovieTable();
    }

    private void refreshMovieTable() {
        List<Movie> movies = dataManager.getAllMovies();
        ObservableList<Movie> items = FXCollections.observableArrayList(movies);
        movieTable.setItems(items);
    }

    private void showRatingDialog(Movie selected) {
        TextInputDialog dialog = new TextInputDialog();
        dialog.initOwner(primaryStage);
        dialog.setTitle("Rate Movie");
        dialog.setHeaderText("Rate: " + selected.getTitle());
        dialog.setContentText("Enter your rating (0.0 - 10.0):");

        Optional<String> result = dialog.showAndWait();
        result.ifPresent(input -> {
            try {
                double rating = Double.parseDouble(input.trim());
                dataManager.rateMovie(selected, rating);
                refreshMovieTable();
            } catch (NumberFormatException ex) {
                showAlert("Invalid Input", "Please enter a valid decimal number.");
            }
        });
    }

    private void showAddMovieDialog() {
        Dialog<Movie> dialog = new Dialog<>();
        dialog.initOwner(primaryStage);
        dialog.setTitle("Add Movie");
        dialog.setHeaderText("Enter new movie details");

        ButtonType addBtnType = new ButtonType("Add", ButtonData.OK_DONE);
        dialog.getDialogPane().getButtonTypes().addAll(addBtnType, ButtonType.CANCEL);

        GridPane grid = new GridPane();
        grid.setHgap(10);
        grid.setVgap(10);
        grid.setPadding(new Insets(20, 150, 10, 10));

        TextField titleField = new TextField();
        TextField yearField = new TextField();
        TextField genreField = new TextField();
        TextField directorField = new TextField();

        titleField.setPromptText("Title");
        yearField.setPromptText("Year");
        genreField.setPromptText("Genre");
        directorField.setPromptText("Director");

        grid.add(new Label("Title:"), 0, 0);
        grid.add(titleField, 1, 0);
        grid.add(new Label("Year:"), 0, 1);
        grid.add(yearField, 1, 1);
        grid.add(new Label("Genre:"), 0, 2);
        grid.add(genreField, 1, 2);
        grid.add(new Label("Director:"), 0, 3);
        grid.add(directorField, 1, 3);

        // disable Add button until the title is filled
        Node addButton = dialog.getDialogPane().lookupButton(addBtnType);
        addButton.setDisable(true);
        titleField.textProperty().addListener((o, oldVal, newVal) ->
                addButton.setDisable(newVal.trim().isEmpty())
        );

        dialog.getDialogPane().setContent(grid);

        dialog.setResultConverter(btn -> {
            if (btn == addBtnType) {
                try {
                    String title = titleField.getText().trim();
                    int year = Integer.parseInt(yearField.getText().trim());
                    String genre = genreField.getText().trim();
                    String director = directorField.getText().trim();

                    // Movie constructor: (String title, int year, String genre, String directorName)
                    Map<Integer, Integer> userRatings = Map.of();
                    Movie movie = new Movie(title, year, genre, director, userRatings);
                    return movie;

                } catch (NumberFormatException e) {
                    showAlert("Invalid Input", "Year must be an integer.");
                }
            }
            return null;
        });

        Optional<Movie> result = dialog.showAndWait();
        result.ifPresent(movie -> {
            dataManager.addMovie(movie);
            refreshMovieTable();
        });
    }

    private void setupSeriesTab() {
        seriesTab = new Tab("Series");
        seriesTab.setClosable(false);

        seriesTree = new TreeView<>();
        TreeItem<String> root = new TreeItem<>("Series");
        seriesTree.setRoot(root);

        Button addSeriesBtn = new Button("Add Series");
        addSeriesBtn.setOnAction(e -> showAddSeriesDialog());

        HBox buttonBox = new HBox(10, addSeriesBtn);
        buttonBox.setPadding(new Insets(10));

        VBox seriesLayout = new VBox(10, seriesTree, buttonBox);
        seriesLayout.setPadding(new Insets(10));

        seriesTab.setContent(seriesLayout);
        refreshSeriesTree();
    }

    private void refreshSeriesTree() {
        TreeItem<String> root = seriesTree.getRoot();
        root.getChildren().clear();

        List<Series> allSeries = dataManager.getAllSeries();
        for (Series s : allSeries) {
            TreeItem<String> seriesNode = new TreeItem<>(s.getTitle());
            root.getChildren().add(seriesNode);
        }
        root.setExpanded(true);
    }

    private void showAddSeriesDialog() {
        TextInputDialog dialog = new TextInputDialog();
        dialog.initOwner(primaryStage);
        dialog.setTitle("Add Series");
        dialog.setHeaderText("Enter series title:");
        dialog.setContentText("Title:");

        Optional<String> result = dialog.showAndWait();
        result.ifPresent(name -> {
            Series newSeries = new Series(name.trim());
            dataManager.addSeries(newSeries);
            refreshSeriesTree();
        });
    }

    private void setupAddContentTab() {
        addContentTab = new Tab("Add Content");
        addContentTab.setClosable(false);

        VBox layout = new VBox(20);
        layout.setPadding(new Insets(20));

        Label titleLabel = new Label("Add New Content");
        titleLabel.setStyle("-fx-font-size: 18px; -fx-font-weight: bold;");

        Button addMovieBtn = new Button("Add New Movie");
        addMovieBtn.setPrefWidth(200);
        addMovieBtn.setOnAction(e -> showAddMovieDialog());

        Button addSeriesBtn = new Button("Add New Series");
        addSeriesBtn.setPrefWidth(200);
        addSeriesBtn.setOnAction(e -> showAddSeriesDialog());

        layout.getChildren().addAll(titleLabel, addMovieBtn, addSeriesBtn);
        addContentTab.setContent(layout);
    }

    private void setupSearchTab() {
        searchTab = new Tab("Search");
        searchTab.setClosable(false);

        VBox layout = new VBox(15);
        layout.setPadding(new Insets(15));

        GridPane searchGrid = new GridPane();
        searchGrid.setHgap(10);
        searchGrid.setVgap(10);

        TextField titleField = new TextField();
        TextField actorField = new TextField();
        TextField directorField = new TextField();
        TextField minImdbField = new TextField();
        TextField minUserField = new TextField();

        searchGrid.add(new Label("Title:"), 0, 0);
        searchGrid.add(titleField, 1, 0);
        searchGrid.add(new Label("Actor:"), 0, 1);
        searchGrid.add(actorField, 1, 1);
        searchGrid.add(new Label("Director:"), 0, 2);
        searchGrid.add(directorField, 1, 2);
        searchGrid.add(new Label("Min IMDB Rating:"), 0, 3);
        searchGrid.add(minImdbField, 1, 3);
        searchGrid.add(new Label("Min User Rating:"), 0, 4);
        searchGrid.add(minUserField, 1, 4);

        Button searchBtn = new Button("Search");
        ListView<String> view = new ListView<>();

        searchBtn.setOnAction(e -> {
            SearchCriteria criteria = new SearchCriteria();

            if (!titleField.getText().trim().isEmpty()) {
                criteria.setTitle(titleField.getText().trim());
            }
            if (!actorField.getText().trim().isEmpty()) {
                criteria.setActorName(actorField.getText().trim());
            }
            if (!directorField.getText().trim().isEmpty()) {
                criteria.setDirectorName(directorField.getText().trim());
            }
            try {
                if (!minImdbField.getText().trim().isEmpty()) {
                    criteria.setMinImdbRating(
                            Double.parseDouble(minImdbField.getText().trim())
                    );
                }
                if (!minUserField.getText().trim().isEmpty()) {
                    criteria.setMinUserRating(
                            Double.parseDouble(minUserField.getText().trim())
                    );
                }
            } catch (NumberFormatException ex) {
                showAlert("Error", "Rating fields must be numeric.");
                return;
            }

            handleSearch(criteria, view);
        });

        layout.getChildren().addAll(
                searchGrid,
                searchBtn,
                new Label("Results:"),
                view
        );
        searchTab.setContent(layout);
    }

    private void handleSearch(SearchCriteria criteria, ListView<String> resultsView) {
        List<Movie> matches = dataManager.searchMovies(criteria);

        List<String> display = matches.stream()
                .map(m -> String.format(
                        "%s (%d) â€“ IMDb: %.1f, User: %.1f",
                        m.getTitle(),
                        m.getYear().getYear() + 1900,
                        m.getImdbRating(),
                        m.getUserRating() != null ? m.getUserRating() : 0.0
                ))
                .collect(Collectors.toList());

        resultsView.setItems(FXCollections.observableArrayList(display));
    }

    private void showAlert(String title, String message) {
        Alert alert = new Alert(Alert.AlertType.ERROR);
        alert.initOwner(primaryStage);
        alert.setTitle(title);
        alert.setHeaderText(null);
        alert.setContentText(message);
        alert.showAndWait();
    }

    public Stage getPrimaryStage() {
        return primaryStage;
    }

    public void setPrimaryStage(Stage primaryStage) {
        this.primaryStage = primaryStage;
    }
}
