package com.papel.imdb_clone.gui;

import com.papel.imdb_clone.data.DataManager;
import com.papel.imdb_clone.model.Movie;
import com.papel.imdb_clone.model.Series;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.*;

/**
 * Test class to verify that MovieDetailsDialog can handle content with awards and box office data
 */
public class MovieDetailsDialogTest {

    private DataManager dataManager;

    @BeforeEach
    void setUp() {
        dataManager = new DataManager();
        dataManager.loadAllData();
    }

    @Test
    void testMovieWithAwardsAndBoxOffice() {
        Movie movie = dataManager.findMovieByTitle("The Godfather");
        assertNotNull(movie, "Movie should be found");

        // Verify the movie has awards and box office data
        assertNotNull(movie.getBoxOffice(), "Movie should have box office data");
        assertFalse(movie.getAwards().isEmpty(), "Movie should have awards");

        // Test that the movie can be used with MovieDetailsDialog
        // (We can't test the actual dialog display in a unit test, but we can verify the data is there)
        assertTrue(movie.getBoxOffice().contains("$245.1 million"), "Box office should contain expected value");
        assertTrue(movie.getAwards().stream().anyMatch(award -> award.contains("Academy Award for Best Picture")), 
                  "Should contain Academy Award for Best Picture");
    }

    @Test
    void testSeriesWithAwardsAndBoxOffice() {
        Series series = dataManager.findSeriesByTitle("The Crown");
        assertNotNull(series, "Series should be found");

        // Verify the series has awards and box office data
        assertNotNull(series.getBoxOffice(), "Series should have box office data");
        assertFalse(series.getAwards().isEmpty(), "Series should have awards");

        // Test that the series can be used with MovieDetailsDialog
        assertTrue(series.getBoxOffice().contains("$13 million per episode"), "Box office should contain expected value");
        assertTrue(series.getAwards().stream().anyMatch(award -> award.contains("Emmy Award")), 
                  "Should contain Emmy Award");
    }

    @Test
    void testContentWithoutAwardsOrBoxOffice() {
        // Test that content without awards/box office data doesn't break the dialog
        Movie movie = dataManager.findMovieByTitle("Back to the Future");
        assertNotNull(movie, "Movie should be found");

        // This movie might not have awards/box office data in our test file
        // The dialog should handle this gracefully
        assertNotNull(movie.getAwards(), "Awards list should not be null (even if empty)");
        // Box office might be null, which is fine
    }
}

