package com.papel.imdb_clone.service;

import com.papel.imdb_clone.data.DataManager;
import com.papel.imdb_clone.repository.ForumRepository;
import com.papel.imdb_clone.repository.impl.DataManagerForumRepository;
import com.papel.imdb_clone.util.AsyncExecutor;
import com.papel.imdb_clone.util.SettingsManager;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.util.concurrent.ConcurrentHashMap;
import java.util.concurrent.ConcurrentMap;

/**
 * Service Locator pattern implementation for managing application services.
 * Provides centralized access to services and ensures singleton behavior.
 */
public class ServiceLocator {
    private static final Logger logger = LoggerFactory.getLogger(ServiceLocator.class);
    private static ServiceLocator instance;
    private final ConcurrentMap<Class<?>, Object> services = new ConcurrentHashMap<>();

    private ServiceLocator() {
        initializeServices();
    }

    public static synchronized ServiceLocator getInstance() {
        if (instance == null) {
            instance = new ServiceLocator();
        }
        return instance;
    }

    private void initializeServices() {
        logger.info("Initializing application services...");

        // Initialize core services
        registerService(DataManager.class, new DataManager());
        registerService(AuthenticationService.class, AuthenticationService.getInstance());
        registerService(EncryptionService.class, EncryptionService.getInstance());
        registerService(SettingsManager.class, SettingsManager.getInstance());

        // Initialize dependent services
        DataManager dataManager = getService(DataManager.class);
        registerService(WatchlistService.class, new WatchlistService(dataManager));

        // Forum services (repository + service)
        ForumRepository forumRepo = new DataManagerForumRepository(dataManager);
        registerService(ForumRepository.class, forumRepo);
        registerService(ForumService.class, new ForumService(forumRepo));

        logger.info("Service initialization complete");
    }

    /**
     * Register a service instance
     */
    public <T> void registerService(Class<T> serviceClass, T serviceInstance) {
        if (serviceClass == null || serviceInstance == null) {
            throw new IllegalArgumentException("Service class and instance cannot be null");
        }
        services.put(serviceClass, serviceInstance);
        logger.debug("Registered service: {}", serviceClass.getSimpleName());
    }

    /**
     * Get a service instance
     */
    @SuppressWarnings("unchecked")
    public <T> T getService(Class<T> serviceClass) {
        if (serviceClass == null) {
            throw new IllegalArgumentException("Service class cannot be null");
        }

        T service = (T) services.get(serviceClass);
        if (service == null) {
            throw new IllegalStateException("Service not found: " + serviceClass.getSimpleName());
        }
        return service;
    }

    /**
     * Check if a service is registered
     */
    public boolean hasService(Class<?> serviceClass) {
        return services.containsKey(serviceClass);
    }

    /**
     * Shutdown all services gracefully
     */
    public void shutdown() {
        logger.info("Shutting down services...");

        // Shutdown services in reverse order of dependency
        try {
            // Stop background executors first to prevent new tasks from scheduling
            try {
                AsyncExecutor.getInstance().shutdown();
                logger.debug("AsyncExecutor shut down");
            } catch (Exception e) {
                logger.warn("Failed to shutdown AsyncExecutor", e);
            }

            if (hasService(DataManager.class)) {
                // DataManager might need cleanup
                logger.debug("Shutting down DataManager");
            }
        } catch (Exception e) {
            logger.error("Error shutting down services", e);
        }

        services.clear();
        logger.info("Service shutdown complete");
    }
}

