+        lock.readLock().lock();
+        try {
+            return movies.stream()
+                    .filter(movie -> movie.getImdbRating() >= minRating && 
+                            movie.getImdbRating() <= maxRating)
+                    .collect(Collectors.toList());
+        } finally {
+            lock.readLock().unlock();
+        }
+    }
+    
+    @Override
+    public List<Movie> searchByCriteria(SearchCriteria criteria) {
+        if (criteria == null) return findAll();
+        
+        lock.readLock().lock();
+        try {
+            return movies.stream()
+                    .filter(movie -> matchesCriteria(movie, criteria))
+                    .collect(Collectors.toList());
+        } finally {
+            lock.readLock().unlock();
+        }
+    }
+    
+    private boolean matchesCriteria(Movie movie, SearchCriteria criteria) {
+        // Title filter
+        if (criteria.getTitle() != null && !criteria.getTitle().trim().isEmpty()) {
+            if (!movie.getTitle().toLowerCase().contains(criteria.getTitle().toLowerCase())) {
+                return false;
+            }
+        }
+        
+        // Actor filter
+        if (criteria.getActorName() != null && !criteria.getActorName().trim().isEmpty()) {
+            boolean hasActor = movie.getActors().stream()
+                    .anyMatch(actor -> actor.getFullName().toLowerCase()
+                            .contains(criteria.getActorName().toLowerCase()));
+            if (!hasActor) {
+                return false;
+            }
+        }
+        
+        // Director filter
+        if (criteria.getDirectorName() != null && !criteria.getDirectorName().trim().isEmpty()) {
+            if (movie.getDirector() == null || 
+                !movie.getDirector().toLowerCase().contains(criteria.getDirectorName().toLowerCase())) {
+                return false;
+            }
+        }
+        
+        // Year filter
+        int movieYear = movie.getYear().getYear() + 1900;
+        if (criteria.getMinYear() != null && movieYear < criteria.getMinYear()) {
+            return false;
+        }
+        if (criteria.getMaxYear() != null && movieYear > criteria.getMaxYear()) {
+            return false;
+        }
+        
+        // Duration filter
+        if (criteria.getMinDuration() != null && movie.getDuration() < criteria.getMinDuration()) {
+            return false;
+        }
+        if (criteria.getMaxDuration() != null && movie.getDuration() > criteria.getMaxDuration()) {
+            return false;
+        }
+        
+        // IMDB rating filter
+        Double minImdb = criteria.getMinImdbRating();
+        Double maxImdb = criteria.getMaxImdbRating();
+        double imdb = movie.getImdbRating();
+        if ((minImdb != null && imdb < minImdb) || (maxImdb != null && imdb > maxImdb)) {
+            return false;
+        }
+        
+        // User rating filter
+        Double minUser = criteria.getMinUserRating();
+        Double maxUser = criteria.getMaxUserRating();
+        double avgUserRating = movie.calculateAverageUserRating();
+        if ((minUser != null && avgUserRating < minUser) || (maxUser != null && avgUserRating > maxUser)) {
+            return false;
+        }
+        
+        // Genre filter
+        if (criteria.getGenres() != null && !criteria.getGenres().isEmpty()) {
+            boolean hasMatchingGenre = criteria.getGenres().stream()
+                    .anyMatch(genre -> movie.getGenres().contains(genre));
