+        lock.readLock().lock();
+        try {
+            return movies.stream()
+                    .sorted(Comparator.comparingDouble(Movie::getImdbRating).reversed())
+                    .limit(limit)
+                    .collect(Collectors.toList());
+        } finally {
+            lock.readLock().unlock();
+        }
+    }
+    
+    @Override
+    public List<Movie> findRecentlyAdded(int limit) {
+        lock.readLock().lock();
+        try {
+            return movies.stream()
+                    .sorted(Comparator.comparingInt(Movie::getId).reversed())
+                    .limit(limit)
+                    .collect(Collectors.toList());
+        } finally {
+            lock.readLock().unlock();
+        }
+    }
+    
+    /**
+     * Adds a movie directly to the repository (used by data loaders).
+     * @param movie The movie to add
+     */
+    public void addMovie(Movie movie) {
+        if (movie == null) return;
