+    /**
+     * Parses genre string into list of Genre enums.
+     */
+    private List<Genre> parseGenres(String genreStr) {
+        List<Genre> genres = new ArrayList<>();
+        if (genreStr != null && !genreStr.trim().isEmpty()) {
+            String[] genreArray = genreStr.split(",");
+            for (String g : genreArray) {
+                try {
+                    genres.add(Genre.valueOf(g.trim().toUpperCase()));
+                } catch (IllegalArgumentException e) {
+                    logger.warn("Unknown genre: {}", g.trim());
+                }
+            }
+        }
+        return genres;
+    }
+    
+    /**
+     * Parses actor string into list of Actor objects.
+     * This is a simplified implementation - in a real system, 
+     * actors would be loaded separately and referenced by ID.
+     */
+    private List<Actor> parseActors(String actorsStr) {
+        List<Actor> actors = new ArrayList<>();
+        if (actorsStr != null && !actorsStr.trim().isEmpty()) {
+            String[] actorArray = actorsStr.split(",");
+            for (String actorName : actorArray) {
+                String name = actorName.trim();
+                if (!name.isEmpty()) {
+                    // Create a simple actor with just the name
+                    // In a real system, this would reference existing actors
+                    String[] nameParts = name.split(" ", 2);
+                    String firstName = nameParts[0];
+                    String lastName = nameParts.length > 1 ? nameParts[1] : "";
+                    
+                    Actor actor = new Actor(firstName, lastName, LocalDate.now(), 'U', "Unknown");
+                    actors.add(actor);
+                }
+            }
+        }
+        return actors;
+    }
