+package com.papel.imdb_clone.service;
+
+import com.papel.imdb_clone.model.Content;
+import com.papel.imdb_clone.model.Movie;
+import com.papel.imdb_clone.model.Series;
+import com.papel.imdb_clone.util.LRUCache;
+import org.slf4j.Logger;
+import org.slf4j.LoggerFactory;
+
+import java.util.List;
+
+/**
+ * Service for caching search results to improve performance.
+ * Uses LRU (Least Recently Used) cache implementation.
+ */
+public class SearchCacheService {
+    private static final Logger logger = LoggerFactory.getLogger(SearchCacheService.class);
+    
+    private static final int DEFAULT_CACHE_SIZE = 100;
+    
+    private final LRUCache<String, List<Movie>> movieCache;
+    private final LRUCache<String, List<Series>> seriesCache;
+    private final LRUCache<String, List<Content>> contentCache;
+    
+    public SearchCacheService() {
+        this(DEFAULT_CACHE_SIZE);
+    }
+    
+    public SearchCacheService(int cacheSize) {
+        this.movieCache = new LRUCache<>(cacheSize);
+        this.seriesCache = new LRUCache<>(cacheSize);
+        this.contentCache = new LRUCache<>(cacheSize);
+        
+        logger.info("Initialized SearchCacheService with cache size: {}", cacheSize);
+    }
+    
+    /**
+     * Gets cached movie search results.
+     * @param key The cache key
+     * @return List of movies if cached, null otherwise
+     */
+    public List<Movie> getMovieResults(String key) {
+        List<Movie> results = movieCache.get(key);
+        if (results != null) {
+            logger.debug("Cache hit for movie search key: {}", key);
+        }
+        return results;
+    }
+    
+    /**
+     * Caches movie search results.
+     * @param key The cache key
+     * @param results The search results to cache
+     */
+    public void putMovieResults(String key, List<Movie> results) {
+        if (key != null && results != null) {
+            movieCache.put(key, results);
+            logger.debug("Cached movie search results for key: {} (size: {})", key, results.size());
+        }
+    }
+    
+    /**
+     * Gets cached series search results.
+     * @param key The cache key
+     * @return List of series if cached, null otherwise
+     */
+    public List<Series> getSeriesResults(String key) {
+        List<Series> results = seriesCache.get(key);
+        if (results != null) {
+            logger.debug("Cache hit for series search key: {}", key);
+        }
+        return results;
+    }
+    
+    /**
+     * Caches series search results.
+     * @param key The cache key
+     * @param results The search results to cache
+     */
+    public void putSeriesResults(String key, List<Series> results) {
+        if (key != null && results != null) {
+            seriesCache.put(key, results);
+            logger.debug("Cached series search results for key: {} (size: {})", key, results.size());
+        }
+    }
+    
+    /**
+     * Gets cached content search results.
+     * @param key The cache key
+     * @return List of content if cached, null otherwise
+     */
+    public List<Content> getContentResults(String key) {
+        List<Content> results = contentCache.get(key);
+        if (results != null) {
+            logger.debug("Cache hit for content search key: {}", key);
+        }
+        return results;
+    }
+    
+    /**
+     * Caches content search results.
+     * @param key The cache key
+     * @param results The search results to cache
+     */
+    public void putContentResults(String key, List<Content> results) {
+        if (key != null && results != null) {
+            contentCache.put(key, results);
+            logger.debug("Cached content search results for key: {} (size: {})", key, results.size());
+        }
+    }
+    
+    /**
+     * Clears the movie search cache.
+     */
+    public void clearMovieCache() {
+        movieCache.clear();
+        logger.debug("Cleared movie search cache");
+    }
+    
+    /**
+     * Clears the series search cache.
+     */
+    public void clearSeriesCache() {
+        seriesCache.clear();
+        logger.debug("Cleared series search cache");
+    }
+    
+    /**
+     * Clears the content search cache.
+     */
+    public void clearContentCache() {
+        contentCache.clear();
+        logger.debug("Cleared content search cache");
+    }
+    
+    /**
+     * Clears all search caches.
+     */
+    public void clearAllCaches() {
+        clearMovieCache();
+        clearSeriesCache();
+        clearContentCache();
+        logger.info("Cleared all search caches");
+    }
+    
+    /**
+     * Gets cache statistics for monitoring.
+     * @return String containing cache statistics
+     */
+    public String getCacheStats() {
+        return String.format("Cache Stats - Movies: %d, Series: %d, Content: %d",
+                movieCache.size(), seriesCache.size(), contentCache.size());
+    }
+    
+    /**
+     * Removes a specific key from all caches.
+     * @param key The key to remove
+     */
+    public void evict(String key) {
+        if (key != null) {
+            movieCache.remove(key);
+            seriesCache.remove(key);
+            contentCache.remove(key);
+            logger.debug("Evicted key from all caches: {}", key);
+        }
+    }
+}

