+import org.slf4j.Logger;
+import org.slf4j.LoggerFactory;
+
+import java.util.List;
+import java.util.Optional;
+import java.util.concurrent.CopyOnWriteArrayList;
+import java.util.concurrent.atomic.AtomicInteger;
+import java.util.concurrent.locks.ReentrantReadWriteLock;
+import java.util.stream.Collectors;
+
+/**
+ * Service class for Actor-related business logic.
+ * Provides high-level operations for actor management.
+ */
+public class ActorService {
+    private static final Logger logger = LoggerFactory.getLogger(ActorService.class);
+    
+    private final List<Actor> actors = new CopyOnWriteArrayList<>();
+    private final AtomicInteger nextId = new AtomicInteger(1);
+    private final ReentrantReadWriteLock lock = new ReentrantReadWriteLock();
+    
+    /**
+     * Finds an actor by their ID.
+     * @param id The actor ID
+     * @return Optional containing the actor if found
+     */
+    public Optional<Actor> findById(int id) {
+        lock.readLock().lock();
+        try {
+            return actors.stream()
+                    .filter(actor -> actor.getId() == id)
+                    .findFirst();
+        } finally {
+            lock.readLock().unlock();
+        }
+    }
+    
+    /**
+     * Finds actors by name (partial match).
+     * @param name The name to search for
+     * @return List of actors matching the name
+     */
+    public List<Actor> findByName(String name) {
+        if (name == null || name.trim().isEmpty()) {
+            return List.of();
+        }
+        
+        lock.readLock().lock();
+        try {
+            String searchName = name.toLowerCase().trim();
+            return actors.stream()
+                    .filter(actor -> actor.getFullName().toLowerCase().contains(searchName))
+                    .collect(Collectors.toList());
+        } finally {
+            lock.readLock().unlock();
+        }
+    }
+    
+    /**
+     * Finds actors by ethnicity.
+     * @param ethnicity The ethnicity to search for
+     * @return List of actors with the specified ethnicity
+     */
+    public List<Actor> findByEthnicity(Ethnicity ethnicity) {
+        if (ethnicity == null) {
+            return List.of();
+        }
+        
+        lock.readLock().lock();
+        try {
+            return actors.stream()
+                    .filter(actor -> ethnicity.equals(actor.getEthnicity()))
+                    .collect(Collectors.toList());
+        } finally {
+            lock.readLock().unlock();
+        }
+    }
+    
+    /**
+     * Finds actors by nationality.
+     * @param nationality The nationality to search for
+     * @return List of actors with the specified nationality
+     */
+    public List<Actor> findByNationality(String nationality) {
+        if (nationality == null || nationality.trim().isEmpty()) {
+            return List.of();
+        }
+        
+        lock.readLock().lock();
+        try {
+            return actors.stream()
+                    .filter(actor -> nationality.equalsIgnoreCase(actor.getNationality()))
+                    .collect(Collectors.toList());
+        } finally {
+            lock.readLock().unlock();
+        }
+    }
+    
+    /**
+     * Retrieves all actors.
+     * @return List of all actors
+     */
+    public List<Actor> findAll() {
+        lock.readLock().lock();
+        try {
+            return List.copyOf(actors);
+        } finally {
+            lock.readLock().unlock();
+        }
+    }
+    
+    /**
+     * Saves an actor (create or update).
+     * @param actor The actor to save
+     * @return The saved actor
+     */
+    public Actor save(Actor actor) {
+        if (actor == null) {
+            throw new IllegalArgumentException("Actor cannot be null");
+        }
+        
+        lock.writeLock().lock();
+        try {
+            if (actor.getId() == 0) {
+                // New actor
+                actor.setId(nextId.getAndIncrement());
+                actors.add(actor);
+                logger.debug("Created new actor: {} (ID: {})", actor.getFullName(), actor.getId());
+            } else {
+                // Update existing actor
+                Optional<Actor> existing = findById(actor.getId());
+                if (existing.isPresent()) {
+                    actors.remove(existing.get());
+                    actors.add(actor);
+                    logger.debug("Updated actor: {} (ID: {})", actor.getFullName(), actor.getId());
+                } else {
+                    throw new IllegalArgumentException("Actor with ID " + actor.getId() + " not found");
+                }
+            }
+            return actor;
+        } finally {
+            lock.writeLock().unlock();
+        }
+    }
+    
+    /**
+     * Deletes an actor by ID.
+     * @param id The actor ID to delete
+     * @return true if deleted, false if not found
+     */
+    public boolean deleteById(int id) {
+        lock.writeLock().lock();
+        try {
+            Optional<Actor> actor = findById(id);
+            if (actor.isPresent()) {
+                actors.remove(actor.get());
+                logger.debug("Deleted actor with ID: {}", id);
+                return true;
+            }
+            return false;
+        } finally {
+            lock.writeLock().unlock();
+        }
+    }
+    
+    /**
+     * Gets the total count of actors.
+     * @return The number of actors
+     */
+    public long getCount() {
+        return actors.size();
+    }
+    
+    /**
+     * Adds an actor directly (used by data loaders).
+     * @param actor The actor to add
+     */
+    public void addActor(Actor actor) {
+        if (actor == null) return;
+        
+        lock.writeLock().lock();
+        try {
+            if (actor.getId() > 0) {
+                nextId.getAndUpdate(current -> Math.max(current, actor.getId() + 1));
+            } else {
+                actor.setId(nextId.getAndIncrement());
+            }
+            actors.add(actor);
+        } finally {
+            lock.writeLock().unlock();
+        }
+    }
+    
+    /**
+     * Clears all actors (used for testing or data reloading).
+     */
+    public void clear() {
+        lock.writeLock().lock();
+        try {
+            actors.clear();
+            nextId.set(1);
+            logger.debug("Cleared all actors");
+        } finally {
+            lock.writeLock().unlock();
+        }
+    }
+}

