+package com.papel.imdb_clone.service;
+
+import com.papel.imdb_clone.data.SearchCriteria;
+import com.papel.imdb_clone.enums.Genre;
+import com.papel.imdb_clone.model.Series;
+import org.slf4j.Logger;
+import org.slf4j.LoggerFactory;
+
+import java.util.List;
+import java.util.Optional;
+import java.util.concurrent.CopyOnWriteArrayList;
+import java.util.concurrent.atomic.AtomicInteger;
+import java.util.concurrent.locks.ReentrantReadWriteLock;
+import java.util.stream.Collectors;
+
+/**
+ * Service class for Series-related business logic.
+ * Provides high-level operations for series management.
+ */
+public class SeriesService {
+    private static final Logger logger = LoggerFactory.getLogger(SeriesService.class);
+    
+    private final List<Series> series = new CopyOnWriteArrayList<>();
+    private final AtomicInteger nextId = new AtomicInteger(1);
+    private final ReentrantReadWriteLock lock = new ReentrantReadWriteLock();
+    private final SearchCacheService searchCacheService;
+    
+    public SeriesService(SearchCacheService searchCacheService) {
+        this.searchCacheService = searchCacheService;
+    }
+    
+    /**
+     * Finds a series by its ID.
+     * @param id The series ID
+     * @return Optional containing the series if found
+     */
+    public Optional<Series> findById(int id) {
+        lock.readLock().lock();
+        try {
+            return series.stream()
+                    .filter(s -> s.getId() == id)
+                    .findFirst();
+        } finally {
+            lock.readLock().unlock();
+        }
+    }
+    
+    /**
+     * Finds a series by its title.
+     * @param title The series title
+     * @return Optional containing the series if found
+     */
+    public Optional<Series> findByTitle(String title) {
+        if (title == null) return Optional.empty();
+        
+        lock.readLock().lock();
+        try {
+            return series.stream()
+                    .filter(s -> title.equals(s.getTitle()))
+                    .findFirst();
+        } finally {
+            lock.readLock().unlock();
+        }
+    }
+    
+    /**
+     * Retrieves all series.
+     * @return List of all series
+     */
+    public List<Series> findAll() {
+        lock.readLock().lock();
+        try {
+            return List.copyOf(series);
+        } finally {
+            lock.readLock().unlock();
+        }
+    }
+    
+    /**
+     * Finds series by genre.
+     * @param genre The genre to search for
+     * @return List of series in the specified genre
+     */
+    public List<Series> findByGenre(Genre genre) {
+        if (genre == null) return List.of();
+        
+        lock.readLock().lock();
+        try {
+            return series.stream()
+                    .filter(s -> s.getGenres().contains(genre))
+                    .collect(Collectors.toList());
+        } finally {
+            lock.readLock().unlock();
+        }
+    }
+    
+    /**
+     * Finds series by actor name.
+     * @param actorName The actor name to search for
+     * @return List of series featuring the actor
+     */
+    public List<Series> findByActorName(String actorName) {
+        if (actorName == null || actorName.trim().isEmpty()) return List.of();
+        
+        lock.readLock().lock();
+        try {
+            String searchName = actorName.toLowerCase().trim();
+            return series.stream()
+                    .filter(s -> s.getActors().stream()
+                            .anyMatch(actor -> actor.getFullName().toLowerCase().contains(searchName)))
+                    .collect(Collectors.toList());
+        } finally {
+            lock.readLock().unlock();
+        }
+    }
+    
+    /**
+     * Finds series by year range.
+     * @param startYear The start year (inclusive)
+     * @param endYear The end year (inclusive)
+     * @return List of series within the year range
+     */
+    public List<Series> findByYearRange(int startYear, int endYear) {
+        lock.readLock().lock();
+        try {
+            return series.stream()
+                    .filter(s -> {
+                        int seriesYear = s.getYear().getYear() + 1900;
+                        return seriesYear >= startYear && seriesYear <= endYear;
+                    })
+                    .collect(Collectors.toList());
+        } finally {
+            lock.readLock().unlock();
+        }
+    }
+    
+    /**
+     * Searches series based on complex criteria with caching.
+     * @param criteria The search criteria
+     * @return List of series matching the criteria
+     */
+    public List<Series> searchByCriteria(SearchCriteria criteria) {
+        if (criteria == null) {
+            return findAll();
+        }
+        
+        // Try to get from cache first
+        String cacheKey = generateCacheKey(criteria);
+        List<Series> cachedResults = searchCacheService.getSeriesResults(cacheKey);
+        if (cachedResults != null) {
+            logger.debug("Returning cached search results for key: {}", cacheKey);
+            return cachedResults;
+        }
+        
+        // Perform search
+        lock.readLock().lock();
+        List<Series> results;
+        try {
+            results = series.stream()
+                    .filter(s -> matchesCriteria(s, criteria))
+                    .collect(Collectors.toList());
+        } finally {
+            lock.readLock().unlock();
+        }
+        
+        // Cache the results
+        searchCacheService.putSeriesResults(cacheKey, results);
+        
+        logger.debug("Search completed, found {} series", results.size());
+        return results;
+    }
+    
+    private boolean matchesCriteria(Series series, SearchCriteria criteria) {
+        // Title filter
+        if (criteria.getTitle() != null && !criteria.getTitle().trim().isEmpty()) {
+            if (!series.getTitle().toLowerCase().contains(criteria.getTitle().toLowerCase())) {
+                return false;
+            }
+        }
+        
+        // Actor filter
+        if (criteria.getActorName() != null && !criteria.getActorName().trim().isEmpty()) {
+            boolean hasActor = series.getActors().stream()
+                    .anyMatch(actor -> actor.getFullName().toLowerCase()
+                            .contains(criteria.getActorName().toLowerCase()));
+            if (!hasActor) {
+                return false;
+            }
+        }
+        
+        // Year filter
+        int seriesYear = series.getYear().getYear() + 1900;
+        if (criteria.getMinYear() != null && seriesYear < criteria.getMinYear()) {
+            return false;
+        }
+        if (criteria.getMaxYear() != null && seriesYear > criteria.getMaxYear()) {
+            return false;
+        }
+        
+        // IMDB rating filter
+        Double minImdb = criteria.getMinImdbRating();
+        Double maxImdb = criteria.getMaxImdbRating();
+        double imdb = series.getImdbRating();
+        if ((minImdb != null && imdb < minImdb) || (maxImdb != null && imdb > maxImdb)) {
+            return false;
+        }
+        
+        // User rating filter
+        Double minUser = criteria.getMinUserRating();
+        Double maxUser = criteria.getMaxUserRating();
+        double avgUserRating = series.calculateAverageUserRating();
+        if ((minUser != null && avgUserRating < minUser) || (maxUser != null && avgUserRating > maxUser)) {
+            return false;
+        }
+        
+        // Genre filter
+        if (criteria.getGenres() != null && !criteria.getGenres().isEmpty()) {
+            boolean hasMatchingGenre = criteria.getGenres().stream()
+                    .anyMatch(genre -> series.getGenres().contains(genre));
+            if (!hasMatchingGenre) {
+                return false;
+            }
+        }
+        
+        // Description filter
+        if (criteria.getDescription() != null && !criteria.getDescription().trim().isEmpty()) {
+            String description = series.getDescription();
+            if (description == null || 
+                !description.toLowerCase().contains(criteria.getDescription().toLowerCase())) {
+                return false;
+            }
+        }
+        
+        return true;
+    }
+    
+    /**
+     * Saves a series (create or update).
+     * @param series The series to save
+     * @return The saved series
+     */
+    public Series save(Series series) {
+        if (series == null) {
+            throw new IllegalArgumentException("Series cannot be null");
+        }
+        
+        lock.writeLock().lock();
+        try {
+            if (series.getId() == 0) {
+                // New series
+                series.setId(nextId.getAndIncrement());
+                this.series.add(series);
+                logger.debug("Created new series: {} (ID: {})", series.getTitle(), series.getId());
+            } else {
+                // Update existing series
+                Optional<Series> existing = findById(series.getId());
+                if (existing.isPresent()) {
+                    this.series.remove(existing.get());
+                    this.series.add(series);
+                    logger.debug("Updated series: {} (ID: {})", series.getTitle(), series.getId());
+                } else {
+                    throw new IllegalArgumentException("Series with ID " + series.getId() + " not found");
+                }
+            }
+            
+            // Clear search cache when data is modified
+            searchCacheService.clearSeriesCache();
+            
+            return series;
+        } finally {
+            lock.writeLock().unlock();
+        }
+    }
+    
+    /**
+     * Deletes a series by ID.
+     * @param id The series ID to delete
+     * @return true if deleted, false if not found
+     */
+    public boolean deleteById(int id) {
+        lock.writeLock().lock();
+        try {
+            Optional<Series> series = findById(id);
+            if (series.isPresent()) {
+                this.series.remove(series.get());
+                
+                // Clear search cache when data is modified
+                searchCacheService.clearSeriesCache();
+                
+                logger.debug("Deleted series with ID: {}", id);
+                return true;
+            }
+            return false;
+        } finally {
+            lock.writeLock().unlock();
+        }
+    }
+    
+    /**
+     * Gets the total count of series.
+     * @return The number of series
+     */
+    public long getCount() {
+        return series.size();
+    }
+    
+    /**
+     * Checks if a series with the given title exists.
+     * @param title The series title to check
+     * @return true if exists, false otherwise
+     */
+    public boolean existsByTitle(String title) {
+        if (title == null) return false;
+        
+        lock.readLock().lock();
+        try {
+            return series.stream()
+                    .anyMatch(s -> title.equals(s.getTitle()));
+        } finally {
+            lock.readLock().unlock();
+        }
+    }
+    
+    /**
+     * Adds a series directly (used by data loaders).
+     * @param series The series to add
+     */
+    public void addSeries(Series series) {
+        if (series == null) return;
+        
+        lock.writeLock().lock();
+        try {
+            if (series.getId() > 0) {
+                nextId.getAndUpdate(current -> Math.max(current, series.getId() + 1));
+            } else {
+                series.setId(nextId.getAndIncrement());
+            }
+            this.series.add(series);
+        } finally {
+            lock.writeLock().unlock();
+        }
+    }
+    
+    /**
+     * Clears all series (used for testing or data reloading).
+     */
+    public void clear() {
+        lock.writeLock().lock();
+        try {
+            series.clear();
+            nextId.set(1);
+            searchCacheService.clearSeriesCache();
+            logger.debug("Cleared all series");
+        } finally {
+            lock.writeLock().unlock();
+        }
+    }
+    
+    /**
+     * Generates a cache key for search criteria.
+     */
+    private String generateCacheKey(SearchCriteria criteria) {
+        StringBuilder key = new StringBuilder("series_search:");
+        
+        if (criteria.getTitle() != null) key.append("title=").append(criteria.getTitle()).append(";");
+        if (criteria.getActorName() != null) key.append("actor=").append(criteria.getActorName()).append(";");
+        if (criteria.getMinYear() != null) key.append("minYear=").append(criteria.getMinYear()).append(";");
+        if (criteria.getMaxYear() != null) key.append("maxYear=").append(criteria.getMaxYear()).append(";");
+        if (criteria.getMinImdbRating() != null) key.append("minImdb=").append(criteria.getMinImdbRating()).append(";");
+        if (criteria.getMaxImdbRating() != null) key.append("maxImdb=").append(criteria.getMaxImdbRating()).append(";");
+        if (criteria.getMinUserRating() != null) key.append("minUser=").append(criteria.getMinUserRating()).append(";");
+        if (criteria.getMaxUserRating() != null) key.append("maxUser=").append(criteria.getMaxUserRating()).append(";");
+        if (criteria.getGenres() != null && !criteria.getGenres().isEmpty()) {
+            key.append("genres=").append(criteria.getGenres().toString()).append(";");
+        }
+        if (criteria.getDescription() != null) key.append("desc=").append(criteria.getDescription()).append(";");
