+package com.papel.imdb_clone.controllers.coordinator;
+
+import com.papel.imdb_clone.data.RefactoredDataManager;
+import com.papel.imdb_clone.data.SearchCriteria;
+import com.papel.imdb_clone.enums.Genre;
+import com.papel.imdb_clone.model.Content;
+import com.papel.imdb_clone.model.Movie;
+import com.papel.imdb_clone.model.Series;
+import javafx.collections.FXCollections;
+import javafx.collections.ObservableList;
+import javafx.concurrent.Task;
+import org.slf4j.Logger;
+import org.slf4j.LoggerFactory;
+
+import java.util.ArrayList;
+import java.util.List;
+import java.util.concurrent.CompletableFuture;
+import java.util.concurrent.ExecutorService;
+import java.util.concurrent.Executors;
+
+/**
+ * Coordinates search operations across different content types.
+ * Provides unified search functionality and manages search execution.
+ */
+public class SearchCoordinator {
+    private static final Logger logger = LoggerFactory.getLogger(SearchCoordinator.class);
+    
+    private final RefactoredDataManager dataManager;
+    private final ExecutorService searchExecutor;
+    
+    public SearchCoordinator(RefactoredDataManager dataManager) {
+        this.dataManager = dataManager;
+        this.searchExecutor = Executors.newFixedThreadPool(3); // For parallel searches
+        logger.info("SearchCoordinator initialized");
+    }
+    
+    /**
+     * Performs a unified search across movies and series.
+     * @param criteria The search criteria
+     * @return CompletableFuture containing the search results
+     */
+    public CompletableFuture<List<Content>> searchAllContent(SearchCriteria criteria) {
+        logger.debug("Starting unified content search");
+        
+        return CompletableFuture.supplyAsync(() -> {
+            List<Content> allResults = new ArrayList<>();
+            
+            try {
+                // Search movies and series in parallel
+                CompletableFuture<List<Movie>> movieSearch = searchMoviesAsync(criteria);
+                CompletableFuture<List<Series>> seriesSearch = searchSeriesAsync(criteria);
+                
+                // Wait for both searches to complete
+                CompletableFuture.allOf(movieSearch, seriesSearch).join();
+                
+                // Combine results
+                allResults.addAll(movieSearch.get());
+                allResults.addAll(seriesSearch.get());
+                
+                logger.debug("Unified search completed. Found {} total results", allResults.size());
+                
+            } catch (Exception e) {
+                logger.error("Error during unified search", e);
+            }
+            
+            return allResults;
+        }, searchExecutor);
+    }
+    
+    /**
+     * Searches movies asynchronously.
+     * @param criteria The search criteria
+     * @return CompletableFuture containing movie results
+     */
+    public CompletableFuture<List<Movie>> searchMoviesAsync(SearchCriteria criteria) {
+        return CompletableFuture.supplyAsync(() -> {
+            logger.debug("Searching movies with criteria");
+            List<Movie> results = dataManager.searchMovies(criteria);
+            logger.debug("Movie search completed. Found {} results", results.size());
+            return results;
+        }, searchExecutor);
+    }
+    
+    /**
+     * Searches series asynchronously.
+     * @param criteria The search criteria
+     * @return CompletableFuture containing series results
+     */
+    public CompletableFuture<List<Series>> searchSeriesAsync(SearchCriteria criteria) {
+        return CompletableFuture.supplyAsync(() -> {
+            logger.debug("Searching series with criteria");
+            List<Series> results = dataManager.searchSeries(criteria);
+            logger.debug("Series search completed. Found {} results", results.size());
+            return results;
+        }, searchExecutor);
+    }
+    
+    /**
+     * Creates a JavaFX Task for searching all content.
+     * This is useful for UI integration with progress indicators.
+     * @param criteria The search criteria
+     * @return Task that can be run on a background thread
+     */
+    public Task<ObservableList<Content>> createSearchTask(SearchCriteria criteria) {
+        return new Task<ObservableList<Content>>() {
+            @Override
+            protected ObservableList<Content> call() throws Exception {
+                updateMessage("Searching content...");
+                updateProgress(0, 100);
+                
+                List<Content> results = new ArrayList<>();
+                
+                // Search movies
+                updateMessage("Searching movies...");
+                updateProgress(25, 100);
+                List<Movie> movies = dataManager.searchMovies(criteria);
+                results.addAll(movies);
+                
+                // Search series
+                updateMessage("Searching series...");
+                updateProgress(75, 100);
+                List<Series> series = dataManager.searchSeries(criteria);
+                results.addAll(series);
+                
+                updateMessage("Search completed");
+                updateProgress(100, 100);
+                
+                return FXCollections.observableArrayList(results);
+            }
+        };
+    }
+    
+    /**
+     * Builds search criteria from UI components.
+     * @param title Title filter
+     * @param selectedGenres Selected genres
+     * @param actorName Actor name filter
+     * @param directorName Director name filter
+     * @param minImdbRating Minimum IMDB rating
+     * @param maxImdbRating Maximum IMDB rating
+     * @param minUserRating Minimum user rating
+     * @param maxUserRating Maximum user rating
+     * @param minDuration Minimum duration
+     * @param maxDuration Maximum duration
+     * @param yearFrom Start year
+     * @param yearTo End year
+     * @param description Description filter
+     * @return SearchCriteria object
+     */
+    public SearchCriteria buildSearchCriteria(String title, List<Genre> selectedGenres, 
+                                            String actorName, String directorName,
+                                            Double minImdbRating, Double maxImdbRating,
+                                            Double minUserRating, Double maxUserRating,
+                                            Integer minDuration, Integer maxDuration,
+                                            Integer yearFrom, Integer yearTo,
+                                            String description) {
+        
