+    private void applyThemeToCurrentScene() {
+        if (primaryStage == null) return;
+        
+        var scene = primaryStage.getScene();
+        if (scene == null) return;
+        
+        // Remove existing stylesheets
+        scene.getStylesheets().removeIf(s -> s.contains("/css/theme-") || s.contains("/css/modern-styles.css"));
+        
+        // Apply base stylesheet
+        try {
+            scene.getStylesheets().add(getClass().getResource("/css/modern-styles.css").toExternalForm());
+        } catch (Exception e) {
+            logger.warn("Could not load base stylesheet: {}", e.getMessage());
+        }
+        
+        // Apply theme stylesheet
+        SettingsManager settings = SettingsManager.getInstance();
+        String themeKey = settings.getTheme();
+        String themePath = "/css/theme-neon-light.css"; // default
+        if ("neon".equals(themeKey)) themePath = "/css/theme-cyberpunk.css";
+        else if ("high-contrast".equals(themeKey)) themePath = "/css/theme-neon-dark.css";
+        
+        try {
+            scene.getStylesheets().add(getClass().getResource(themePath).toExternalForm());
+        } catch (Exception e) {
+            logger.warn("Could not load theme stylesheet {}: {}", themePath, e.getMessage());
+        }
+        
+        // Apply font size
+        if (scene.getRoot() != null) {
+            scene.getRoot().setStyle("-fx-font-size: " + settings.getFontSize() + "px;");
+        }
+    }
