package com.papel.imdb_clone.model;

import com.papel.imdb_clone.enums.Ethnicity;

import java.time.LocalDate;
import java.util.List;
import java.util.Objects;

public class Actor extends Celebrity {
    private Ethnicity ethnicity;
    // Backward-compatible storage of the original race label used in older tests
    private String raceLabel;
    private String nationality;

    public Actor(String firstName, String lastName, LocalDate birthDate, char gender, Ethnicity ethnicity) {
        super(firstName, lastName, birthDate, gender);
        this.ethnicity = ethnicity;
        this.raceLabel = ethnicity != null ? ethnicity.getLabel() : null;
    }

    // Backward-compatible constructor accepting a race/ethnicity label
    public Actor(String firstName, String lastName, LocalDate birthDate, char gender, String raceLabel) {
        super(firstName, lastName, birthDate, gender);
        if (raceLabel != null && !raceLabel.isBlank()) {
            this.raceLabel = raceLabel;
            try {
                this.ethnicity = Ethnicity.fromLabel(raceLabel);
            } catch (IllegalArgumentException ex) {
                this.ethnicity = null; // Unknown label; keep null to remain backward-compatible
            }
        }
    }

    public Ethnicity getEthnicity() {
        return ethnicity;
    }

    public void setEthnicity(Ethnicity ethnicity) {
        this.ethnicity = ethnicity;
        this.raceLabel = ethnicity != null ? ethnicity.getLabel() : this.raceLabel;
    }

    // Backward-compatible accessors using the previous "race" naming
    public String getRace() {
        return raceLabel;
    }

    public void setRace(String raceLabel) {
        if (raceLabel == null || raceLabel.isBlank()) {
            this.raceLabel = null;
            this.ethnicity = null;
            return;
        }
        this.raceLabel = raceLabel;
        try {
            this.ethnicity = Ethnicity.fromLabel(raceLabel);
        } catch (IllegalArgumentException ex) {
            this.ethnicity = null;
        }
    }
    /**
     * Compares this {@code Actor} object with the specified object for equality.
     * Returns {@code true} if the given object is also an {@code Actor} and
     * all fields (inherited and specific to {@code Actor}) are equal.
     *
     * <p>Specifically, this method considers two {@code Actor} objects equal if:
     * <ul>
     * <li>They are the exact same object (reference equality).</li>
     * <li>The other object is not {@code null} and is an instance of the same class.</li>
     * <li>The superclass's {@code equals} method returns {@code true} (meaning
     * all inherited fields are equal).</li>
     * <li>Their {@code race} fields are equal (using {@link Objects#equals(Object, Object)}
     * for null-safe comparison).</li>
     * </ul>
     *
     * @param obj The object to be compared for equality with this {@code Actor}.
     * @return {@code true} if the specified object is equal to this {@code Actor}; {@code false} otherwise.
     */
    @Override
    public boolean equals(Object obj) {
        if (this == obj) return true;
        if (obj == null || getClass() != obj.getClass()) return false;
        if (!super.equals(obj)) return false;
        Actor actor = (Actor) obj;
        return Objects.equals(this.getRace(), actor.getRace()) && Objects.equals(getFirstName(), actor.getFirstName())
                && Objects.equals(getLastName(), actor.getLastName()) && Objects.equals(getBirthDate(), actor.getBirthDate())
                && getGender() == actor.getGender();
    }

    /**
     * Generates a hash code for this {@code Actor} object.
     * The hash code is computed based on the hash code of the superclass and the
     * value of the {@code race} field.
     * This method is consistent with {@link #equals(Object)}, meaning that for any two
     * {@code Actor} objects, {@code a} and {@code b}, if {@code a.equals(b)} is true,
     * then {@code a.hashCode()} must be the same as {@code b.hashCode()}.
     *
     * @return A hash code value for this object.
     */
    @Override
    public int hashCode() {
        return Objects.hash(super.hashCode(), getRace());
    }

    @Override
    public String toString() {
        return "Actor{" +
                "id=" + getId() +
                ", firstName='" + getFirstName() + '\'' +
                ", lastName='" + getLastName() + '\'' +
                ", birthDate=" + getBirthDate() +
                ", gender=" + getGender() +
                ", race='" + getRace() + "'" +
                '}';
    }

    public void setId(int id) {
        this.id = id;
    }
}

