package com.papel.imdb_clone.controllers;

import com.papel.imdb_clone.data.DataManager;
import com.papel.imdb_clone.model.Quiz;
import com.papel.imdb_clone.model.QuizQuestion;
import javafx.animation.FadeTransition;
import javafx.animation.KeyFrame;
import javafx.animation.ScaleTransition;
import javafx.animation.Timeline;
import javafx.fxml.FXML;
import javafx.geometry.Insets;
import javafx.geometry.Pos;
import javafx.scene.control.Button;
import javafx.scene.control.Label;
import javafx.scene.layout.GridPane;
import javafx.scene.layout.HBox;
import javafx.scene.text.Text;
import javafx.util.Duration;

import java.util.ArrayList;
import java.util.List;

public class QuizController extends BaseController {
    @FXML private HBox quizSelector;
    @FXML private Label quizTitle;
    @FXML private Label timerLabel;
    @FXML private javafx.scene.control.ProgressBar progress;
    @FXML private Text questionText;
    @FXML private GridPane optionsGrid;
    @FXML private Button prevBtn;
    @FXML private Button nextBtn;
    @FXML private Button submitBtn;
    @FXML private javafx.scene.control.TitledPane resultPane;
    @FXML private Label scoreLabel;
    @FXML private Label feedbackLabel;
    @FXML private Button playAgainBtn;

    private final List<Quiz> quizzes = new ArrayList<>();
    private Quiz current;
    private int qIndex = 0;
    private int timeLeft = 30; // seconds
    private Timeline timer;
    private DataManager dataManager;
    private com.papel.imdb_clone.model.User currentUser;
    private String sessionToken;
    private MainControllerRefactored mainController;

    @Override
    protected void initializeController() {
        // If DataManager is not yet provided, controls will be ready; data comes via setDataManager
        buildSelector();
        if (!quizzes.isEmpty()) selectQuiz(quizzes.get(0));
        else disableControls(true);
        prevBtn.setOnAction(e -> goPrev());
        nextBtn.setOnAction(e -> goNext());
        submitBtn.setOnAction(e -> submit());
        if (playAgainBtn != null) playAgainBtn.setOnAction(e -> replay());
    }

    public void setDataManager(DataManager dataManager) {
        this.dataManager = dataManager;
        reloadFromDataManager();
    }

    public void setCurrentUser(com.papel.imdb_clone.model.User user) {
        this.currentUser = user;
    }

    public void setSessionToken(String token) {
        this.sessionToken = token;
    }

    public void setMainController(MainControllerRefactored mc) {
        this.mainController = mc;
    }

    private void reloadFromDataManager() {
        quizzes.clear();
        if (dataManager != null) quizzes.addAll(dataManager.getQuizzes());
        buildSelector();
        if (!quizzes.isEmpty()) selectQuiz(quizzes.get(0));
        else {
            quizTitle.setText("No quizzes available yet");
            questionText.setText("Please check back later.");
            optionsGrid.getChildren().clear();
            progress.setProgress(0);
            if (timer != null) timer.stop();
            disableControls(true);
        }
    }

    private void buildSelector() {
        quizSelector.getChildren().clear();
        quizSelector.setAlignment(Pos.CENTER_LEFT);
        quizSelector.setSpacing(10);
        for (Quiz q : quizzes) {
            Button b = new Button(q.getTitle());
            b.getStyleClass().add("accent-button");
            b.setOnAction(e -> selectQuiz(q));
            quizSelector.getChildren().add(b);
        }
    }

    private void selectQuiz(Quiz quiz) {
        this.current = quiz;
        this.qIndex = 0;
        this.quizTitle.setText(quiz.getTitle());
        this.progress.setProgress(0);
        this.resultPane.setExpanded(false);
        timeLeft = 30;
        startTimer();
        renderQuestion();
        disableControls(false);
    }

    private void startTimer() {
        if (timer != null) timer.stop();
        timer = new Timeline(new KeyFrame(Duration.seconds(1), e -> {
            timeLeft--;
            if (timeLeft <= 0) { timer.stop(); submit(); }
            updateTimerLabel();
        }));
        timer.setCycleCount(Timeline.INDEFINITE);
        updateTimerLabel();
        timer.play();
    }

    private void updateTimerLabel() {
        int mm = timeLeft / 60;
        int ss = timeLeft % 60;
        timerLabel.setText(String.format("%02d:%02d", mm, ss));
    }

    private void renderQuestion() {
        if (current == null) { disableControls(true); return; }
        QuizQuestion q = current.getQuestions().get(qIndex);
        questionText.setText((qIndex + 1) + "/" + current.getQuestions().size() + ": " + q.getText());
        optionsGrid.getChildren().clear();
        optionsGrid.setPadding(new Insets(4));
        for (int i = 0; i < q.getOptions().length; i++) {
            final int idx = i;
            int row = idx / 2;
            int col = idx % 2;
            Button opt = new Button(q.getOptions()[idx]);
            opt.setMaxWidth(Double.MAX_VALUE);
            opt.getStyleClass().add("primary-button");
            opt.setOnAction(e -> selectAnswer(idx));
            optionsGrid.add(opt, col, row);
            FadeTransition ft = new FadeTransition(Duration.millis(250 + idx * 60L), opt);
            ft.setFromValue(0); ft.setToValue(1); ft.play();
        }
        progress.setProgress(qIndex / (double) current.getQuestions().size());
        prevBtn.setDisable(qIndex == 0);
        nextBtn.setDisable(qIndex >= current.getQuestions().size() - 1);
    }

    private void selectAnswer(int selectedIdx) {
        if (current == null) return;
        QuizQuestion q = current.getQuestions().get(qIndex);
        q.setUserAnswer(selectedIdx);
        pulse(optionsGrid);
    }

    private void goPrev() {
        if (current == null) return;
        if (qIndex > 0) { qIndex--; renderQuestion(); }
    }

    private void goNext() {
        if (current == null) return;
        if (qIndex < current.getQuestions().size() - 1) { qIndex++; renderQuestion(); }
    }

    private void submit() {
        if (current == null) return;
        int correct = 0;
        for (QuizQuestion q : current.getQuestions()) if (q.getUserAnswer() != null && q.getUserAnswer() == q.getCorrectIdx()) correct++;
        scoreLabel.setText("Score: " + correct + "/" + current.getQuestions().size());
        feedbackLabel.setText(correct == current.getQuestions().size() ?
                "Perfect! You're a movie maestro!" :
                correct >= current.getQuestions().size()/2 ? "Nice! Solid movie knowledge." : "Keep watchingpractice makes perfect.");
        resultPane.setExpanded(true);
        pulse(resultPane);
        if (timer != null) timer.stop();
        if (correct == current.getQuestions().size()) fireConfetti(resultPane);
    }

    private void replay() {
        if (current == null) return;
        // reset user answers
        for (QuizQuestion q : current.getQuestions()) {
            q.setUserAnswer(null);
        }
        qIndex = 0;
        timeLeft = 30;
        resultPane.setExpanded(false);
        startTimer();
        renderQuestion();
    }

    private void disableControls(boolean disabled) {
        if (prevBtn != null) prevBtn.setDisable(disabled);
        if (nextBtn != null) nextBtn.setDisable(disabled);
        if (submitBtn != null) submitBtn.setDisable(disabled);
    }

    private void fireConfetti(javafx.scene.Node anchor) {
        // simple confetti burst using small rectangles dropped with fade
        javafx.scene.layout.Pane layer = new javafx.scene.layout.Pane();
        layer.setMouseTransparent(true);
        // attach above anchor's parent if possible
        javafx.scene.Parent parent = anchor.getParent();
        if (parent instanceof javafx.scene.layout.Pane p) {
            p.getChildren().add(layer);
            double w = p.getWidth() > 0 ? p.getWidth() : 600;
            double h = p.getHeight() > 0 ? p.getHeight() : 300;
            int pieces = 28;
            java.util.Random rnd = new java.util.Random();
            for (int i = 0; i < pieces; i++) {
                javafx.scene.shape.Rectangle r = new javafx.scene.shape.Rectangle(6, 10);
                String[] colors = {"#ff2d95", "#00f7ff", "#ffee00", "#7b2cff", "#00ff85", "#ff8a00"};
                r.setFill(javafx.scene.paint.Color.web(colors[i % colors.length]));
                r.setArcWidth(3); r.setArcHeight(3);
                r.setOpacity(0.9);
                r.setLayoutX(w/2 + (rnd.nextDouble()-0.5)*80);
                r.setLayoutY(anchor.getLayoutY()+10);
                layer.getChildren().add(r);
                // fall and spin
                javafx.animation.RotateTransition rt = new javafx.animation.RotateTransition(Duration.millis(900 + rnd.nextInt(600)), r);
                rt.setByAngle(360 * (rnd.nextBoolean()?1:-1));
                rt.setCycleCount(1);
                javafx.animation.TranslateTransition tt = new javafx.animation.TranslateTransition(Duration.millis(900 + rnd.nextInt(600)), r);
                tt.setByY(120 + rnd.nextDouble()*120);
                tt.setByX((rnd.nextDouble()-0.5)*160);
                javafx.animation.FadeTransition ft = new javafx.animation.FadeTransition(Duration.millis(900 + rnd.nextInt(600)), r);
                ft.setFromValue(1.0); ft.setToValue(0.0);
                javafx.animation.ParallelTransition pt = new javafx.animation.ParallelTransition(r, rt, tt, ft);
                int idx = i;
                pt.setOnFinished(ev -> layer.getChildren().remove(r));
                pt.play();
            }
            // cleanup the layer after animations
            javafx.animation.PauseTransition pause = new javafx.animation.PauseTransition(Duration.seconds(2.0));
            pause.setOnFinished(ev -> p.getChildren().remove(layer));
            pause.play();
        }
    }

    private void pulse(javafx.scene.Node node) {
        ScaleTransition st = new ScaleTransition(Duration.millis(220), node);
        st.setFromX(1.0); st.setFromY(1.0);
        st.setToX(1.04); st.setToY(1.04);
        st.setAutoReverse(true); st.setCycleCount(2);
        st.play();
    }

    /**
     * Sets the primary stage for this controller.
     * @param primaryStage The primary stage
     */
    public void setPrimaryStage(javafx.stage.Stage primaryStage) {
        // Store the primary stage if needed for dialogs
    }

    /**
     * Sets the data manager for this controller.
     * @param dataManager The data manager
     */
    public void setDataManager(Object dataManager) {
        // Store the data manager if needed
    }

    /**
     * Refreshes the controller data.
     */
    public void refresh() {
        // Refresh quiz data if needed
    }
}

