package com.papel.imdb_clone.model;

import com.papel.imdb_clone.enums.Genre;

import java.util.*;

public class Movie extends Content {
    private final int duration;
    private Date year;
    private final List<Actor> actors;

    // Rich content fields
    /**
     * @deprecated Use {@link #getSummary()} and {@link #setSummary(String)} from Content class instead.
     */
    @Deprecated
    private String description;
    private String posterUrl;
    private String trailerUrl;
    private List<String> imageUrls;
    private String boxOffice;
    private List<String> awards;
    private List<String> filmingLocations;
    private List<String> productionCompanies;
    private String mpaaRating; // G, PG, PG-13, R, NC-17
    private List<Genre> genres; // Multiple genres support

    public Movie(String title, int year, Director director, String userRatings, int duration, List<Actor> actors, double imdbRating) {
        super(title, new Date(year - 1900, 0, 1), Genre.ACTION, 
              director != null ? director.getFullName() : "Unknown", new HashMap<>(), imdbRating);
        this.duration = duration;
        this.actors = new ArrayList<>(actors != null ? actors : new ArrayList<>());
        initializeRichContentFields();
    }

    public Movie(String title, int year, String genre, String director, Map<Integer, Integer> userRatings, double imdbRating) {
        super(title, new Date(year - 1900, 0, 1), Genre.valueOf(genre.toUpperCase().replace(" ", "_")), director, userRatings, imdbRating);
        this.duration = 0;
        this.actors = new ArrayList<>();
        initializeRichContentFields();
    }

    private void initializeRichContentFields() {
        this.imageUrls = new ArrayList<>();
        this.awards = new ArrayList<>();
        this.filmingLocations = new ArrayList<>();
        this.productionCompanies = new ArrayList<>();
        this.genres = new ArrayList<>();
        this.genres.add(getGenre()); // Add the primary genre from parent class
    }


